INSERT INTO `websoc_spider` VALUES ('58', '962', 'web服务器', 'Coldfusion 8.0.1 文件上传漏洞', ' 高危', '文件上传 ', 'Coldfusion 8.0~9.0.1', 'Adobe ColdFusion是一款高效的网络应用服务器开发环境。\r\nAdobe ColdFusion 9.0.1及之前版本的管理控制台中存在多个目录遍历漏洞。远程攻击者可借助向CFIDE/administrator/中的CFIDE/administrator/settings/mappings.cfm，logging/settings.cfm，datasources/index.cfm，j2eepackaging/editarchive.cfm和enter.cfm发送的locale参数读取任意文件。', 'CVE-2009-2265', 'CNNVD-200907-058', ' ', '2013-05-14');
INSERT INTO `websoc_spider` VALUES ('59', '85', 'web服务器', 'zeus 4.2 服务器管理接口XSS漏洞', ' 中危', '跨站脚本', 'Zeus Technologies Zeus Web Server 4.2 r2\r\nZeus Technologies Zeus Web Server 4.2\r\nZeus Technologies Zeus Web Server 4.1 r1 - r5\r\nZeus Technologies Zeus Web Server 4.1\r\nZeus Technologies Zeus Web Server 4.0', 'zeus服务器包含了一个基本的管理员接口，可以被XSS攻击。\r\n由于对用户的输入没有采取严格的过滤，攻击者可以构造一个恶意链接，其中包含任意HTML和脚本代码，这将在访问恶意链接的用户Web客户端执行。\r\n应当指出，攻击是否成功还取决于用户是否通过管理员接口的认证。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('60', '962', 'web服务器', 'Coldfusion 8.0.1 文件上传漏洞', ' 高危', '文件上传 ', '攻击者可以上传恶意代码文件，从而控制服务器。', 'CKSource FCKeditor（现称CKEditor）是波兰CKSource公司的一套开源的、基于网页的文字编辑器。该编辑器具有轻量化、易于安装等特点。\r\nFCKeditor的editor/filemanager/browser/default/connectors/php/connector.php模块中存在文件上传限制漏洞：\r\n147.function FileUpload( $resourceType， $currentFolder )\r\n148.{\r\n149.$sErrorNumber = \'0\' ;\n150.$sFileName = \'\' ;\n151.\n152.if ( isset( $_FILES[\'NewFile\'] ) && !is_null( $_FILES[\'NewFile\'][\'tmp_name\'] ) )\n153.{\n154.$oFile = $_FILES[\'NewFile\'] ;\n155.\n156.// Map the virtual path to the local server path.\n157.$sServerDir = ServerMapFolder( $resourceType， $currentFolder ) ;\n158.\n159.// Get the uploaded file name.\n160.$sFileName = $oFile[\'name\'] ;\n161.$sOriginalFileName = $sFileName ;\n162.// Security fix by truzone 01-15-2006\n163.//$sExtension = substr( $sFileName， ( strrpos($sFileName， \'.\') + 1 ) ) ;\n164.//$sExtension = strtolower( $sExtension ) ;\n165.\n166.if(extension_loaded(\"mime_magic\")){167.$sExtension = mime_content_type($oFile[\'tmp_name\']);\n168.}else{\n169.$sExtension = $oFile[\'type\'];\n170.}\n171.// en of security fix by truzone 01-15-2006\n172.global $Config ;\n173.\n174.$arAllowed= $Config[\'AllowedExtensions\'][$resourceType] ;\n175.$arDenied= $Config[\'DeniedExtensions\'][$resourceType] ;\n由于166-170行仅检查了MIME类型的上传请求，因此远程攻击者可以通过pht扩展名向Web服务器上传恶意脚本。', 'CVE-2009-2265', 'CNNVD-200907-058', ' ', '2013-05-14');
INSERT INTO `websoc_spider` VALUES ('61', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '利用token绕过登陆，可获得系统管理员的权限，会导致系统信息泄漏', '可以通过sql注入获取API token,利用token来绕过登陆认证', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('62', '133', 'web服务器', 'lighttpd 1.3.7 远程CGI脚本代码泄漏漏洞', ' 中危', '代码泄漏', 'lighttpd lighttpd x<=1.4.29', 'lighttpd是德国软件开发者Jan Kneschke所研发的一款开源的Web服务器，它的主要特点是仅需少量的内存及CPU资源即可达到同类网页服务器的性能。\r\nlighttpd 1.4.30之前的版本中存在远程拒绝服务漏洞。攻击者可利用该漏洞造成应用程序崩溃，导致拒绝服务。', 'CVE-2005-0453', 'CNNVD-200502-061', ' ', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('63', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 7.0.0-7.0.22\r\nTomcat 6.0.0-6.0.33\r\nTomcat 5.5.0-5.5.34', 'Apache Tomcat是一个流行的开放源码的JSP应用服务器程序。\r\nApache Tomcat在参数处理的实现上存在拒绝服务漏洞。远程攻击者可通过特制的请求利用此漏洞消耗大量的CPU资源，造成拒绝服务。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('64', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以在服务器中执行恶意代码。', 'Apache Struts2的Freemarker存在一个远程代码执行漏洞。当在Freemarker标签中使用表达式常量或强制表达式时使用请求值可能会导致远程代码执行漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('65', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0a9\r\nApache 2.0.35<2.0.50\r\nApache 2.0.32\r\nApache 2.0.28\r\nApache 2.0', 'Apache是一款开放源代码流行的Httpd服务程序。\r\nApache包含的mod_disk_cache模块存在信息泄露问题，远程攻击者可以利用这个漏洞获得客户端验证的敏感信息。\r\nmod_disk_cache模块把所有客户端验证信息存储在磁盘上，问题存在于modules/experimental/mod_disk_cache.c代码中的write_headers()函数：\r\n========================================================================\r\n/* Parse the vary header and dump those fields from the headers_in. */\r\n/* Make call to the same thing cache_select_url calls to crack Vary. */\r\n/* @@@ Some day， not today. */\r\nif (r->headers_in) {\r\n~    int i;\r\n~    apr_table_entry_t *elts = (apr_table_entry_t *)\r\n~       apr_table_elts(r->headers_in)->elts;\r\n~    for (i = 0; i < apr_table_elts(r->headers_in)->nelts; ++i) {\r\n~        if (elts[i].key != NULL) {\r\n~            buf = apr_pstrcat(r->pool， elts[i].key， \": \"，  elts[i].val，\r\n~                  CRLF， NULL);\r\n~            amt = strlen(buf);\r\n~            apr_file_write(hfd， buf， &amt);\r\n~        }\r\n~    }\r\n~    buf = apr_pstrcat(r->pool， CRLF， NULL);\r\n~    amt = strlen(buf);\r\n~    apr_file_write(hfd， buf， &amt);\r\n}\r\n========================================================================\r\n所有r->headers_in字段写入到磁盘上，这个字段包含所有客户端的验证信息。因此攻击者可以借此获得一些验证的明文密码等信息。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('66', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA WebLogic Server = 5.1\r\nBEA WebLogic Server  = 6.1\r\nBEA WebLogic Server = 7.0\r\nBEA WebLogic Server = 7.0.0.1\r\n\r\nBEA WebLogic Server Express = 5.1\r\nBEA WebLogic Server Express = 6.1\r\nBEA WebLogic Server Express  = 7.0\r\nBEA WebLogic Server Express = 7.0.0.1\r\n\r\nBEA WebLogic Server Exterprise = 5.0.1\r\nBEA WebLogic Server Exterprise = 5.1\r\n\r\nTuxedo = 8.0\r\nTuxedo   = 8.1', 'WebLogic 和 Tuxedo 此版本存在SSL认证处理不正确的漏洞。\r\n远程攻击者可以通过伪造的web sites和数据字签名进行欺骗。成功攻击后将取得对应权限。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('67', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 <= 2.2.6', 'yaSSL是一个开源的SSL的开发库。\r\nyaSSL实现上存在多个远程溢出及无效内存访问问题，远程攻击者可能利用此漏洞控制服务器。\r\n-------------------------------------------\r\nA] ProcessOldClientHello缓冲区溢出\r\n-------------------------------------------\r\n用于包含客户端所接收的Hello报文中的数据的缓冲区结构如下(源自yassl_imp.hpp)：\r\nclass ClientHello : public HandShakeBase {\r\n    ProtocolVersion     client_version_;\r\n    Random              random_;\r\n    uint8               id_len_;                         // session id length\r\n    opaque              session_id_[ID_LEN];\r\n    uint16              suite_len_;                      // cipher suite length\r\n    opaque              cipher_suites_[MAX_SUITE_SZ];\r\n    uint8               comp_len_;                       // compression length\r\n    CompressionMethod   compression_methods_;\r\n    ...\r\n这里ID_LEN长度为32个单元，MAX_SUITE_SZ为64，RAN_LEN (Random)为32。如果接收到了旧版的Hello报文的话，所调用的ProcessOldClientHello函数没有执行必要的检查来限制填充上述3个字段的数据数量，导致缓冲区溢出漏洞。\r\n以下是handshake.cpp中的漏洞代码：\r\nvoid ProcessOldClientHello(input_buffer& input, SSL& ssl)\r\n    ...\r\n    ClientHello ch;\r\n    ...\r\n    for (uint16 i = 0; i < ch.suite_len_; i += 3) {    \r\n        byte first = input[AUTO];\r\n        if (first)  // sslv2 type\r\n            input.read(len, SUITE_LEN); // skip\r\n        else {\r\n            input.read(&ch.cipher_suites_[j], SUITE_LEN);\r\n            j += SUITE_LEN;\r\n        }\r\n    }\r\n    ch.suite_len_ = j;\r\n    if (ch.id_len_)\r\n        input.read(ch.session_id_, ch.id_len_);\r\n    if (randomLen < RAN_LEN)\r\n        memset(ch.random_, 0, RAN_LEN - randomLen);\r\n    input.read(&ch.random_[RAN_LEN - randomLen], randomLen);\r\n    ...\r\n------------------------------------------------\r\nB] input_buffer& operator>>缓冲区溢出\r\n------------------------------------------------\r\n用于处理Hello报文的函数中存在另一个缓冲区溢出，但基本不太可能利用这个溢出执行代码。以下是yassl_imp.cpp中的漏洞代码：\r\ninput_buffer& operator>>(input_buffer& input, ClientHello& hello)\r\n    ...\r\n    hello.id_len_ = input[AUTO];\r\n    if (hello.id_len_) input.read(hello.session_id_, ID_LEN);\r\n    // Suites\r\n    byte tmp[2];\r\n    tmp[0] = input[AUTO];\r\n    tmp[1] = input[AUTO];\r\n    ato16(tmp, hello.suite_len_);\r\n    input.read(hello.cipher_suites_, hello.suite_len_);\r\n    ...\r\n-----------------------------------------------------\r\nC] HASHwithTransform::Update无效内存访问\r\n-----------------------------------------------------\r\n在Hello报文中使用了过大的大小值会由于越界读取内存而导致函数库崩溃。以下是hash.cpp中的漏洞代码：\r\nvoid HASHwithTransform::Update(const byte* data, word32 len)\r\n{\r\n    // do block size increment\r\n    word32 blockSz = getBlockSize();\r\n    byte*  local   = reinterpret_cast<byte*>(buffer_);\r\n    while (len) {\r\n        word32 add = min(len, blockSz - buffLen_);\r\n        memcpy(&local[buffLen_], data, add);\r\n        ...', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('68', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35>=2.0.52\r\nApache 2.0.32\r\nApache 2.0.28\r\nApache 2.0', 'Apache Web Server是一款HTTP服务程序。\r\nApache Web Server不正确处理包含多个空格符号的请求，远程攻击者可以利用这个漏洞对服务程序进行拒绝服务攻击。\r\nChintan Trivedi报告远程攻击者可以发送多个特殊构建的包含多个空格符号的HTTP GET请求，可导致WEB服务器消耗大量资源而造成拒绝服务。\r\n供应商后来报告字段长度限制不能很好的防止部分恶意请求造成的危害。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('69', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.* < 1.3.31\r\nApache 2.* < 2.0.49', 'Apache是一款开放源代码WEB服务程序。\r\nApache存在安全问题，远程攻击者可以利用这个漏洞对Apache服务进行拒绝服务攻击。\r\n远程攻击者通过一个在一个极少有可能访问的端口上监听的套接口，可使Apache引起拒绝服务攻击。这可封闭到服务器的新的连接，直到初始化在另一个极少有可能访问的端口上发起的其他连接。\r\n连接阻挡(connect blocking)功能除了Windows平台之外，其他所有平台中默认打开。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('70', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Roller 2.3, 3.0, 3.1, 4.0', 'Apache Roller 2.3， 3.0， 3.1， and 4.0 存在跨站脚本攻击漏洞，远程攻击者功过搜索中的参数q可以执行任意web脚本和HTML。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('71', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'Libsys 5.0', '漏洞代码 file_get_content($_GET[\'url\']) 直接接受参数，读取并返回', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('72', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'ECstore', 'ECstore 利用http://mall.brands.com.tw/ec/index.php/tools-staticPage.html?filename=../../../robots.txt类似方法可以包含任何本地文件。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('73', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'CMSimple 4.4, 4.4.2', 'CMSimple是丹麦软件开发者Peter Andreas Harteg所研发的一套基于PHP的小型网站内容管理工具。该工具支持所见即所得编辑器、文件自动备份和多种语言等。\r\nCMSimple 4.4.2及之前的版本中存在远程文件包含漏洞，该漏洞源于程序没有充分过滤用户提交的输入。攻击者可利用该漏洞在Web服务器进程上下文中执行任意脚本代码。', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('74', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.3.x, 2.0.x~2.0.64, 2.2.x~2.2.19', 'Apache HTTP Server是一款开源的流行的HTTPD服务程序。\r\n当处理包含大量Ranges头的HTTP请求时，ByteRange过滤器存在一个错误，攻击者可以向服务器发送特制HTTP请求，消耗大量内存，造成应用程序崩溃。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('75', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'WordPress download-shortcode 1.1', 'download-shortcode 1.1插件中/wp-content/force-download.php?file=xxx file参数未做正确过滤,导致存在本地文件包含漏洞', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('76', '853', 'web服务器', 'JBoss 5.1.0 DeploymentFileRepository 代码执行漏洞', ' 高危', '代码执行 ', 'JBoss 4.2.0', 'JBoss是基于J2EE的开放源代码的应用服务器，其4.2.0版本默认会开启BSHDeployer服务， 当攻击者绕过JMX-console拦截里， 可以利用BSHDeployer服务方便地（将war信息直接写在bsh文件里）部署一个war，从而成功地远程部署了恶意代码。', 'CVE-2006-5750', 'CNNVD-200611-435', ' ', '2012-08-13');
INSERT INTO `websoc_spider` VALUES ('77', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.8.0-0.8.15\r\nnginx 0.7.0-0.7.62\r\nnginx 0.6.0-0-6.39\r\nnginx 0.1.0-0-5.37', 'nginx是俄罗斯软件开发者Igor Sysoev所研发的一款HTTP和反向代理服务器，也可以作为邮件代理服务器。 \r\nnginx的ngx_http_parse_complex_uri()函数在处理特制的URI时存在整数下溢漏洞，远程攻击者可以通过向服务器提交恶意请求导致所分配缓冲区之前的内存位置写入URI中所包含的数据。\r\nnginx设计为以单个特权主进程运行，多个非特权的worker进程处理特定的请求，因此成功利用这个漏洞可能导致以worker进程的权限执行任意指令或worker进程崩溃。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('78', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'Nginx 0.8.41-1.5.6', 'Ngnix在遇到空格时，便跳过对\\0字符的检测，从而绕过之前发现\\0便抛出NGX_HTTP_PARSE_INVALID_REQUEST的错误，使得之前\\0又重新起作用。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('79', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.7.x/0.8.x', 'nginx是俄罗斯软件开发者Igor Sysoev所研发的一款HTTP和反向代理服务器，也可以作为邮件代理服务器。 \r\nnginx服务器无法处理交换数据流(ADS)，将其处理为普通文件的数据量。攻击者可以使用filename::$data的形式读取并下载Web应用文件的源码；此外如果在HTTP请求中添加了目录遍历序列的话，就可以覆盖内存寄存器，导致拒绝服务。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('80', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.8.36', 'nginx是俄罗斯软件开发者Igor Sysoev所研发的一款HTTP和反向代理服务器，也可以作为邮件代理服务器。\r\nnginx存在安全漏洞。远程攻击者可以借助某些编码的目录遍历序列触发内存占用，导致拒绝服务。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('81', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.5.* 0.6.* 0.7<=0.7.65 0.8<=0.8.37', 'Ngnix在遇到%00空字节时与后端FastCGI处理不一致，导致可以在图片中嵌入PHP代码然后通过访问xxx.jpg%00.php来执行其中的代码。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('82', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'Nginx:0.8.34\r\nNginx:0.8.35\r\nNginx:0.8.32\r\nNginx:0.8.33\r\nNginx:0.8.38\r\nNginx:0.8.39\r\nNginx:0.8.36', 'nginx是俄罗斯软件开发者Igor Sysoev所研发的一款HTTP和反向代理服务器，也可以作为邮件代理服务器。 \r\nnginx服务器无法处理交换数据流(ADS)，将其处理为普通文件的数据量。攻击者可以使用filename::$data的形式读取并下载Web应用文件的源码；此外如果在HTTP请求中添加了目录遍历序列的话，就可以覆盖内存寄存器，导致拒绝服务。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('83', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.6.* 0.7.* 0.8.*', 'nginx默认可配置以CGI的方式支持PHP运行。nginx在解析PHP脚本文件路径时存在安全问题。站点在允许用户上传文件并可以以WEB权限访问的情况下，攻击者可以上传恶意文件，并在上传文件后提交php扩展名进行访问，可以WEB权限执行任意代码。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('84', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', '版本: v1.3 2004/07/13，造成服务器上的任意文件读取。', 'iGENUS邮件系统为用户提供完整的电子邮件服务解决方案，实现了基于Linux平台的邮件系统全面管理。 由于 \'Login.php\' 文件未对 Lang 参数进行严格控制, %00截断后可读取任意目录下的文件。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('85', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'Modoer 2.5', '脚本在处理用户传递参数 “do” 时未对其进行严格过滤和检测，最终将其拼接成字符串，然后include该路径。当使用低版本php时可以通过 / 超长截断来包含任意文件。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('86', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.1.0-0.4.15\r\nnginx 0.5.0-0.5.38\r\nnginx 0.6.0-0.6.39\r\nnginx 0.7.0-0.7.62\r\nnginx 0.8.0-0.8.14', 'nginx (又称Engine X) 0.1.0至0.4.14版本, 0.5.38版本之前的版本0.5.x , 0.6.39版本之前的版本0.6.x, 0.7.62版本之前的版本0.7.x,以及0.8.14版本之前的版本0.8.x中的src/http/ngx_http_parse.c，允许远程攻击者可以借助一个超长的URI，造成拒绝服务(空指针废弃和工作程序崩溃)。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('87', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.7.64', 'nginx 0.7.64 版本向具有一个没有审查过的非打印字符的登录文件写入数据，这可能会允许远程攻击者借助一个包含终极模拟器的一个溢出序列的HTTP请求，修改一个窗口的标题或可能执行任意执行并重写文件。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('88', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'WordPress TheCartPress Plugin 1.3.9', '用户传入的值直接带入 include 中导致任意本地文件包含漏洞。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('89', '70', 'web服务器', 'nginx 0.8.15 HTTP远程请求缓冲区溢出漏洞', ' 高危', '缓冲区溢出', 'nginx 0.7.0-0.7.63\r\nnginx 0.8.0-0.8.17', 'nginx (又称Engine X) 0.7.63版本之前的版本, 以及0.8.17版本之前的0.8.x版本中的src/http/modules/ngx_http_dav_module.c存在目录遍历漏洞。远程验证用户可以借助一个对WebDAV(1)COPY或(2)MOVE方法的目的HTTP页眉中的..，创建或重写任意文件。', 'CVE-2009-2629', 'CNNVD-200909-302', 'CNVD-2009-13840', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('90', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'Qibo Blog 1.0', '用户传入的变量直接带入 SQL 语句导致 SQL 注入漏洞，可以造成管理员密码泄露。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
