INSERT INTO `websoc_spider` VALUES ('92', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'Hikvision iVMS-4200', '海康威视ivms-4200系列视频监控系统日志读取模块存在文件包含漏洞，由于日志读取模块对访问权限未做验证，且对文件参数controller未作过滤，导致任意用户可通过路径穿越的方式读取任意文件。同时，攻击者可利用该漏洞执行通过get请求写入到web日志中的恶意代码，获得webshell(需配合nginx解析漏洞)。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('93', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'DedeCms 5.6', '/plus/carbuyaction.php?dopost=return&code= 当变量dopost 为return时,会使用require_once直接包含此code变量,导致本地文件包含漏洞', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
