INSERT INTO `websoc_spider` VALUES ('95', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'phpMyAdmin 4.2.12', '在libraries/gis/pma_gis_factory.php函数factory 将传入的参数$type转换小写以后赋值给$type_lower，直接拼接成路径进行include_once导致本地文件包含漏洞产生', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('96', '853', 'web服务器', 'JBoss 5.1.0 DeploymentFileRepository 代码执行漏洞', ' 高危', '代码执行 ', 'JBoss 4.2.0', 'JBoss是基于J2EE的开放源代码的应用服务器，其4.2.0默认会开启JMXInvoker， 攻击可使用JMXInvoker部署一个war，从而成功地远程部署了恶意代码。', 'CVE-2006-5750', 'CNNVD-200611-435', ' ', '2012-08-13');
INSERT INTO `websoc_spider` VALUES ('97', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'Qibo Menhu V5', '齐博门户系统 /hr/listperson.php 中 $tplpath 参数可控，并未经过过滤传入 require 函数，导致本地文件包含，可以造成信息泄露、Getshell 等。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('98', '853', 'web服务器', 'JBoss 5.1.0 DeploymentFileRepository 代码执行漏洞', ' 高危', '代码执行 ', 'JBoss 4.2.0', 'JBoss是基于J2EE的开放源代码的应用服务器，其4.2.0版本会开启WebConsole， 攻击可使用WebConsole/Invoker部署一个war，从而成功地远程部署了恶意代码。', 'CVE-2006-5750', 'CNNVD-200611-435', ' ', '2012-08-13');
INSERT INTO `websoc_spider` VALUES ('99', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'ThinkSNS 3.1', 'ThinkSNS 3.1 /apps/page/Lib/Action/DiyAction.class.php 中由于过滤不严，当gpc=Off时任意文件读取，可配合图片上传直接getshell。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('100', '853', 'web服务器', 'JBoss 5.1.0 DeploymentFileRepository 代码执行漏洞', ' 高危', '代码执行 ', 'jboss 4.0~5.1.0', 'JBoss Application Server (jbossas)中的DeploymentFileRepository类存在目录遍历漏洞，远程认证用户可以通过和控制台管理器相关的未明向量，读取或修改任意文件并可能执行任意代码。', 'CVE-2006-5750', 'CNNVD-200611-435', ' ', '2012-08-13');
INSERT INTO `websoc_spider` VALUES ('101', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'DedeCMS 5.7 SP1', '/install/index.php 存在远程文件包含漏洞', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('102', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'ShopBuilder 5.6.1', 'footer.php 为过滤 m 参数，导致 %00 截断包含，可以在一定条件下 getshell。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('103', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'WordPress Really Simple Guest Post Plugin', 'WordPress是WordPress软件基金会的一套使用PHP语言开发的博客平台，该平台支持在PHP和MySQL的服务器上架设个人博客网站。Really Simple Guest Post是其中的一个访客发表评论的插件。\r\nWordPress Really Simple Guest Post插件中存在本地文件包含漏洞，该漏洞源于程序没有充分过滤用户提交的输入。攻击者可利用该漏洞获取敏感信息，并执行任意本地脚本，控制应用程序和计算机。', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('104', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'PHPWind 7.5', 'phpwind7.5 apps/groups/index.php 里$route和$basePath变量可控 导致远程包含或者本地包含文件导致执行任意代码', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('105', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Tomcat 7.0.x<7.0.7\r\nApache Tomcat 6.0.x<6.0.31', 'Apache Tomcat 是一款由Apache Foundation维护的免费开放源代码的Java Servlet和JSP服务程序。\r\nApache Tomcat 7.0.0至7.0.6，和6.0.0至6.0.30没有为与NIO HTTP连接器有关的请求强制执行maxHttpHeaderSize限制。远程攻击者可以借助特制请求导致拒绝服务(OutOfMemoryError)。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('106', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.3.*<=1.3.39\r\nApache 2.2.*<= 2.2.6\r\nApache 2.0.*<= 2.0.61', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache HTTP Server 2.2.6和较早版本2.2.x系列、2.0.61和较早版本2.0.x系列、1.3.39和较早版本1.3.x系列中的mod_negotiation模块中存在跨站脚本漏洞。远程认证攻击者利用该漏洞通过上传带有包含XSS序列名字的文件和文件扩展，注入任意web脚本或HTML，文件扩展名请求被程序忽略，这将导致程序的（1）“406 Not Acceptable”页面或者（2）“300 Multiple Choices”HTTP响应页面受到注入攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('107', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =2.0.63 \r\nApache =2.2.13', 'pache HTTP Server(简称Apache)是Apache软件基金会的一个开放源码的网页服务器，可以在大多数计算机操作系统中运行，由于其多平台和安全性被广泛使用，是最流行的Web服务器端软件之一。\r\nApache HTTP Server 2.0.63和2.2.13的od_proxy_ftp模块的modules/proxy/proxy_ftp.c中的ap_proxy_ftp_handler函数允许远程FTP服务商借助一个畸形的对EPSV指令的回应，引起拒绝服务攻击(空指针引用和child进程崩溃)。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('108', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'PHPYun v3.1.0604', '由于weixin的index_action函数使用SimpleXML对POST中的数据进行XML解析，导致可以在XML中包含任意文件而index_action解析后又会将字符串返回到客户端，因此造成任意文件包含漏洞', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('109', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'php <= 5.3.12\r\nphp <= 5.4.2', 'PHP（PHP：Hypertext Preprocessor，PHP：超文本预处理器）是PHP Group和开放源代码社区共同维护的一种开源的通用计算机脚本语言。该语言支持多重语法、支持多数据库及操作系统和支持C、C++进行程序扩展等。\r\nPHP中存在信息泄漏漏洞。远程攻击者可利用该漏洞在服务器进程上下文中查看文件的源代码，获取敏感信息，在受影响计算机上运行任意PHP代码，也可能执行其他攻击。', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('110', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 < 2.2.14', 'Apache HTTP Server(简称Apache)是Apache软件基金会的一个开放源码的网页服务器，可以在大多数计算机操作系统中运行，由于其多平台和安全性被广泛使用，是最流行的Web服务器端软件之一。\r\nApache HTTP服务器的mod_proxy_ftp模块中存在权限许可和访问控制漏洞。远程攻击者可借助与嵌入到认证HTTP头的命令有关的向量，绕过预设的访问限制并发送任意命令到FTP服务器。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('111', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'Kuwebs 3.1.3', 'Kuwebs的 admin/index.php文件由于使用了include_once函数引入来自变量的文件地址，在PHP配置include_once开启的情况下可直接控制变量内容提交远程webshell文件URL进行远程包含。', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('112', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group Apache 2.0.39\r\nApache Group Apache 2.0.36\r\nApache Group Apache 2.0.35\r\nApache Group Apache 2.0.34-BETA win32\r\nApache Group Apache 2.0.32-BETA win32\r\nApache Group Apache 2.0.32\r\nApache Group Apache 2.0.28-BETA win32\r\nApache Group Apache 2.0.28-BETA\r\nApache Group Apache 2.0.28\r\nApache Group Apache 2.0', 'Apache是一款广泛流行的开放源代码的WEB服务程序。\r\nApache 2.0.40以前的for windows版本中存在一个目录遍历漏洞，远程攻击者可以利用这个漏洞获得服务器上任意文件内容甚至执行系统命令。\r\nApache 2.0.x for windows没有正确检查\'\\\'( == %5c)字符，攻击者可能利用这个字符来进行目录遍历攻击。攻击者可能获取与apache同一驱动器下的任意系统文件内容，如果存在可执行目录，例如/cgi-bin，攻击者甚至可以执行任意系统命令。\r\nUnix和其他平台的Apache Web服务程序不受此漏洞影响，Cygwin用户受到此漏洞影响。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('113', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'EkuCMS 2.5', 'core\\Lib\\Action\\Home\\MyAction.class.php 未过滤\"\\\"导致文件包含，同时利用 ThinkPHP 的日志功能产生包含一句话木马的日志，包含日志进行getshell。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('114', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 < 2.2.14', 'Apache Portable Runtime (APR) library的poll/unix/port.c的Event Port backend中的Solaris pollset特征, 当在Apache HTTP Server中运行的时候,当地处理错误，远程攻击者可以借助与prefork和event MPMs相关的未明的HTTP请求，造成拒绝服务(后台程序悬挂)。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
