INSERT INTO `websoc_spider` VALUES ('118', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'PTCeffect 4.6', 'PTCeffect是一套PTC商业智能软件。该软件提供网站统计、支付系统和转介系统等模块。\r\nPTCeffect中存在SQL注入漏洞和本地文件包含漏洞，这些漏洞源于程序没有充分过滤用户提交的数据。攻击者可利用这些漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞，使用目录遍历字符串在应用程序上下文中执行本地脚本代码，或获取敏感信息。PTCeffect 4.6版本中存在漏洞，其他版本也可能受到影响。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
