INSERT INTO `websoc_spider` VALUES ('128', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group Geronimo 2.1 - 2.1.3', 'Apache Geronimo是美国阿帕奇（Apache）软件基金会的一款开源的J2EE服务器产品，该产品具有可伸缩性、可进行配置管理等特点。\r\nApache Geronimoweb管理控制台的/console/portal/Server/Monitoring脚本没有正确的验证用户请求中的name、ip、username、description等参数，远程攻击者可以通过提交恶意请求执行跨站脚本或跨站请求伪造攻击；此外/console/portal//Services/Repository脚本没有正确地验证', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('129', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache  2.0.1<=2.0.4\r\nApache  1.29 \r\nApache  1.27 \r\nApache  1.3\r\nApache  1.99', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache没有正确地验证对Apache::Status和Apache2::Status模块所传送的输入便返回给了用户，远程攻击者可以通过向上述模块提交恶意请求执行跨站脚本攻击，导致在用户浏览器会话中执行任意HTML和脚本代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
