INSERT INTO `websoc_spider` VALUES ('132', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_proxy_ajp =2.2.11', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache服务器的mod_proxy_ajp模块在处理畸形的POST请求时存在错误，远程攻击者可以通过提交特制的HTTP请求泄露其他用户请求相关的响应数据。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('133', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'wordpress linenity theme 2.0', 'WordPress是WordPress软件基金会的一套使用PHP语言开发的博客平台，该平台支持在PHP和MySQL的服务器上架设个人博客网站。Linenity是其中的一个杂志主题。\r\nWordPress Linenity主题中存在本地文件包含漏洞，该漏洞源于程序没有充分过滤用户提交的输入。攻击者可利用该漏洞获取敏感信息，也可能存在其他形式的攻击。Linenity 1.2.0版本中存在漏洞，其他版本也可能受到影响。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('134', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'phpcms V9', 'PHPCMS是中国盛大网络（Shanda）公司的一套基于PHP和Mysql架构的网站内容管理系统。该系统包括新闻、图片、下载、信息、产品等模块。\r\n由于文件/plugin.php对于用户提交的变量未过滤，导致本地文件包含漏洞的产生。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('135', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'PHPCMS V9', 'PHPCMS是中国盛大网络（Shanda）公司的一套基于PHP和Mysql架构的网站内容管理系统。该系统包括新闻、图片、下载、信息、产品等模块。\r\n由于PHPCMSV9文件attachments.php的未验证上传用户权限，可以上传文件(仅图片)。加上其他文件存在可以截断的本地包含漏洞，导致包含任意文件并获取webshell。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('136', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'PHPWind 7.5', '1、apps/groups/index.php 里$route和$basePath变量没有初始化，导致远程包含或者本地包含php文件,导致执行任意php代码；\r\n2、apps/share/index.php 里$route和$basePath变量没有初始化，导致远程包含或者本地包含php文件,导致执行任意php代码；\r\n3、api/class_base.php文件里callback函数里$mode变量没有过滤导致任意包含本地文件，从而可以执行任意PHP代码。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('137', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'phpMyAdmin * < 2.6.4 pl3', 'phpMyAdmin是用PHP编写的工具，用于通过WEB管理MySQL。\r\nphpMyAdmin 2.6.4-pl3 及以前版本 grab_globals.php 文件中的register_globals emulation 层不能完成对上传文件中_FILES数组值的检测，这允许攻击者利用不使用grab_globals.php的直接请求包含任意的本地文件。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('138', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache  2.2.* <2.2.7\r\nApache 2.0.* <2.0.62\r\nApache 1.3.* < 1.3.40', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache2.0.63之前版本机2.2系列中低于2.2.9的版本中 mod_proxy_ftp模块存在跨站脚本漏洞。\r\n远程攻击者可以构造一个恶意的FTP URI(路径最后一个目录组件包含有至少一个通配符)，注入任意Web脚本及HTML。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('139', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_proxy_http =2.0.63\r\nmod_proxy_http =2.2.8', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache的mod_proxy_http模块中的ap_proxy_http_process_response()函数没有正确地转发中间响应，如果mod_proxy受骗向客户端发送了大量的中间响应的话，就会耗尽大量内存资源。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('140', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'phpMyAdmin * < 2.7.0rc1', 'phpMyAdmin是用PHP编写的工具，用于通过WEB管理MySQL。\r\nphpMyAdmin对某些变量的处理上存在漏洞，远程攻击者可能利用此漏洞在主机上执行任意命令。phpMyAdmin将不应该被register_globals仿真层覆盖的变量黑名单存储在全局变量中，但却没有包含$import_blacklist变量名，因此攻击者可以覆盖该变量。如果覆盖了该变量的话，攻击者就可以用任意内容覆盖$GLOBALS数组，导致执行任意代码。', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('141', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache < 1.3.41 + ssl < 1.59', 'Apache-SSL apache_1.3.41+ssl_1.59之前的版本中的ExpandCert函数，没有正确的处理客户信任凭证中的标识符中的(1) \'/\'和(2)\'=\'字符，这可能使得运程攻击者可以借助一个特制的DN，绕过身份验证。该DN会触发对环境变量的重写。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('142', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 <=2.2.63', '在Apache HTTP Server 2.2.0 至 2.2.6的mod_proxy_balancer模块中的负载均衡管理功能存在跨站脚本攻击漏洞。远程攻击者可以借助ss、wr、或者(3) rr参数以及URL，注入任意的web脚本或HTML。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('143', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 < 2.2.11', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache HTTP Server没有正确地处理AllowOverride指令中的Options=IncludesNOEXEC选项，本地用户可以通过在.htaccess文件中配置(1) Options Includes、(2) Options +Includes或(3) Options +IncludesNOEXEC并在.shtml文件中注入exec元素导致绕过安全限制获得权限。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('144', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 <= 2.2.6\r\nApache 2.0.35 <= 2.0.61\r\nApache 1.3.2 <= 1.3.39', '在Apache HTTP Server 2.2.0 至 2.2.6, 2.0.35 至 2.0.61, and 1.3.2 至 1.3.39的mod_status中的跨站脚本攻击漏洞在server-status页激活的情况下会允许远程攻击者借助未知名的向量注入任意的网站脚本或HTML。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('145', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.2.0 <= 2.2.6 \r\nApache 2.0.35 <= 2.0.61\r\nApache 1.3.0 <= 1.3.39', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache的mod_imagemap和mod_imap模块中没有正确地过滤某些用户输入，远程攻击者提交恶意的HTTP请求执行跨站脚本攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('146', '166', '攻击方式', 'phpMyAdmin 2.6.4 Pl3以前版本本地文件包含漏洞', ' 中危', '本地文件包含', 'PHPCMS V9', 'PHPCMS是中国盛大网络（Shanda）公司的一套基于PHP和Mysql架构的网站内容管理系统。该系统包括新闻、图片、下载、信息、产品等模块。\r\n由于PHPCMSV9文件attachments.php的未验证上传用户权限，可以上传文件(仅图片)。加上其他文件存在可以截断的本地包含漏洞，导致包含任意文件并获取webshell。', 'CVE-2005-3300', 'CNNVD-200510-184', 'CNVD-2009-08177', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('147', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35<= 2.0.63\r\nApache 2.2.9', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache2.0.63之前版本机2.2系列中低于2.2.9的版本中 mod_proxy_ftp模块存在跨站脚本漏洞。\r\n远程攻击者可以构造一个恶意的FTP URI(路径最后一个目录组件包含有至少一个通配符)，注入任意Web脚本及HTML。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('148', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'David Castro Apache::AuthCAS 0.4', 'Apache::AuthCAS是可配置的Apache认证模块。\r\nApache::AuthCAS模块中存在SQL注入漏洞，远程攻击者可能利用此漏洞非授权操作数据库。\r\n在CPAN版本的516行，会话ID是直接从cookie获得的：\r\n $cookie =~ /.*$SESSION_COOKIE_NAME=([^;]+)(\\s*;.*|\\s*$)/;\r\n $sid = $1 || \"\";\r\n然后在544行未经任何过滤便传送给了get_session_data()函数，get_session_data()在1005行将$sid注入到SQL中：\r\nmy $sth = $dbh->prepare(\"SELECT last_accessed, uid, pgtiou FROM $DB_SESSION_TABLE WHERE id=\'$sid\';\");\r\n这就允许攻击者通过控制cookie来执行SQL注入攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('149', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.* <2.2.6', 'Apache HTTP Server中的mod_autoindex.c存在跨站脚本攻击漏洞,当服务器发生页面的字符集没有明确定义时，远程攻击者可以借助利用UTF-7 字符集的p参数注入任意web脚本或HTML。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('150', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Jackrabbit  1.5\r\nApache Jackrabbit  1.4', 'Apache Jackrabbit是一个完全遵守Java API版的内容存储规范(JCR)的实现。\r\nApache Jackrabbit的search.jsp和swr.jsp页面没有正确地过滤q参数便返回给了用户，这允许远程攻击者通过提交恶意请求执行跨站脚本攻击，在用户浏览器会话中执行任意HTML和脚本代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('151', '180', '攻击方式', 'phpMyAdmin 2.7.0 全局变量覆盖导致文件包含漏洞', ' 高危', '远程文件包含', 'PHPWind 7.5', 'PHPWind 7.5版本中apps/share/index.php 里$route和$basePath变量没有初始化,导致远程包含或者本地包含php文件,导致执行任意php代码', 'CVE-2005-4079', 'CNNVD-200512-131', 'CNVD-2009-08309', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('152', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =2.2.4 \r\nApache =2.0.59', 'Apache Software Foundation Apache HTTP Server中存在代码注入漏洞。本地用户可以借助在worker进程中被执行的特定代码，引起拒绝服务攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('153', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =1.3.37\r\nApache =2.0.59\r\nApache =2.2.4', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache HTTP Server Worker进程实现上存在多个漏洞，本地攻击者可能利用这些漏洞导致服务不可用。\r\n在发送信号之前Apache HTTP Server没有验证进程为Apache子进程。能够在Apache HTTP Server上运行脚本的本地攻击者可以控制记分板并终止任意进程，导致拒绝服务。\r\n如果Apache httpd安装了Prefork MPM模块的话，本地攻击者就可以修改worker_score和process_score数组使其引用任意进程ID，主进程向其发送了SIGUSR1信号的话就会导致拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('154', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'weblogic = 7.0-10.0', 'Oracle Database是一款商业性质大型数据库系统BEA Product Suite组件\"WebLogic Server Plugins for Apache component\"未明安全漏洞，远程攻击者可以通过未明向量影响机密性、完整性和不可否认性。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('155', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =2.3.0\r\nApache 2.2.x <= 2.2.4\r\nApache 2.0.x <= 2.0.59', 'Apache 2.3.0版本的模块s/proxy/proxy_util.c (mod_proxy)中的日期处理代码,当运行一个threaded MPM时, 远程源服务器可以借助特制的能触发一个缓冲区重写的日期处理器，造成拒绝服务(caching forward proxy程序崩溃)。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('156', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'weblogic = 7.0-10.0', 'Oracle Database是一款商业性质大型数据库系统BEA Product Suite组件\"WebLogic Server Plugins for Apache component\"未明安全漏洞，远程攻击者可以通过未明向量影响机密性、完整性和不可否认性。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('157', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_cache 2.2.x<=4\r\nmod_cache 2.0.x<=59\r\nmod_cache 1.3.x<=37', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache HTTP Server(httpd)的mod_cache模块中的cache_util.c文件存在安全漏洞，远程攻击者可能利用此漏洞导致服务器崩溃。\r\n如果服务器启用了缓存且在使用线程多处理模块(MPM)，远程攻击者就可以通过发送特制的HTTP请求且不对s-maxage、max-age、min-fresh或max-stale Cache-Control头设置值导致子进程处理器崩溃。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('158', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =2.2.4', 'Apache是一款开放源代码WEB服务程序。\r\nApache mod_mem_cache文件中的recall_headers函数没有正确拷贝所有级别的首部数据，如果用户向服务器提交了恶意请求的话就可能返回包含有之前使用数据的HTTP头，导致泄露敏感信息。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('159', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA Systems Weblogic Server = 8.1 SP [1,6]\r\nBEA Systems Weblogic Server = 7.0 SP [1,7]\r\nBEA Systems Weblogic Server = 6.1 SP 7\r\nBEA Systems Weblogic Server = 6.1 SP [1, 5]\r\n\r\nBEA Systems Weblogic Server = 9.2 Maintenance Pack\r\nBEA Systems Weblogic Server = 9.2\r\nBEA Systems Weblogic Server = 9.1\r\nBEA Systems Weblogic Server = 9.0\r\nBEA Systems Weblogic Server = 10.0 MP1\r\nBEA Systems Weblogic Server = 10.0\r\n\r\nBEA Systems WebLogic Express = 8.1 SP [1,5]\r\nBEA Systems WebLogic Express = 8.1 \r\nBEA Systems WebLogic Express = 7.0 .0.1 SP [1,4]\r\nBEA Systems WebLogic Express = 7.0 SP [1,7]\r\nBEA Systems WebLogic Express = 6.1 SP 7\r\nBEA Systems WebLogic Express = 6.1 SP [1,5]\r\n\r\nBEA Systems WebLogic Express = 9.2\r\nBEA Systems WebLogic Express = 9.1\r\nBEA Systems WebLogic Express = 9.0\r\nBEA Systems WebLogic Express = 8.1.0 SP 6\r\nBEA Systems WebLogic Express = 10.0', 'WebLogic包含多种应用系统集成方案，包括Server/Express/Integration等。\r\nWebLogic的Apache连接器实现上存在漏洞，如果远程攻击者向WebLogic的Apache连接器发送了超长的POST请求的话，就可能触发栈溢出，导致执行任意指令。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('160', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA Systems WebLogic Server for Win32 8.1 SP [1,2]\r\nBEA Systems WebLogic Server for Win32 8.1 \r\nBEA Systems WebLogic Server for Win32 7.0 SP [1,5]\r\nBEA Systems WebLogic Server for Win32 7.0 \r\nBEA Systems WebLogic Server for Win32 6.1 SP [1,6]\r\nBEA Systems WebLogic Server for Win32 6.1 \r\nBEA Systems Weblogic Server 8.1 SP [1,2]\r\nBEA Systems Weblogic Server 8.1 \r\nBEA Systems Weblogic Server 7.0 SP [1,5]\r\nBEA Systems Weblogic Server 7.0 \r\nBEA Systems Weblogic Server 6.1 SP6\r\nBEA Systems Weblogic Server 6.1 SP [1,5]\r\nBEA Systems Weblogic Server 6.1\r\n\r\nBEA Systems WebLogic Express for Win32 8.1 SP [1,2]\r\nBEA Systems WebLogic Express for Win32 8.1 \r\nBEA Systems WebLogic Express for Win32 7.0 SP [1,5]\r\nBEA Systems WebLogic Express for Win32 7.0 \r\nBEA Systems WebLogic Express for Win32 6.1 SP [1,6]\r\nBEA Systems WebLogic Express for Win32 6.1 \r\nBEA Systems WebLogic Express 8.1 SP [1,2]\r\nBEA Systems WebLogic Express 8.1 \r\nBEA Systems WebLogic Express 7.0 SP [1,5]\r\nBEA Systems WebLogic Express 7.0 \r\nBEA Systems WebLogic Express 6.1 SP [1,6]\r\nBEA Systems WebLogic Express 6.1', 'BEA WebLogic Server和Express 8.1 SP2及其以前的版本，7.0 SP4及其以前的版本， 6.1至SP6，和5.1至SP13版本的默认配置响应HTTP TRACE请求，远程攻击者使用跨站跟踪攻击（XST)盗取信息，该攻击在易受跨站脚本影响的应用进程中。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('161', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_status 2.2.0\r\nmod_status 2.2.3\r\nmod_status 2.2.4', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache HTTP Server(httpd)的mod_status模块中的mod_status.c文件存在跨站脚本漏洞，远程攻击者可能利用此漏洞在用户的浏览器中执行恶意代码。\r\n如果站点的server-status页面公开可访问且启用了ExtendedStatus的话，远程攻击者就可以在执行字符集检查的浏览器中通过没有指定content-type的页面注入并执行脚本或HTML代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('162', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group mod_tcl 1.0', 'Apache是一款开放源代码WEB服务程序。\r\nApache的mod_tcl模块中存在编程错误，成功利用这个漏洞的攻击者可能以httpd的权限执行任意指令。\r\nmod_tcl模块允许将用户提供的数据做为一些参数的格式串标识符传送给内部定义变量参数函数。set_var函数声明如下：\r\n  mod_tcl.h:117:void set_var(Tcl_Interp *interp, char *var1, \r\n                             char *var2, const char *fmt, ...);\r\n对这个函数有几个不安全调用：\r\n  tcl_cmds.c:437:         set_var(interp, nm_var, (char*) key, \r\n                                  (char*) val);\r\n  tcl_cmds.c:2231:                set_var(interp, nm_env, env[i], \r\n                                          sptr + 1);\r\n  tcl_core.c:650:                         set_var(interp, namespc, \r\n                                                  vl[i].var2, \r\n                                                  vl[i].var3);', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('163', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA WebLogic Server and Expres = 7.0\r\nBEA WebLogic Server and Expres  = 7.0.0.1', 'BEA WebLogic Server以及Express 7.0版本和7.0.0.1版本的Weblogic.admin在明文中将JDBCConnectionPoolRuntimeMBean密码显示到屏幕上，攻击者可以通过物理观察(\"shoulder surfing\")屏幕来读取用户密码。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('164', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 2.2.2', 'Apache是一款开放源代码WEB服务程序。\r\nApache的mod_alias模块在支持区分大小写目录名的文件系统上处理区分大小写的别名指令参数时存在规范化错误。如果攻击者访问有大写的目录名(如CGI-BIN)的URL的话，就可以在ScriptAlias指令引用了文档根目录中目录的某些非默认配置中泄漏cgi-bin目录中应用程序的源码。\r\n有漏洞配置示例：\r\nDocumentRoot \"[path]/docroot/\"\r\nScriptAlias /cgi-bin/ \"/[path]/docroot/cgi-bin\"', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('165', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA WebLogic Server = 7.0\r\nBEA WebLogic Server  = 7.0.0.1', 'BEA WebLogic Server和Express 7.0版本以及7.0.0.1版本在config.xml、filerealm.properties和weblogic-rar.xml中不安全地存储某些含关于密码加密术的隐私，本地用户可以得知那些隐私以及解码。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('166', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod-proxy-balancer 2.2\r\nmod-proxy-balancer 2.2.2<= 2.2.6', 'Server 2.2.7版权及之前的2.2.x所有版本的mod_proxy_balancer模块存在跨站请求伪造漏洞，当在windows系统上运行时，允许远程攻击者通过一个长未明向量获取特权。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('167', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =2.2.0\r\nApache  2.0.46<2.0.59\r\nApache  1.3.0<1.3.35', 'Apache是一款开放源代码WEB服务程序。\r\nApache的mod_rewrite模块在转义绝对URI主题时存在单字节缓冲区溢出漏洞，攻击者可能利用此漏洞在服务器上执行任意指令。\r\nmod_rewrite模块的escape_absolute_uri()函数分离LDAP URL中的令牌时，会导致在字符指针数组以外写入指向用户控制数据的指针，这样就可能完全控制受影响的主机。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('168', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'Oracle BEA WebLogic Server = 10.0 MP1\r\nOracle BEA WebLogic Server = 9.2 MP3\r\nOracle BEA WebLogic Server = 9.1\r\nOracle BEA WebLogic Server = 9.0\r\nOracle BEA WebLogic Server = 8.1 SP6\r\nOracle BEA WebLogic Server = 8.1 SP7', 'Oracle Database是一款商业性质大型数据库系统。\r\nBEA Product Suite(10.3, 10.0 MP1, 9.2 MP3, 9.1, 9.0, 8.1 SP6, 7.0 SP7) 服务Apache,SunIIS插件Oracle BEA WebLogic Server 存在未明访问控制安全漏洞。远程认证用户可以通过未明向量影响系统的机密性、完整性和可用性。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('169', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA WebLogic Server = 7.0\r\nBEA WebLogic Server  = 7.0.0.1', 'BEA WebLogic Server以及Express 7.0版本和7.0.0.1版本的默认CredentialMapper将密码以明文形式储存在磁盘中，本地用户可以借助该漏洞提取密码。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('170', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.46 <2.0.60\r\nApache 2.1.* <2.1.9\r\nApache 2.2.* <2.2.8', 'Apache HTTP Server是一款流行的Web服务器。\r\nApache HTTP Server处理畸形用户请求时存在漏洞，远程攻击者可能利用此漏洞获取脚本源码。\r\n如果远程用户提交的畸形HTTP请求承载有以下形式之一负载(如JavaScript)和无效长度数据的话，就会导致Apache HTTP服务器返回客户端所提供的脚本代码：\r\n两个Content-length头等于0，如Content-Length: 0[LF]Content-Length: 0\r\n一个Content-length头等于两个值，如Content-length: 0, 0\r\n一个Content-length:头等于负数，如Content-length: -1\r\n一个Content-length头等于很大的值，如Content-length: 9999999999999999999999999999999999999999999999。\r\n提交了无效长度数据后Apache就会返回413 Request Entity Too Large错误，导致在用户浏览器会话中执行任意HTML和脚本代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('171', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0 <= 2.0.55', 'Mod_SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。\r\n在某些配置情况下Apache的Mod_SSL存在拒绝服务器，远程攻击者可能利用此漏洞导致Apache服务器的拒绝服务。此拒绝服务漏洞是一个可能的空指针废弃问题引起的，当Apache被配置成支持对代码400错误可定制ErrorDocument时可触发此漏洞，远程攻击者可导致Apache进程或线程崩溃，持继性的攻击可以使Apache失去响应。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('172', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'apache mod_auth_shadow 2.0\r\napache mod_auth_shadow 1.4\r\napache mod_auth_shadow 1.3\r\napache mod_auth_shadow 1.2\r\napache mod_auth_shadow 1.1\r\napache mod_auth_shadow 1.0', 'mod-auth-shadow是Apache HTTP Server用于认证/etc/shadow文件的模块。\r\nmod_auth_shadow module 1.0，1.5 到 2.0如果启用了AuthShadow的话，则mod_auth_shadow模块会对所有使用require group指令的位置都使用shadow认证，即使已经指定了其他的认证机制，这个允许远程认证用户绕过安全限制。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('173', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.*<2.0.55', 'Apache是一款开放源代码WEB服务程序。\r\nApache 2的worker模块在处理中止的TCP连接时存在内存泄漏，远程攻击者可以反复触发这种情况耗尽所有可用的内存，最终导致拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('174', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA WebLogic Server and Express = 6.*\r\nBEA WebLogic Server and Express = 7.*', 'BEA WebLogic Server以及Express 6.0到7.0版本没有正确限定某些执行管理函数的内部伺服程序的访问权限，远程攻击者可以利用该漏洞读取任意文件或执行任意代码。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('175', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35<2.0.55\r\nApache 2.1.*<2.1.8\r\nApache 2.0.32 \r\nApache 2.0.28 Beta\r\nApache 2.0.28 \r\nApache 2.0 a9\r\nApache 2.0', 'Apache是一款广泛使用的开放源代码WEB服务程序。\r\nApache 2.x mod_ssl中存在限制绕过漏洞，成功利用这个漏洞的攻击者可以绕过安全策略，无需有效的客户端证书便可访问受限制的资源。如果mod_ssl配置为同SSLVerifyClient指令使用的话，就会出现这个漏洞。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('176', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA Systems Weblogic Server 4.5.1 \r\nBEA Systems Weblogic Server 5.1\r\nBEA Systems Weblogic Server 6.0', '在BEA Systems Weblogic Server 4.5.1、BEA Systems Weblogic Server 5.1和BEA Systems Weblogic Server 6.0中，通过向weblogic server服务器进行请求的URL地址中添加ASC码来获取weblogic server服务器文件夹中的文件信息。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('177', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.0 < 2.0.54', 'Apache 2.0系列中2.0.54之前的版本中的字节范围过滤器允许远程攻击者借助于具有大的范围字段的HTTP报头造成拒绝服务(内存损耗)。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('178', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache mod_auth_kerb 5.2\r\nApache mod_auth_kerb 5.1\r\nApache mod_auth_kerb 5.0', 'Apache是一款开放源代码WEB服务程序。Apache的mod_auth_kerb模块实现上存在堆溢出漏洞，远程攻击者可能利用此漏洞导致模块拒绝服务。在Apache的mod_auth_kerb模块的spnegokrb5/der_get.c文件中，der_get_oid()函数存在单字节溢出漏洞。攻击者可以通过发送特制的Kerberos消息触发堆溢出，导致拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('179', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'apache mod_auth_radius 1.5.7\r\napache mod_auth_radius 1.5.4', 'Apache是一款著名的Web服务器软件。\r\nApache中mod-auth-radius(1.5.4)及libpam-radius-auth模块存在漏洞，可导致拒绝服务。\r\n远程恶意RADIUS服务期可以通过RADIUS attribute长度为1的RADIUS_REPLY_MESSAGE导致出现长度为－1的memcpy操作，从而造成程序崩溃，引发拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('180', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'WebLogic = 5.1.0', 'Bea WebLogic Server 5.1.0版本存在缓冲区溢出漏洞。远程攻击者借助超长的以“..”字符串开始的URL执行任意命令。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('181', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Jakarta =1.4.2', 'Apache Jakarta是一款支持Java的WEB服务程序。\r\n\r\nApache Jakarta Lucene没有正确过滤用户提交的URI输入，远程攻击者可以利用这个漏洞进行跨站脚本攻击，可能泄露敏感信息。\r\n\r\n攻击者可以构建包含恶意HTML和脚本代码的URI链接，诱使用户访问，可导致基于验证的敏感信息泄露。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('182', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.3.*<= 1.3.29', 'Apache是一款广泛使用的开放源代码WEB服务程序。\r\nApache的mod_alias和mod_rewrite模块中存在缓冲区溢出漏洞，可能允许本地攻击者在有漏洞的主机上执行任意代码。\r\n如果正则表达式配置了多于9个捕获括号的话，就可能导致mod_alias和mod_rewrite模块出现缓冲区溢出。攻击者可以通过创建特制的配置文件导致拒绝服务(崩溃)或执行任意代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('183', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.7 .0 BETA1-3\r\nZope Zope 2.6.1 - 2.6.2', '攻击者通过不当的输入验证、访问验证、信息泄漏和多个不正确的安全选项可以对含有漏洞的系统进行攻击。并且可以引发进一步的XSS、DOS等其它攻击。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('184', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 2.0.51', 'Apache是一款开放源代码WEB服务程序。\r\nApache Web Server中存在访问控制绕过漏洞，起因是在合并Satisfy命令时的未明错误。远程攻击者可以利用这个漏洞绕过访问控制，非授权访问受限的资源。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('185', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.* <= 1.3.33', '上述版本中的“ htpasswd”命令行下存在一个本地缓冲区溢出的漏洞。如果“ htpasswd”运行设置了用户标识符或者如果被CGI脚本所包含，一个远程用户远程运行任意命令，它可能被一个本地用户利用来提高权限。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('186', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.3.22 -1.3.29\r\nApache 1.3.17 - 1.3.19\r\nApache 1.3.14\r\nApache 1.3.12\r\nApache 1.3.11\r\nApache 1.3.31\r\nApache 1.3.9 \r\nApache 1.3.7\r\nApache 1.3.6 \r\nApache 1.3.4 \r\nApache 1.3.3 \r\nApache 1.3.1 \r\nApache 1.3.0', 'mod_include是Apache标准模块允许用户在HTML中包含文件执行命令等。\r\nmod_include get_tag()对用户提交输入缺少充分过滤，本地攻击者可以利用这个漏洞进行缓冲区溢出攻击。\r\n问题是get_tag()函数存在缓冲区溢出问题，攻击者从handle_echo()函数可以触发，本地用户可以建立特殊的HTML，当Apache处理时，可能以httpd子进程权限执行任意代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('187', '184', 'web服务器', 'resin 1.2 目录遍历漏洞', ' 低危', '目录遍历', 'Resin Application Server 4.0.36', 'com.caucho.doc.ViewFileServlet 通过 file 参数读取文件，可以造成源码泄露。', 'CVE-2001-0304', 'CNNVD-200105-065', ' ', '2009-12-22');
INSERT INTO `websoc_spider` VALUES ('188', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.0 < 2.0.51', 'Apache是一款开放源代码WEB服务程序。\r\nApache Web Server在处理IPv6通信时存在问题，远程攻击者可以利用这个漏洞对服务程序进行缓冲区溢出攻击。\r\nApache的apr-util库中的IPv5 URI解析函数存在输入验证错误，当使用libc内存拷贝函数时不正确处理字符串长度参数，可触发缓冲区溢出，精心构建提交数据可以在基于BSD UNIX的系统上执行任意指令。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('189', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.0 < 2.0.51', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\nApache Web Server的\'mod_dav\'模块在处理LOCK命令时存在问题，远程攻击者可以利用这个漏洞对服务程序拒绝服务攻击。\r\n当Apache配置使用\'mod_dav\'模块时，接收到验证用户一特殊序列的LOCK命令时会导致Apache进程崩溃。如果Apache配置使用线程进程模型，攻击者可以完全使Apache崩溃，如果配置成使用多进程方式，攻击者可以使独立的WEB服务进程崩溃。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('190', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.6.1\r\nZope Zope 2.5.1\r\n+ Gentoo Linux 1.4 _rc1\r\n+ Gentoo Linux 1.2', '由于对用户输入的的表单数据验证不严，zope的ExampledbBrowseReport示例脚本存在HTML注入漏洞。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('191', '184', 'web服务器', 'resin 1.2 目录遍历漏洞', ' 低危', '目录遍历', 'Caucho Technology Resin 3.1.1\r\nCaucho Technology Resin 3.1\r\nCaucho Technology Resin 3.0.19\r\nCaucho Technology Resin 3.0.18\r\nCaucho Technology Resin 3.0.17\r\nCaucho Technology Resin 3.0.16\r\nCaucho Technology Resin 2.1.12\r\nCaucho Technology Resin 2.1.2\r\nCaucho Technology Resin 2.1.1\r\nCaucho Technology Resin 2.1 .s020711\r\nCaucho Technology Resin 2.0 b2\r\n- Apache Software Foundation Apache 1.3.9\r\n- Microsoft IIS 5.0\r\nCaucho Technology Resin 2.0\r\nCaucho Technology Resin 1.3\r\n- Apache Software Foundation Apache 1.3.9\r\n- Microsoft IIS 5.0\r\nCaucho Technology Resin 1.2.5\r\n- Apache Software Foundation Apache 1.3.9\r\n- Microsoft IIS 5.0\r\nCaucho Technology Resin 1.2.3\r\n- Apache Software Foundation Apache 1.3.9\r\n- Microsoft Windows 2000 Professional\r\nCaucho Technology Resin 1.2.2\r\nCaucho Technology Resin 1.2\r\n- Apache Software Foundation Apache 1.3.9\r\n- Microsoft IIS 5.0\r\nCaucho Technology Resin 1.1', '3.0.25之前的Caucho Resin, 和3.1.4之前的 3.1.x 的viewfile 文件命令中存在跨站脚本攻击漏洞,远程攻击者可以通过文件参数注入任意web脚本或HTML。', 'CVE-2001-0304', 'CNNVD-200105-065', ' ', '2009-12-22');
INSERT INTO `websoc_spider` VALUES ('192', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.*<=2.0.50', 'Apache是一款开放源代码WEB服务程序。\r\nApache在处理配置文件环境变量时存在缓冲区溢出，本地攻击者可以利用这个漏洞以Apache进程权限执行任意指令。\r\n问题存在于当扩展.htaccess或httpd.conf文件中的${ENVVAR}结构时，server/util.c文件中的ap_resolve_env()函数从环境变量拷贝数据到使用strcat(3)的tmp字符数组时缺少正确边界缓冲区检查，可导致缓冲区溢出，精心构建提交数据可能以Apache进程权限执行任意指令。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('193', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.6.1\r\nZope Zope 2.5.1\r\n+ Gentoo Linux 1.4 _rc1\r\n+ Gentoo Linux 1.2', '当传入一个过大的URL参数的值给addItems脚本时，zope服务器会抛出一个异常，其中包含了敏感的系统信息。攻击者可以利用这些信息发起更进一步的攻击。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('194', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA Systems WebLogic Server for Win32 = 7.0 .0.1 SP 1\r\nBEA Systems WebLogic Server for Win32 =  7.0 .0.1\r\nBEA Systems WebLogic Server for Win32 = 7.0 SP 1\r\nBEA Systems WebLogic Server for Win32 = 7.0 \r\n\r\nBEA Systems Weblogic Server = 7.0 .0.1 SP 2\r\nBEA Systems Weblogic Server = 7.0 .0.1 SP 1\r\nBEA Systems Weblogic Server = 7.0 .0.1\r\nBEA Systems Weblogic Server = 7.0 SP 2\r\nBEA Systems Weblogic Server = 7.0 SP 1\r\nBEA Systems Weblogic Server = 7.0 \r\n\r\nBEA Systems WebLogic Express for Win32 = 7.0 .0.1 SP 1\r\nBEA Systems WebLogic Express for Win32 = 7.0 .0.1\r\nBEA Systems WebLogic Express for Win32 = 7.0 SP 1\r\nBEA Systems WebLogic Express for Win32 = 7.0\r\n\r\nBEA Systems WebLogic Express = 7.0 .0.1 SP 2\r\nBEA Systems WebLogic Express = 7.0 .0.1 SP 1\r\nBEA Systems WebLogic Express = 7.0 .0.1\r\nBEA Systems WebLogic Express = 7.0 SP 2\r\nBEA Systems WebLogic Express = 7.0 SP 1\r\nBEA Systems WebLogic Express = 7.0', 'WebLogic此版本存在信息泄露漏洞，有报告称通过精心构造的请求可以获取主机敏感信息。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('195', '184', 'web服务器', 'resin 1.2 目录遍历漏洞', ' 低危', '目录遍历', 'Caucho Technology Resin Professional 3.1 for windows\r\nCaucho Technology Resin 3.1 for windows', 'Windows Caucho Resin Professional 和Caucho Resin 中存在目录遍历漏洞。远程攻击者可以借助一个包含\"\\web-inf\"祖列的URI中的..，读取特定文件。', 'CVE-2001-0304', 'CNNVD-200105-065', ' ', '2009-12-22');
INSERT INTO `websoc_spider` VALUES ('196', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'WebLogic = 7.0\r\n              = 7.0.0.1', 'BEA Systems WebLogic包含多种系统集成方案，包括Server/Express/Integration等。\r\nBEA WebLogic Web应用程序组件实现的会话持续功能存在漏洞，远程攻击者利用这个漏洞未授权访问WebLogic服务器。\r\n当应用程序包含的WEB应用程序组件使用\"memory\"会话持续没有通过重启服务器来重新配置，之前登录进WEB应用程序的用户就不需要验证再次访问，即使用户已经离最后一次访问很长时间也能进行登录。\r\n只有使用WEB应用程序，\"memory\"会话持续和动态重配置(即不重启进行重新配置)的系统才受此漏洞影响。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('197', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'Oracle Weblogic Server 10.3.2\r\nOracle Weblogic Server 10.3.1\r\nOracle Weblogic Server 9.2 MP3\r\nOracle Weblogic Server 9.2 MP2\r\nOracle Weblogic Server 9.2 MP1\r\nOracle Weblogic Server 9.2\r\nOracle Weblogic Server 8.1 SP6\r\nOracle Weblogic Server 8.1 MP6\r\nOracle Weblogic Server 8.1 MP4\r\nOracle Weblogic Server 8.1\r\nOracle Weblogic Server 7.0 SP7\r\nOracle Weblogic Server 7.0 MP5\r\nOracle Weblogic Server 7.0 MP4\r\nOracle Weblogic Server 7.0 MP2\r\nOracle Weblogic Server 7.0\r\nOracle Weblogic Server 10.3\r\nOracle Weblogic Server 10.0 MP2\r\nOracle Weblogic Server 10.0 MP1\r\nOracle Weblogic Server 10', 'Oracle Weblogic中安装可选的节点管理器工具用于远程启动或终止服务器例程。节点管理器运行beasvc.exe进程时，监听于5556端口。节点管理器组件支持多个命令，其中一些命令无需任何认证，用户只需知道Weblogic域的名称便可执行这些命令。默认的Weblogic安装拥有至少两个域：wl_server和medrec。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('198', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.6.1\r\nZope Zope 2.5.1\r\n+ Gentoo Linux 1.4 _rc1\r\n+ Gentoo Linux 1.2', '当用户使用购物车实例脚本的时候，调用了一个无效的查询操作，zope会泄漏路径信息。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('199', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.5.1\r\n+ Gentoo Linux 1.4 _rc1\r\n+ Gentoo Linux 1.2\r\nZope Zope 2.5.0\r\nZope Zope 2.4.4 b1\r\nZope Zope 2.4.0 - 2.4.3\r\nZope Zope 2.3.2 - 2.3.3', '这个漏洞是由于zope没有适当的处理XML-RPC请求。\r\n通过构造特殊的XML - RPC请求可能导致Zope响应的时候泄漏系统错误页面的具体细节。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('200', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.0 <= 2.0.50', 'Mod_SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。\r\nApache Mod_SSL中包含一个输入过滤错误，远程攻击者可以利用这个漏洞对mod_ssl服务进行拒绝服务攻击。\r\n报告者使用第三方工具对使用mod_ssl服务的Apache进行测试，发现在ssl_io_input_getline()函数中存在一个漏洞。Apache在部分状态下没有关闭过滤器的SSL连接，相反是持续读取GETLINE/阻塞模式下的空闲请求。这样会导致ssl_io_input_getline()函数持续循环调用ssl_io_input_read()，造成消耗大量CPU资源，大量此类请求可导致服务停止响应，产生拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('201', '184', 'web服务器', 'resin 1.2 目录遍历漏洞', ' 低危', '目录遍历', 'Caucho Technology Resin 3.0.18 for windows\r\nCaucho Technology Resin 3.0.17 for windows', 'Resin是一款由Caucho Technology开发的WEB服务器，可使用在Microsoft Windows操作系统下。\r\nResin捆绑了自己的单机版Web服务器，默认下运行在8080端口上，其对用户请求的处理上存在漏洞，远程攻击者可以利用此漏洞遍历服务器的目录。\r\n任意用户都可以请求类似于以下的URL：\r\nhttp://victim:8080/C:%5C/\r\n访问C盘根目录及其之下的任意文件。攻击者还可以指定任意盘符。', 'CVE-2001-0304', 'CNNVD-200105-065', ' ', '2009-12-22');
INSERT INTO `websoc_spider` VALUES ('202', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.0<=2.0.50', 'Mod_SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。\r\n在某些条件下处理反向代理SSL实现时存在漏洞，该漏洞源于当使用RewriteRule在运行IIS的服务器上执行反向代理SSL时，在ssl_engine_io.c:348的char_buffer_read函数中可发生段错误。远程攻击者可以利用这个漏洞可能造成服务程序发生缓冲区溢出。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('203', '184', 'web服务器', 'resin 1.2 目录遍历漏洞', ' 低危', '目录遍历', 'Caucho Technology Resin 3.0.18\r\nCaucho Technology Resin 3.0.17', 'Resin是一款由Caucho Technology开发的WEB服务器，可使用在Microsoft Windos操作系统下。\r\nResin的某个CGI程序实现上存在输入验证漏洞，远程攻击者可能利用此漏洞读取Web主目录下的任意文件，包括JSP源码或类文件。\r\n默认下Resin的/webapps目录下/resin-doc中包含有一个扩展war文件。该文档包含有用于在集成的手册中浏览文件的servlet：\r\nhttp://targetsystem/resin-doc/viewfile/?contextpath=%2Fresin-doc%2Fjmx%2Ftutorial%2Fbasic &servletpath=%2Findex.xtp &file=index.jsp &re-marker= &re-start= &re-end=#code-highlight\r\nviewfile servlet可以无需参数在Web主目录中浏览任意文件：\r\nhttp://targetsystem/resin-doc/viewfile/?file=index.jsp\r\n攻击者可以设置resin-doc外的上下文路径，读取其他Web主目录的任意文件：\r\nhttp://targetsystem/resin-doc/viewfile/?contextpath=/otherwebapp &servletpath= &file=WEB-INF/web.xml\r\n如果系统中安装了resin-doc就可能读取Web主目录中的所有文件，包括类文件，然后可以反编译这些文件浏览Java源代码：\r\nhttp://targetsystem/resin-doc/viewfile/?contextpath=/ &servletpath= &file=WEB-INF/classes/com/webapp/app/target.class\r\n请求中错误的路径还会泄漏安装的绝对路径：\r\nFile not found\r\n/C:/customer/sites/deploy/n/wwwroot/WEB-INF/classes/com/webapp/app/non-existant.class', 'CVE-2001-0304', 'CNNVD-200105-065', ' ', '2009-12-22');
INSERT INTO `websoc_spider` VALUES ('204', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.3.2和其之前版本', 'Digital Creations Zope 2.3.2版本及之前版本存在漏洞。本地攻击者可以借助Zclass中对象和方法的Zclass许可绘制的改变来提升额外特权。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('205', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35 <= 2.0.49', 'Apache是一款开放源代码流行的Web服务程序。\r\nApache ap_escape_html内存分配存在问题，远程攻击者可以利用这个漏洞对Apache服务程序进行拒绝服务攻击。\r\n问题存在于server/protocol.c ap_get_mime_headers_core:\r\n------\r\n      if (last_field != NULL) {\r\n            if ((len > 0) && ((*field == \'\\t\') || *field == \' \')) {\r\n...\r\nfold_buf = (char *)apr_palloc(r->pool， alloc_len);\r\n-----\r\n如果头字段以TAB或SPACE开始，Apache就会分配内存给它。\r\n而在ap_escape_html(r->pool， last_field)函数中，last_field字段可以任意长，根据如下代码：\r\n----\r\nint i， j;\r\nfor (i = 0， j = 0; s[i] != \'\\0\'; i++)\r\n        if (s[i] == \'<\' || s[i] == \'>\')\r\n            j += 3;\r\n        else if (s[i] == \'&\')\r\n            j += 4;\r\n    if (j == 0)\r\n        return apr_pstrmemdup(p， s， i);\r\n    x = apr_palloc(p， i + j + 1);\r\n----\r\n(i + j + 1)也可以是任意长度，导致分配任意内存，可消耗大量资源。在linux x86_64上证实发送820MB数据可以溢出(i+j+1)，导致memcpy崩溃而引起拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('206', '184', 'web服务器', 'resin 1.2 目录遍历漏洞', ' 低危', '目录遍历', 'Caucho Technology Resin 1.2', 'CVE(CAN) ID: CAN-2001-0304\r\nCaucho Technology Resin 1.2.2存在一个安全问题。远程用户可以获取http根\r\n目录之外的文件的读取权限。通过构造一个特别的包含\'/..\'或者\'/...\'的URL，\r\n可能导致目录遍历。\r\n此问题只影响在Windows NT/2000系统中安装的Resin.\r\n \r\n<* 来源：joetesta (joetesta@hushmail.com) *>', 'CVE-2001-0304', 'CNNVD-200105-065', ' ', '2009-12-22');
INSERT INTO `websoc_spider` VALUES ('207', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 1.3.31\r\nApache Software Foundation Apache 1.3.29\r\nApache Software Foundation Apache 1.3.28\r\nApache Software Foundation Apache 1.3.26\r\nApache Software Foundation Apache 1.3.25\r\nApache Software Foundation Apache 1.3.27', 'mod_proxy是一个可在Apache使用的代理模块。\r\nmod_proxy在处理负的Content-Length值时存在问题，远程攻击者可以利用这个漏洞进行缓冲区溢出攻击，可能以Apache进程权限在系统上执行任意指令。\r\n连接远程服务器使其返回非法(负值)的Content-Length，可使Mod_proxy触发基于整数的溢出，会导致Apache子进程崩溃。问题存在于proxy_util.c中：\r\nn = ap_bread(f， buf， MIN((int)buf_size，\r\n(int)(len - total_bytes_rcvd)));\r\n这里len可为负值。\r\n在旧的OpenBSD/FreeBSD系统中可利用memcpy的反向拷贝实现来执行任意指令。新的BSD系统中由于攻击者可以控制3个任意字节也能用于执行任意指令。如果define AP_ENABLE_EXCEPTION_HOOK启用的情况下，可能在任何平台都可被利用。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('208', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_ssl <2.8.17', 'Mod_SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。\r\nApache mod_ssl 2.8.17及其以前版本的实现中模块ssl_util.c的‘ssl_util_uuencode_binary’函数存在问题，远程攻击者可以利用使用该函数的ssl_engine_kernel.c模块进行拒绝服务攻击或可能以WEB进程执行任意指令。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('209', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.2.4\r\n+ Debian Linux 2.2 sparc\r\n+ Debian Linux 2.2 powerpc\r\n+ Debian Linux 2.2 arm\r\n+ Debian Linux 2.2 alpha\r\n+ Debian Linux 2.2 68k\r\n+ Debian Linux 2.2\r\n+ MandrakeSoft Corporate Server 1.0.1\r\n+ MandrakeSoft Linux Mandrake 7.2\r\n+ MandrakeSoft Linux Mandrake 7.1\r\n+ RedHat Linux 7.0 sparc\r\n+ RedHat Linux 7.0 i386\r\n+ RedHat Linux 7.0 alpha\r\n+ RedHat Linux 6.2 sparc\r\n+ RedHat Linux 6.2 i386\r\n+ RedHat Linux 6.2 alpha\r\n+ RedHat Linux 6.1 sparc\r\n+ RedHat Linux 6.1 i386\r\n+ RedHat Linux 6.1 alpha\r\nZope Zope 2.2.0 - 2.2.3\r\nRedHat inetd-0.16-4.i386.rpm\r\n+ RedHat Linux 6.2', 'Digital Creations Zope 2.3.1 b1及其早期版本存在漏洞。拥有through-the-web脚本能力本地攻击者 (Zope用户)可以利用该漏洞改变ZClasses类属性。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('210', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.2 beta1\r\nZope Zope 2.1 .x\r\n- Debian Linux 2.2 sparc\r\n- Debian Linux 2.2 powerpc\r\n- Debian Linux 2.2 arm\r\n- Debian Linux 2.2 alpha\r\n- Debian Linux 2.2\r\n+ RedHat PowerTools 6.2\r\n+ RedHat PowerTools 6.1', 'Zope 2.2和更早版本中DocumentTemplate包存在漏洞。远程攻击者利用此漏洞可以在无认证情况下修改 DTMLDocuments或DTMLMethods。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('211', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache', '用户可以查看此目录下所有文件的列表。其中可能包含敏感信息。\r\n该漏洞可能导致敏感信息泄露。\r\nApache 服务器配置成可以显示目录内的文件。\r\n由于目录信息中可能包含一些隐藏的、不应被列出的文件，所以不建议设置列目录。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('212', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 2.0.48\r\nApache Software Foundation Apache 2.0.47\r\nApache Software Foundation Apache 2.0.46\r\nApache Software Foundation Apache 2.0.45\r\nApache Software Foundation Apache 2.0.44\r\nApache Software Foundation Apache 2.0.43\r\nApache Software Foundation Apache 2.0.42\r\nApache Software Foundation Apache 2.0.41\r\nApache Software Foundation Apache 2.0.40\r\nApache Software Foundation Apache 2.0.39\r\nApache Software Foundation Apache 2.0.38\r\nApache Software Foundation Apache 2.0.37\r\nApache Software Foundation Apache 2.0.36\r\nApache Software Foundation Apache 2.0.35\r\nApache Software Foundation Apache 2.0.32\r\nApache Software Foundation Apache 2.0.28\r\nApache Software Foundation Apache 2.0', 'Apache是一款开放源代码WEB服务程序。\r\nApache在错误日志记录过程中缺少充分过滤，远程攻击者可以利用这个漏洞建议任意文件或执行任意脚本代码。\r\nApache WEB服务器由于在记录日志时存在输入验证错误，允许转义字符序列注入到Apache日志文件中，利用这个漏洞可引出多个问题，如任意文件建立或者脚本代码执行。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('213', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35 <= 2.0.48', 'Mod_SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。\r\nmod_ssl存在一个内存泄露问题，远程攻击者可以利用这个漏洞发送标准HTTP请求给SSL端口，可导致远程拒绝服务攻击。\r\n详细漏洞细节目前没有提供。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('214', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'apache mod_access 1.3.*<= 1.3.29', 'mod_access在Apache服务器上的用于访问控制设置，用来为Apache Web服务器提供加密支持。\r\nmod_access存在一个安全问题，远程攻击者可以利用这个漏洞绕过此模块设置的访问规则。\r\n当允许或拒绝的规则被指定，而用于规则中的IP地址没有使用子网掩码，受此漏洞影响的模块将匹配规则错误，结果导致绕过访问限制，访问受限资源。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('215', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0a9\r\nApache 2.0.35<=2.0.48\r\nApache 2.0.28\r\nApache 2.0\r\nApache 1.3.17<=1.3.29\r\nApache 1.3.14\r\nApache 1.3.12\r\nApache 1.3.11\r\nApache 1.3\r\nApache 1.0\r\nApache 0.8.14\r\nApache 0.8.11', 'Apache cygwin是一款可在Windows平台上进行应用的环境。\r\nApache httpd在cygwin环境上对部分请求缺少充分过滤，远程攻击者可以利用这个漏洞进行目录遍历攻击。\r\n提交包含多个对\'../\'进行URI编码的请求给cygwin环境上的Apache httpd，可绕过WEB ROOT限制，以WEB进程权限在系统上查看任意文件内容。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('216', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35<=2.0.48\r\nApache 1.3.22<=1.3.29\r\nApache Software Foundation Apache 2.0a9\r\nApache Software Foundation Apache 2.0.32\r\nApache Software Foundation Apache 2.0.28\r\nApache Software Foundation Apache 2.0\r\nApache Software Foundation Apache 1.3.20\r\nApache Software Foundation Apache 1.3.19\r\nApache Software Foundation Apache 1.3.18\r\nApache Software Foundation Apache 1.3.17\r\nApache Software Foundation Apache 1.3.14\r\nApache Software Foundation Apache 1.3.12\r\nApache Software Foundation Apache 1.3.11\r\nApache Software Foundation Apache 1.3', 'Apache mod_php是用于解析PHP的Apache模块。\r\nApache mod_php在部分设置的情况下存在问题，远程攻击者可以利用这个漏洞获得敏感信息。\r\n如果服务器配置文件\"php.ini\"设置\"register_globals = on\"，及提交一个请求给虚拟主机(虚拟主机包含\"php_admin_flag register_globals off\"设置)，如果下一个请求通过相同apache子进程发送给其他的虚拟主机(此虚拟主机没有任何设置)，那么服务器部分设置就会泄露。\r\n根据服务器和站点配置，攻击者可能获得全局变量信息，如MySQL密码等。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('217', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache < 1.3.29 + ssl < 1.53', 'Apache是一款开放源代码WEB服务程序，其中可通过SSL对通信进行加密。\r\nApache-SSL在部分配置情况下存在问题，远程攻击者可以利用这个漏洞伪造客户端证书进行认证。\r\n如果把SSLVerifyClient设置为1或3(客户端证书可选)及SSLFakeBasicAuth，Apache-SSL 1.3.28+1.52及之前版本允许客户端使用实际BASIC验证来伪造客户端证书。\r\n所有攻击者需要一个合法用户的\"one-line DN\"，用于在Apache-SSL伪造BASIC AUTH，和一个固定的密码(默认是\"password\")。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('218', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 1.3.*<= 1.3.29', 'Apache是一款广泛使用的开放源代码WEB服务程序。\r\nApache的mod_alias和mod_rewrite模块中存在缓冲区溢出漏洞，可能允许本地攻击者在有漏洞的主机上执行任意代码。\r\n如果正则表达式配置了多于9个捕获括号的话，就可能导致mod_alias和mod_rewrite模块出现缓冲区溢出。攻击者可以通过创建特制的配置文件导致拒绝服务(崩溃)或执行任意代码。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('219', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 2.0.40', 'Apache 2.0.40版本中的默认配置存在漏洞。当运行于Red Hat Linux 9.0时，远程攻击者不管在自动索引被关闭和有默认web网页配置的情况下都可以通过含有双斜线(//)的GET请求列出目录的内容。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('220', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35 < 2.0.47\r\nApache 2.0.32 \r\nApache 2.0.28 \r\nApache 2.0 .0\r\nApache =1.3.27', 'Apache是一款使用广泛的开放源代码WEB服务程序。\r\nApache 2的2.0.47之前版本prefork MPM不能正确的处理某些来自accept的错误。如果在很少访问的端口上的accept()返回某些错误消息的话，就可能导致临时的拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('221', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'IIS4/5', 'IIS4、IIS5加载CGI，处理环境块的时候，错误的把“\\\\n”字符用“\\\\x00”替换，导致可以伪造任意环境块。IIS加载CGI的时候，把自己的请求加上“HTTP_”前缀加入环境变量和本地环境变量区分，通过利用”\\\\n”替换成”\\0”的漏洞就可以把这些前缀去掉，从而任意伪造环境块变量。攻击者可以在http头里提交“a=b\\\\nPATH_TRANSLATED:var”使得IIS加载CGI的时候环境块变量成为”HTTP_a=b”和“PATH_TRANSLATED=var”，成功伪造环境块“PATH_TRANSLATED=var”，让php.exe执行脚本文件“var”，从而执行任意命令。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('222', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.34 < 2.0.47\r\nApache 2.0.32\r\nApache 2.0.28 \r\nApache 2.0 a9\r\nApache 2.0.0', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\nApache的FTP代理组件在处理IPV6通信时存在问题，远程攻击者可以利用这个漏洞对FTP代理服务器进行拒绝服务攻击。\r\n当目标主机是IPV6，但代理服务器不能建立IPv6套接口，可导致FTP代理产生拒绝服务攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('223', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'IIS <7.5', 'windows 为兼容老版本的8.3短文件名机制，IIS在解析该类短文件名时响应头错误码存在差异，可以导致暴力破解当前网站目录下所有文件名和子目录名', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('224', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'Microsoft IIS 6.0\r\nMicrosoft IIS 5.1\r\nMicrosoft IIS 5.0', 'Microsoft Internet信息服务(IIS)是Microsoft Windows自带的一个网络信息服务器，其中包含HTTP服务功能 。\r\nIIS的WebDAV功能在解析URI并发送回数据时没有正确地处理Unicode令牌环，远程攻击者可以通过提交恶意HTTP GET请求绕过受口令保护的文件夹的认证，或在受口令保护的WebDAV目录中列出、上传或下载文件 。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('225', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.37 <= 2.0.45', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\nApache 2.0版本的2.0.37到2.0.45在部分条件下存在未明漏洞，远程攻击者可以利用这个漏洞对Apache服务进行拒绝服务攻击。\r\n此漏洞可远程通过mod_dav或其他机制触发，目前没有详细漏洞细节提供。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('226', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 6.0', '在iis6.0里，web管理接口存在漏洞，导致认证绕过。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('227', '76', 'web服务器', 'WebLogic 5.1.0 存在命令执行漏洞', ' 高危', '命令执行', 'BEA WebLogic Server = 10.3\r\nBEA WebLogic Server = 10.0 MP1\r\nBEA WebLogic Server = 9.2 MP3\r\nBEA WebLogic Server = 9.1\r\nBEA WebLogic Server = 9.0\r\nBEA WebLogic Server = 8.1 SP6\r\nBEA WebLogic Server = 7.0 SP7\r\nBEA WebLogic Server = 6.1 SP7', 'Oracle Database是一款商业性质大型数据库系统BEA Product Suite组件\"WebLogic Server Plugins for Apache component\"未明安全漏洞，远程攻击者可以通过未明向量影响机密性、完整性和不可否认性。', 'CVE-2001-0098', 'CNNVD-200102-057', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('228', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_access_referer mod_access_referer 1.0.2', 'Apache Mod_Access_Referer模块可实现基于\"Referer\" HTTP头字段的访问控制。\r\nMod_Access_Referer模块解析非法HTTP Referer头字段时存在问题，远程攻击者可以利用这个漏洞对Apache进行拒绝服务攻击。\r\n在find_allowdeny()函数中，uri_components结构的\"uptr\"由Apache ap_parse_uri_components函数初始化，这个结构\"hostname\"指针回提供给is_ip()函数用于判断由referer字段的值是否为IP地址或主机名。相关代码如下：\r\n        --- -snip snip- ---\r\n         ap_parse_uri_components (r->pool，\r\n                                       ap_table_get\r\n                                       (r->headers_in，\r\n                                       \"Referer\")，\r\n                                        &uptr);\r\n         if (!is_ip (uptr.hostname)) {\r\n         --- -snip snip- ---\r\nstatic int\r\nis_ip (const char *host)\r\n{\r\n        /* this just tests if it matches [\\d.]* */\r\n        /* XX is a better test needed? */\r\n        while ((*host == \'.\') || ap_isdigit (*host))\r\n                host++;\r\n                \r\n        return (*host == \'\\0\');\r\n}\r\n         --- -snip snip- ---\r\n当服务器接收到不正确的referer头字段，ap_parse_uri_components会不初始化uptr.hostname指针，这就导致is_ip()会读取NULL指针而发生段错误，产生服务崩溃。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('229', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.1', 'Microsoft IIS 5.0(Internet Infomation Server 5)是Microsoft Windows 2000自带的一个网络信息服务器，其中包含HTTP服务功能。\r\nMicrosoft IIS在处理重定向错误页面时存在跨站脚本执行问题，远程攻击者可以利用这个漏洞构建恶意页面，诱使用户访问，获得用户基于认证的敏感信息。\r\n此跨站脚本执行攻击影响IIS 4.0，5.0和5.1，IIS在处理错误信息传递给通告用户请求URL将被重定向时，对响应的信息没有进行充分过滤。攻击者可以诱骗用户点击恶意页面，此页面包含恶意脚本代码的链接指向第三方的IIS服务器，导致IIS服务器在返回错误信息给用户时包含恶意代码，这可导致恶意脚本在用户浏览器上执行，使用户基于认证的敏感信息泄露给攻击者。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('230', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.40<=2.0.45', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\nApache 2.0版本的2.0.40到2.0.45的基本验证模块(basic authentication)存在设计缺陷，远程攻击者可以利用这个漏洞对Apache服务进行拒绝服务攻击。\r\n当使用crypt_r或crypt此漏洞由于不正确处理thread_safely，当使用一个线程化(threaded)的MPM(在部分平台下不是默认安装)，允许远程攻击者利用漏洞对服务进行拒绝服务攻击，导致基本验证失败，不能对合法用户名和密码进行验证，需要管理员手工重新启动Apache。目前没有详细漏洞细节提供。\r\n根据报告，此漏洞不能用于未授权用户访问受保护资源。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('231', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', '', 'iisPROTECT是一款设计对WEB目录提供密码保护的程序，类似Apache中的htaccess方法。\r\n在安装和实现iisPROTECT后，用户访问受保护的目录会要求输入正确的用户名和密码。\r\n如果未对iisPROTECT设置访问密码，远程攻击者可以访问受保护文件。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('232', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.35 < 2.0.47\r\nApache 2.0.32\r\nApache 2.0.28\r\nApache 2.0.0', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\n由于部分目录序列重协商存在问题，当SSLCipherSuite指示用于升级ciphersuite时，从不强壮的ciphersuite升级到加强的，会导致这个弱ciphersuite代替加强的那个ciphersuite。\r\n目前没有详细漏洞细节提供。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('233', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.0', 'iissamples/sdk/asp/interaction/ServerVariables_Jscript.asp 文件可以泄露web根目录的物理路径，一个攻击者可以得到服务器的敏感信息。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('234', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.0<=2.0.44', 'Apache是一款流行的开放源代码httpd服务程序。\r\nApache存在未明漏洞，远程攻击者可以利用这个漏洞对服务程序进行拒绝服务攻击。\r\n目前没有详细漏洞细节。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('235', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.0', 'iissamples/sdk/asp/interaction/Form_JScript.asp 没有正确过滤用户的输入，导致跨站脚本漏洞。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('236', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.0.*<2.0.48', 'Apache是一款流行的WEB服务程序。\r\nApache包含的mod_cgid模块在当使用线程的MPM时存在问题，可导致数据重定向泄露敏感信息或不正确授权访问。\r\n当线程的MPM使用时，mod_cgid错误处理CGI重定向路径，会导致把CGI输出错误的定向到客户端，数据错误重定向可泄露敏感信息或不正确授权访问。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('237', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.0', '在iis5.0里，idc扩展可能导致xss攻击.', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('238', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'Microsoft IIS 4.0\r\n　　　　- Microsoft Windows NT 4.0 \r\n　　Microsoft IIS 5.0\r\n　　　　- Microsoft Windows 2000 SP3 \r\n　　　　- Microsoft Windows 2000 Server SP2 \r\n　　　　- Microsoft Windows 2000 Server SP1 \r\n　　Microsoft IIS 5.1\r\n　　　　- Microsoft Windows XP Professional \r\n　　　　- Microsoft Windows XP Home', 'Microsoft IIS(Internet Information Server)是MS Windows系统默认自带的Web服务器软件。\r\n由于Microsoft IIS在处理帮助文件搜索功能时没有很好的检查用户输入，可以导致攻击者进行跨站脚本执行攻击。\r\n当IIS在处理帮助文件搜索功能时，没有对搜索引擎字段数据进行充分检查，攻击者可以在搜索引擎字段中输入包含恶意脚本代码的链接并返回给浏览此连接的客户端，如果客户端信任此WEB站点，包含在链接中的恶意脚本代码就会在用户浏览器上执行，导致跨站脚本攻击。\r\n可能导致攻击获得用户基于Cookie认证的敏感信息。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('239', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.3.0 < 1.3.25\r\nApache 2.0.0 < 2.0.46', 'Apache是一款广泛使用的开放源代码WEB服务程序。\r\nApache对日志中的转义序列处理存在问题，攻击者可能利用恶意的日志信息在服务器执行任意命令。\r\nApache无法过滤错误日志中以ASCII(0x1B)序列开始且带有一系列参数的终端转义序列。如果攻击者能够向Apache错误日志中注入转义序列的话，就可能对远程用户发动各种攻击，包括拒绝服务，文件修改和执行任意命令。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('240', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 2.0.43\r\nApache Software Foundation Apache 2.0.42\r\nApache Software Foundation Apache 2.0.41\r\nApache Software Foundation Apache 2.0.40\r\nApache Software Foundation Apache 2.0.39\r\nApache Software Foundation Apache 2.0.38\r\nApache Software Foundation Apache 2.0.37\r\nApache Software Foundation Apache 2.0.36', 'Apache Web Server是一款开放源代码的HTTP服务程序。\r\nApache WEB服务程序对保留MS-DOS设备名的HTTP GET请求处理不正确，远程攻击者可以利用这个漏洞对Apache WEB服务进行拒绝服务攻击。\r\n这个漏洞只存在于运行在Windows9x版本下的Apache，由于ap_directory_walk函数错误的检查，攻击者请求保留的\"aux\"设备名就可以导致WEB服务崩溃。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('241', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.1', 'Microsoft Internet Information Server (IIS) 5.1版本存在漏洞。远程攻击者借助(1) /_vti_pvt/access.cnf， (2) /_vti_pvt/botinfs.cnf， (3) /_vti_pvt/bots.cnf， or (4) /_vti_pvt/linkinfo.cnf的GET请求查看路径信息。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('242', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 2.0.43\r\nApache Software Foundation Apache 2.0.42\r\nApache Software Foundation Apache 2.0.41\r\nApache Software Foundation Apache 2.0.40\r\nApache Software Foundation Apache 2.0.39\r\nApache Software Foundation Apache 2.0.38\r\nApache Software Foundation Apache 2.0.37\r\nApache Software Foundation Apache 2.0.36', 'Apache Web Server是一款开放源代码的HTTP服务程序。\r\nMicrosoft Windows版本下的Apache WEB服务程序对部分HTTP请求处理不正确，远程攻击者可以利用这个漏洞获得服务器部分文件内容。\r\nWindows平台下的Apache对在HTTP请求最后追加非法字符(如\"<\")缺少正确的处理，攻击者可以请求包含非法字符的文件，而使服务程序返回请求文件的实际内容，造成信息泄露。利用这个漏洞可以帮助攻击者进一步对系统进行攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('243', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Software Foundation Apache 1.3.1 - 1.3.27 for windows and os/2', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\nApache HTTP 1.3.28修补了多个漏洞，这些漏洞包括拒绝服务攻击、文件描述符泄露、日志记录失败等问题。\r\n目前没有提供详细漏洞细节。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('244', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache =2.0.42', 'Apache是一款开放源代码WEB服务程序。\r\nApache中存在信息泄漏漏洞，起因是对CGI脚本执行了不充分的检查。仅在对文件夹启用了WebDAV和CGI的情况下才会出现这个漏洞。\r\n攻击者可以通过向CGI脚本发送POST请求来利用这个漏洞。由于WebDAV和CGI脚本之间不正确的交互，可能导致Web server向远程攻击者返回CGI脚本的内容。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('245', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.0', '在iis5.0里，存在危险的Sample文件，导致泄露服务器的敏感信息。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('246', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.0', 'IIS 4.0 and 5.0存在漏洞，如果虚拟路径映射到UNC共享便不能正确处理ISAPI extension，远程攻击者可以利用这个漏洞读取ASP和其他文件的源码，也称“虚拟UNC共享”漏洞。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('247', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'apache 1.3.17*<1.3.27 \r\nApache 1.3.14 \r\nApache 1.3.12 \r\nApache 1.3.11 \r\nApache 1.3.9 \r\nApache 1.3.6 \r\nApache 1.3.4 \r\nApache 1.3.3 \r\nApache 1.3.1 \r\nApache 1.3', 'Apache Web Server是一款非常流行的开放源码、功能强大的Web服务器程序，由Apache Software Foundation开发和维护。它可以运行在多种操作系统平台下，例如Unix/Linux/BSD系统以及Windows系统。\r\nApache在处理共享内存Scoreboard存在漏洞，本地攻击者可能利用该漏洞以Apache UID的权限执行任意命令，并且可以以root的权限给任意进程发送SIGUSR1信号，杀死进程，产生拒绝服务。\r\n利用Scoreboard覆盖parent[].pid和parent[].last_rtime的共享内存段，可以以管理员权限给任意进程发送信号。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('248', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 4.0', 'IIS 4.0是Microsoft公司的一个广泛应用的Internet服务器产品，运行在Windows平台上。IIS自带了一些ASP例子脚本，这些文件是默认安装的且存在于web目录下。\r\nIIS 4.0自带的某些例子脚本如showcode.asp实现上存在输入验证漏洞，远程攻击者可能利用此漏洞读取Web目录所在驱动器的中任意文件的内容，包括一些ASP脚本的源码。\r\nshowcode.asp被设计用来察看其他样本文件的源代码，但是由于设计失误，没有过滤\"..\"导致可访问上级目录的文件，这样就可能导致Web中的其他asp源文件泄漏甚至Web目录之外的其他文件泄漏。脚本默认的可访问URL为：http://www.sitename.com/msadc/Samples/SELECTOR/showcode.asp 。\r\nViewCode.asp、CodeBrws.asp和Winmsdp.exe存在类似漏洞。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('249', '88', 'web服务器', 'zope 2.2 DTML模板和DTML方法远程修改漏洞', ' 高危', '登录绕过', 'Zope Zope 2.2.4\r\n+ Debian Linux 2.2 sparc\r\n+ Debian Linux 2.2 powerpc\r\n+ Debian Linux 2.2 arm\r\n+ Debian Linux 2.2 alpha\r\n+ Debian Linux 2.2 68k\r\n+ Debian Linux 2.2\r\n+ MandrakeSoft Corporate Server 1.0.1\r\n+ MandrakeSoft Linux Mandrake 7.2\r\n+ MandrakeSoft Linux Mandrake 7.1\r\n+ RedHat Linux 7.0 sparc\r\n+ RedHat Linux 7.0 i386\r\n+ RedHat Linux 7.0 alpha\r\n+ RedHat Linux 6.2 sparc\r\n+ RedHat Linux 6.2 i386\r\n+ RedHat Linux 6.2 alpha\r\n+ RedHat Linux 6.1 sparc\r\n+ RedHat Linux 6.1 i386\r\n+ RedHat Linux 6.1 alpha\r\nZope Zope 2.2.0 - 2.2.3', 'Zope 2.2.0到2.2.4版本不能正确执行保护Image和File对象的数据更新类函数。有DTML编辑特权的攻击者修改这些对象的原始数据。', 'CVE-2000-0483', 'CNNVD-200006-063', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('250', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 4.0', '带有IIS 4.0版本的Microsoft Site Server 2.0版本存在漏洞。允许用户向目标网站上传包括ASP的内容，从而允许他们远程地执行命令。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('251', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 1.3.*< 1.3.27', 'Apache Web Server是一款非常流行的开放源码、功能强大的Web服务器程序，由Apache Software Foundation开发和维护。它可以运行在多种操作系统平台下，例如Unix/Linux/BSD系统以及Windows系统。\r\nApache在处理共享内存Scoreboard存在漏洞，本地攻击者可能利用该漏洞以Apache UID的权限执行任意命令，并且可以以root的权限给任意进程发送SIGUSR1信号，杀死进程，产生拒绝服务。\r\n利用Scoreboard覆盖parent[].pid和parent[].last_rtime的共享内存段，可以以管理员权限给任意进程发送信号。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('252', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group Apache 2.0 < 2.0.43\r\nApache Group Apache 1.3.x <= 1.3.26', 'Apache是一款广泛使用的开放源代码WEB服务程序。\r\nApache的对默认错误页面的处理存在问题，攻击者可以利用此漏洞执行跨站脚本攻击。\r\n起因是没有正确的过滤SSI错误页面的恶意HTML代码。攻击者可以利用这个漏洞在访问恶意链接的客户端执行HTML和脚本代码，导致控制Web内容或窃取基于cookie的认证凭证。\r\n请注意仅在UseCanonicalName设置为Off且服务器运行在使用了通配符DNS的域中的情况下才可能利用这个漏洞。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('253', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group Apache 2.0.39\r\nApache Group Apache 2.0.36\r\nApache Group Apache 2.0.35\r\nApache Group Apache 2.0.34-BETA win32\r\nApache Group Apache 2.0.32-BETA win32\r\nApache Group Apache 2.0.32\r\nApache Group Apache 2.0.28-BETA win32\r\nApache Group Apache 2.0.28-BETA\r\nApache Group Apache 2.0.28\r\nApache Group Apache 2.0', 'Apache是一款广泛流行的开放源代码的WEB服务程序。\r\nApache 2.0.40以前的for windows版本中存在一个泄漏物理路径漏洞，远程攻击者可以利用这个漏洞获得服务器物理路径信息。\r\n当客户端请求一个.var后缀的不存在的文件时，Apache 2.0.x for Windows会返回一些出错信息。这些信息中包含Apache的物理路径，这可能允许攻击者了解服务器的配置情况，可能有助于发动进一步攻击。\r\nUnix和其他平台的Apache Web服务程序不受此漏洞影响，Cygwin用户受到此漏洞影响。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('254', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 5.0', 'IIS中存在漏洞，攻击者利用该漏洞通过使用将借助Perl (perl.exe)解释的不存在的URL请求确定一个真正的路径。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('255', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'IIS', 'IIS开启WebDAV后可以使用PUT方法向服务器写文件，并可以通过MOVE方法移动文件，攻击者可以利用此漏洞，在服务器上创建任意文件。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('256', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Tomcat 3.0 <= 3.3.1 \r\nApache Tomcat 4.0 <= 4.1', 'Apache Tomcat是一款免费的用于JAVA Servlet和Javaserver页技术的Servlet实现。\r\nApache Tomcat包含的两个样例类存在漏洞，可导致系统敏感信息泄露。\r\n攻击者可以向服务器请求Tomcat包含的两个样例类(SnoopServlet 和 TroubleShooter)，可导致服务程序返回包含Tomcat安装路径的信息给攻击者。\r\n攻击者可以根据这些敏感信息对系统进一步进行攻击。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('257', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以在服务器中执行任意命令，获取服务器权限。', 'Oracle Fusion Middleware（Oracle融合中间件）是美国甲骨文（Oracle）公司的一套面向企业和云环境的业务创新平台。该平台提供了中间件、软件集合等功能。Oracle WebLogic Server是其中的一个适用于云环境和传统环境的应用服务器组件。\nOracle Fusion Middleware中的Oracle WebLogic Server组件的Core Components子组件存在安全漏洞。攻击者可利用该漏洞控制组件，影响数据的保密性，完整性及可用性。以下版本受到影响：Oracle WebLogic Server 10.3.6.0版本，12.1.3.0版本，12.2.1.2版本，12.2.1.3版本。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('258', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache < 1.3.24\r\nApache 2.0.* < 2.0.34', 'Apache是使用最广泛的开放源码的Web服务器程序，分Unix和Windows两种发行版本。\r\nWindows版本的Apache在处理批处理文件的Web请求没有过滤一些特殊字符(比如\'|\')，远程攻击者可以利用这个漏洞在目标主机执行任意命令。\r\nApache在Windows操作系统下一般都是以SYSTEM权限运行，所以会造成很大的危害。\r\n2.0.x系列的Windows版Apache默认安装都自带了一个test的批处理文件，这个文件可以利用来执行命令。其它任意可以通过Web访问的批处理文件都可以利用。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('259', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以利用该漏洞在服务器中执行任意代码。', 'Drupal是Drupal社区所维护的一套用PHP语言开发的免费、开源的内容管理系统。\nDrupal中带有默认或通用模块配置的多个子系统存在安全漏洞。远程攻击者可利用该漏洞执行任意代码。以下版本受到影响：Drupal 7.58之前版本，8.3.9之前的8.x版本，8.4.6之前的8.4.x版本，8.5.1之前的8.5.x版本。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('260', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'mod_ssl before 2.8.7-1.3.23\r\nApache-SSL before 1.3.22+1.46', 'Mod_SSL和Apache-SSL是Apache服务器上的SSL实现，用来为Apache Web服务器提供加密支持。这个模块利用OpenSSL来完成SSL实现。\r\n版本低于2.8.7-1.3.23的Mod_SSL和版本低于1.3.22+1.47的Apache-SSL实现上以一种不安全方式使用OpenSSL函数，在某些条件下，可能导致缓冲区溢出，远程攻击者可能对服务器程序实施拒绝服务攻击或在主机上执行任意指令。\r\n在启用SSL会话缓存后，mod_ssl会对SSL会话变量进行排序和存储，以便日后使用。Mod_SSL在实现\'shm\'或\'dbm\'会话缓存机制时调用了OpenSSL的i2d_SSL_SESSION函数，OpenSSL要求在调用该函数时必须为其分配足够大小的内存以保存数据。但是由于Mod_SSL没有按照正确的方式进行调用，Mod_SSL在处理连续会话时可能导致一个静态缓冲区发生溢出。\r\n要利用这个漏洞，攻击者必须想办法增加代表会话的数据的长度，这就要通过在客户端指定超大的证书实现。这个漏洞需要服务器打开对客户端证书的认证并且客户端证书是经由一个Web服务器程序信任的CA的认证的情况下才能被利用。尽管漏洞难以被利用，我们仍然建议管理员尽快升级以避免潜在的危险。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('261', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以在服务器中执行恶意代码。', 'Apache Struts2的 REST 插件存在远程代码执行的高危漏洞，当启用 Struts REST 的XStream handler 去反序列化处理一个没有经过任何类型过滤的\nXStream 的实例，可能导致在处理 XML 时造成远程代码执行漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('262', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache >= 1.2.2\r\nApache 1.3 <= 1.3.24\r\nApache 2.0 <= 2.0.36', 'Apache Web Server是一款非常流行的开放源码、功能强大的Web服务器程序，由Apache Software Foundation开发和维护。它可以运行在多种操作系统平台下，例如Unix/Linux/BSD系统以及Windows系统。\r\nApache在处理以分块(chunked)方式传输数据的HTTP请求时存在设计漏洞，远程攻击者可能利用此漏洞在某些Apache服务器上以Web服务器进程的权限执行任意指令或进行拒绝服务攻击。\r\n分块编码(chunked encoding)传输方式是HTTP 1.1协议中定义的Web用户向服务器提交数据的一种方法，当服务器收到chunked编码方式的数据时会分配一个缓冲区存放之，如果提交的数据大小未知，客户端会以一个协商好的分块大小向服务器提交数据。\r\nApache服务器缺省也提供了对分块编码(chunked encoding)支持。Apache使用了一个有符号变量储存分块长度，同时分配了一个固定大小的堆栈缓冲区来储存分块数据。出于安全考虑，在将分块数据拷贝到缓冲区之前，Apache会对分块长度进行检查，如果分块长度大于缓冲区长度，Apache将最多只拷贝缓冲区长度的数据，否则，则根据分块长度进行数据拷贝。然而在进行上述检查时，没有将分块长度转换为无符号型进行比较，因此，如果攻击者将分块长度设置成一个负值，就会绕过上述安全检查，Apache会将一个超长(至少>0x80000000字节)的分块数据拷贝到缓冲区中，这会造成一个缓冲区溢出。\r\n对于1.3到1.3.24(含1.3.24)版本的Apache，现在已经证实在Win32系统下， 远程攻击者可能利用这一漏洞执行任意代码。在UNIX系统下，也已经证实至少在OpenBSD系统下可以利用这一漏洞执行代码。据报告称下列系统也可以成功的利用：\r\n *      Sun Solaris 6-8 (sparc/x86)\r\n *      FreeBSD 4.3-4.5 (x86)\r\n *      OpenBSD 2.6-3.1 (x86)\r\n *      Linux (GNU) 2.4 (x86)\r\n对于Apache 2.0到2.0.36(含2.0.36)，尽管存在同样的问题代码，但它会检测错误出现的条件并使子进程退出。\r\n根据不同因素，包括受影响系统支持的线程模式的影响，本漏洞可导致各种操作系统下运行的Apache Web服务器拒绝服务。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('263', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'AuthPG 1.2b2(also known as mod_auth_pg) \r\nmod_auth_mysql 1.9\r\nmod_auth_oracle 0.5.1\r\nmod_auth_pgsql 0.9.5\r\nmod_auth_pgsql_sys 0.9.4', 'PostgreSQL认证模式(1)mod_auth_pgsql 0.9.5，(2)mod_auth_pgsql_sys 0.9.4存在漏洞。远程攻击者可以借助用户名上的SQL注入攻击绕过认证并执行任意SQL。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('264', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以获取到网站的登录口令及安全码，可利用登录口令和安全码获取到网站的管理权限。', 'OURPHP（傲派建站系统）是一款使用PHP语言开发的网站内容管理系统，开发商为哈尔滨伟成科技有限公司。其编辑器文件中存在一个信息泄露漏洞，会泄露 OURPHP 的管理员后台登录口令及安全码。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('265', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group Apache 1.3.20\r\nApache Group Apache 1.3.19\r\nApache Group Apache 1.3.18\r\nApache Group Apache 1.3.17\r\nApache Group Apache 1.3.14\r\nApache Group Apache 1.3.11', 'CVE(CAN) ID: CVE-2001-0731\r\nApache的AutoIndex(自动索引)模块会自动对目录进行索引。\r\n如果该目录下存在index.html文件，那么自动索引将显示index.html的内容。\r\n然而，这个模块存在一个可能的问题，如果提交某些特殊命令，可能会泄漏目录\r\n列表，不管index.html文件是否存在。\r\n问题出在/src/modules/standard/mod_autoindex.c ：\r\n#define K_NAME \'N\'              /* Sort by file name (default) */\r\n#define K_LAST_MOD \'M\'          /* Last modification date */\r\n#define K_SIZE \'S\'              /* Size (absolute， not as displayed) */\r\n#define K_DESC \'D\'              /* Description */\r\n#define D_ASCENDING \'A\'\r\n#define D_DESCENDING \'D\'', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('266', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以在服务器中执行恶意代码。', '当 Tomcat运行在Windows操作系统时，且启用了HTTP PUT请求方法（例如，将 readonly 初始化参数由默认值设置为 false），攻击者将有可能通过精心构造的攻击请求数据包向服务器上传包含任意代码的 JSP 文件，JSP文件中的恶意代码将能被服务器执行。导致服务器上的数据泄露或获取服务器权限。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('267', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache Group Apache 1.3.9 win32\r\nApache Group Apache 1.3.6 win32\r\nApache Group Apache 1.3.20 win32\r\nApache Group Apache 1.3.12 win32\r\nApache Group Apache 1.3.11 win32\r\nIBM HTTP Server 1.3.6.2 win32\r\nIBM HTTP Server 1.3.3 win32', 'Apache是一款广泛使用的开放源代码WEB服务程序。\r\nApache Web Server 1.3.x(Win32)对于请求路径的处理上存在漏洞，远程攻击者利用此漏洞通过请求包含大量的‘/’字符串的URL获取目录列表信息。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('268', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用SQL注入漏洞脱库，篡改网站，获取数据库甚至是Web服务器的控制权限。', 'MetInfo采用PHP+Mysql架构是一款适合企业、公司网站制作的cms建站系统。 MetInfo小于等于5.3.17版本中存在一个SQL注入漏洞，允许远程攻击者利用漏洞提交特制的SQL查询，可操作或获取数据库数据，或获取WEB SHELL。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('269', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以利用该漏洞上传可执行文件，直接得到服务器权限。', 'OURPHP是一款基于PHP+MySQL开发符合W3C标准的建站系统。 OURPHP在小于1.7.3的版本中前台存在一个任意文件上传漏洞。由于\'ourphp_filebox.php\'未能对访客提交的数据进行有效的检验从而导致任意文件上传漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('270', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '恶意用户可以在服务器上执行任意 OGNL语法的Java 代码。', 'Apache Struts2 2.3.x版本中存在S2-048远程代码执行漏洞。该漏洞存在Struts2和Struts1一个Showcase插件Action Message类中，攻击者可通过构建不可信的输入实现远程命令攻击。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('271', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可利用漏洞上传任意文件，获得服务器控制权。', 'NiuShop开源商城系统是由山西牛酷信息科技有限公司自主设计、研发的一套PHP的开源电商系统。 NIiuShop开源商城系统存在文件上传漏洞。攻击者可通过本地构造一个上传用的HTML，利用漏洞上传任意文件，获得服务器控制权。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('272', '220', 'web服务器', 'apache 1.3.20 目录遍历漏洞', ' 低危', '目录遍历', 'Apache 2.*< 2.0.46 on OS/2', 'Apache HTTP Server是美国阿帕奇（Apache）软件基金会的一款开源网页服务器。该服务器具有快速、可靠且可通过简单的API进行扩充的特点。\r\nOS2平台下的Apache HTTP服务器在处理部分设备名请求时存在问题，远程攻击者可以利用这个漏洞对pache HTTP服务器进行拒绝服务攻击。\r\n问题存在与\'srclib/apr/file_io/os2/filestat.c\'文件中，攻击者请求特殊的设备名，可导致OS2 worker进程崩溃，停止对正常请求的响应。', 'CVE-2000-0505', 'CNNVD-200005-109', ' ', '2009-12-23');
INSERT INTO `websoc_spider` VALUES ('273', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache  4.1.x<4.1.38 5.5.x<5.5.27 6.0.x<6.0.17', 'Apache Tomcat是一个流行的开放源码的JSP应用服务器程序。\r\nTomacat4.1.0至4.1.37，5.5.0至5.5.26，6.0.0至6.0.16中均存在目录遍历漏洞。当启用allowLinking及UTF-8时，Tomcat在处理请求的编码时存在漏洞，远程攻击者可通过提交包含经编码的目录路径，读取服务器上的任意文件。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('274', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 7.0.0-7.0.21\r\nTomcat 6.0.30-6.0.33', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\nApache Tomcat 6.0.30到6.0.33中存在安全漏洞，恶意攻击者可利用该漏洞绕过安全限制。该漏洞源于当发生某些错误时需要添加到访问日志中，访问日志进程会在其回收之后触发请求对象重填(re-population)。但是在用于下一个请求之前没有回收请求对象，可导致把前一个请求中的远程IP地址，HTTP头等敏感信息泄露到下一个请求中去。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('275', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以上传可执行文件到服务器中，可获取服务器权限。', '欧虎政务系统的\'uploadPage.php\' 文件对上传的文件类型没有进行过滤，导致攻击者可以上传任意文件。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('276', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用该漏洞在服务器中执行任意代码。', 'FineCMS企业网站管理系统（简称免费版或企业版）是一款基于PHP+MySql+CI框架开发的高效简洁的中小型内容管理系统，面向多终端包括PC端网页和移动端网页，支持自定义内容模型和会员模型，并且可以自定义字段，可面向中小型站点提供重量级网站建设解决方案，适用于小型站点、企业级网站、新闻内容网站等。FineCMS的\'Template.php\'中存在代码执行漏洞，其sys-key在cookie中泄露了，可以通过sys-key绕过api的认证从而进入到\'Template.php\'中，调用存在漏洞的函数进而代码执行。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('277', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 7.0.0 ~ 7.0.20的所有版本\r\nTomcat 6.0.0 ~ 6.0.33的所有版本\r\nTomcat 5.5.0 ~ 5.5.33的所有版本\r\n早期的已不再提供支持的版本也可能受影响', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\nTomcat在AJP协议的实现上存在安全限制绕过漏洞。此漏洞源于Apache Tomcat错误处理了某些请求，可被利用注入任意AJP消息并泄露敏感信息或绕过身份验证机制。成功利用需要不使用org.apache.jk.server.JkCoyoteHandler AJP连接器，接受POST请求，不处理请求主体。远程攻击者可利用此漏洞绕过某些安全限制。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('278', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用SQL注入漏洞脱库，篡改网站，获取数据库甚至是Web服务器的控制权限。', 'FineCMS企业网站管理系统（简称免费版或企业版）是一款基于PHP+MySql+CI框架开发的高效简洁的中小型内容管理系统，面向多终端包括PC端网页和移动端网页，支持自定义内容模型和会员模型，并且可以自定义字段，可面向中小型站点提供重量级网站建设解决方案，适用于小型站点、企业级网站、新闻内容网站等。FineCMS的\'Template.php\'中存在SQL注入漏洞，其sys-key在cookie中泄露了，可以通过sys-key绕过api的认证从而进入到\'Template.php\'中，调用存在漏洞的函数从而导致SQL注入漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('279', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 7.0.x<7.0.12', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\nApache Tomcat 7.0.12之前的7.0.x版本中的HTTP BIO连接器没有正确处理HTTP流水线。远程攻击者可以通过检查HTTP包中的应用数据，为随机环境中的其他客户端读取响应。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('280', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', '', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\n当web.xml不存在安全限制时，Apache Tomcat 7.0.11之前的7.x版本没有遵循ServletSecurity注释。远程攻击者可以借助对web应用程序的HTTP请求，绕过预设的访问限制。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('281', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用该漏洞获取用户的所有信息，包括用户名，密码等。', 'FineCMS企业网站管理系统（简称免费版或企业版）是一款基于PHP+MySql+CI框架开发的高效简洁的中小型内容管理系统，面向多终端包括PC端网页和移动端网页，支持自定义内容模型和会员模型，并且可以自定义字段，可面向中小型站点提供重量级网站建设解决方案，适用于小型站点、企业级网站、新闻内容网站等。FineCMS的\'controllers/Api.php\' 中存在权限绕过漏洞，可以获取到任意用户的所有信息。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('282', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以上传可执行文件到服务器中，可获取服务器权限。', 'FineCMS企业网站管理系统（简称免费版或企业版）是一款基于PHP+MySql+CI框架开发的高效简洁的中小型内容管理系统，面向多终端包括PC端网页和移动端网页，支持自定义内容模型和会员模型，并且可以自定义字段，可面向中小型站点提供重量级网站建设解决方案，适用于小型站点、企业级网站、新闻内容网站等。finecms某处过滤不严格，导致可上传任意脚本文件。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('283', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 7.0.x<=7.0.3, 6.0.x,5.5.x', 'Apache Tomcat 是一款由Apache Foundation维护的免费开放源代码的Java Servlet和JSP服务程序。\r\n当在SecurityManager中运行时，Apache Tomcat 7.0.0至7.0.3版本，6.0.x，以及5.5.x版本没有将ServletContext属性设为只读。本地web应用程序可以利用该漏洞读或写预设工作目录外的文件。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('284', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以上传可执行文件到服务器中，可获取服务器权限。', 'FineCMS企业网站管理系统（简称免费版或企业版）是一款基于PHP+MySql+CI框架开发的高效简洁的中小型内容管理系统，面向多终端包括PC端网页和移动端网页，支持自定义内容模型和会员模型，并且可以自定义字段，可面向中小型站点提供重量级网站建设解决方案，适用于小型站点、企业级网站、新闻内容网站等。finecms某处过滤不严格，导致可上传任意脚本文件。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('285', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 6.0.12<=x<=6.0.29,7.0.x<=7.0.4', 'Apache Tomcat是一款开放源码的JSP应用服务器程序。\r\n由于受影响的会话列表屏幕(由sessionList.jsp脚本提供)没有采用任何过滤使用orderBy和sort请求，因此可造成跨站脚本攻击。用户必须注意Tomcat 6默认没有把会话COOKIE设置httpOnly，否则攻击者可利用此漏洞获得会话COOKIE。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('286', '1006', 'web服务器', 'IIS PUT 任意文件创建漏洞', ' 高危', '任意文件创建', 'iis 4.0', '微软IIS是Windows平台上的一种WEB Server。\r\nIIS 4.0有一个远程可访问目录/IISADMPWD，包含了一些.HTR文件。这些文件最初设计用于为系统管理员提供基于HTTP的网络用户口令修改机制。提交对.HTR文件的请求，将返回一个表单，让用户输入帐号名、当前口令、准备使用的新口令。\r\n据报告，即使一个帐号的本地安全策略中设置了\"用户不能修改口令\"，仍有可能通过IISADMPWD目录下的.HTR程序修改自己的口令。对于被禁用的帐号，此问题同样存在。', ' ', ' ', ' ', '2013-07-10');
INSERT INTO `websoc_spider` VALUES ('287', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '未经授权的攻击者利用该漏洞就能实现远程代码执行。', 'PHPMailer是一个用于发送电子邮件的PHP类库。\r\nPHPMailer 5.2.18之前的版本中的isMail transport的‘mailSend’函数存在安全漏洞，该漏洞源于程序没有设置Sender属性。远程攻击者可利用该漏洞向邮件命令中传递额外的参数，并执行任意代码。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('288', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 6.x', 'Apache Tomcat是一款开放源码的JSP应用服务器程序。\r\nApache Tomcat 6.x版本的默认配置没有包含Set-Cookie头中的HTTPOnly标记。导致远程攻击者可以借助访问cookie的脚本劫持会话。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('289', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用SQL注入漏洞脱库，篡改网站，获取数据库甚至是Web服务器的控制权限。', 'Joomla!是美国Open Source Matters团队的一套使用PHP和MySQL开发的开源、跨平台的内容管理系统(CMS)。\r\nJoomla! 3.7.1之前的3.7.x版本中存在SQL注入漏洞。远程攻击者可利用该漏洞执行任意SQL命令。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('290', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 5.5.x<=5.5.29, 6.0.x<=6.0.27, 7.0.0 beta', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\nApache Tomcat 5.5.0版本 到 5.5.29版本, 6.0.0版本 到6.0.27版本, 以及 7.0.0 beta版本没有正确的处理无效Transfer-Encoding头，远程攻击者可以借助一个与\"缓冲区干扰\"相关的畸形头文件导致拒绝服务(应用中断)，也可能获取敏感信息。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('291', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以添加任意网站管理员。', '国微CMS为国内最主流的CMS系统之一,也是中国南方PHP领域最大的开源平台提供商。国微政府网站系统注册页面 name参数过滤不严导致任意管理员添加漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('292', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 5.5.x<=5.5.29,6.0.x<=6.0.26', 'Apache Tomcat是一款由Apache Foundation维护的免费开放源代码的Web服务器程序。\r\nBASIC和DIGEST认证的WWW-Authenticate头包含有区域名。如果web.xml中对应用指定了<realm-name>元素，就会使用指定的区域名，否则Tomcat就会使用以下代码段生成一个区域名：request.getServerName() + \":\" + request.getServerPort() 在某些环境中这可能会暴露运行Tomcat机器的本地主机名或IP地址。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('293', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '恶意用户可能会在 JavaScript、 VBScript、 ActiveX、 HTML 或 Flash 中注入成一个易受攻击的应用程序来欺骗用户，以便从中收集数据。', '国微CMS为国内最主流的CMS系统之一,也是中国南方PHP领域最大的开源平台提供商。国微政府网站系统中\'image_cut.php\' url参数过滤不严导致跨站脚本漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('294', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 7.0.11', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\n当web.xml没有设置登录配置时，Apache Tomcat 7.0.11版本没有遵循安全约束。远程攻击者可以借助对元数据完整的web应用程序的HTTP请求，绕过预设的访问限制。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('295', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 7.0.x<7.0.10', 'Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\nApache Tomcat 7.0.10之前的7.x版本中存在安全限制绕过漏洞。由于应用程序在加载小服务程序时未能正确执行\"@ServletSecurity\"注释，远程攻击者可以借助对web应用程序的HTTP请求，绕过预设的访问限制。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('296', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用SQL注入漏洞脱库，篡改网站，获取数据库甚至是Web服务器的控制权限。', '国微CMS为国内最主流的CMS系统之一,也是中国南方PHP领域最大的开源平台提供商。国微政府网站系统中\'list_ask.php\' sid参数过滤不严导致SQL注入漏洞。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('297', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 7.0.x<7.0.6 \r\nApache Tomcat 5.5.x<5.5.32\r\nApache Tomcat 6.0.x<6.0.30', 'Apache Software Foundation Tomcat 是一款开放源码的JSP应用服务器程序。\r\nApache Software Foundation Tomcat 7.0.6之前的7.0版本，5.5.32之前的5.5版本，以及6.0.30之前的6.0版本中存在多个跨站脚本攻击漏洞。远程攻击者可以利用这些漏洞注入任意web脚本或者HTML。', ' ', ' ', ' ', '2009-12-17');
