INSERT INTO `websoc_spider` VALUES ('354', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以获取到一份完整的导出的配置文件。', 'Drupal 8 system.temporary route 允许攻击者未授权下载导出的配置文件，导出的配置文件需授权访问。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('355', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 4.0.3', '构造一个到DOS设别名称的请求可能使得Tomcat抛出一个异常，从而导致XSS攻击。这个异常也会显示Tomcat的安装的物理路径。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('356', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 3.2.3 - 3.2.4', 'Apache Tomcat是一款由Apache Foundation维护的免费开放源代码的Web服务器程序。\r\nApache Tomcat中的realPath.jsp对用户提交的请求处理存在漏洞，可导致远程攻击者获得系统路径相关的信息。\r\nApache Tomcat安装后会提供在Web目录下建立test目录，并提供几个样例脚本，其中包括Source.JSP，攻击者可以直接请求Source.JSP，导致脚本返回Web目录安装路径信息。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('357', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Software Foundation Tomcat 4.0.3', 'Apache Tomcat 4.0.3版本存在跨站脚本漏洞。远程攻击者可以像其他web用户借助带/servlet/绘图的URL脚本执行脚本，该漏洞在控制器抛出异常脚本时不过滤脚本。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('358', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用SQL注入漏洞脱库，篡改网站，获取数据库甚至是Web服务器的控制权限。', 'Destoon基于PHP+MySQL的开源B2B(电子商务)行业门户的解决方案。Destoon的\'spread/index.php\'中存在一个SQL注入漏洞，允许远程攻击者利用漏洞提交SQL语句并执行，可获取或操作数据库数据。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('359', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 3.0\r\nTomcat 3.1', 'Jakarta Tomcat的默认配置不限制/admin文本的访问权限，远程攻击者通过直接的调用管理servlets使得在根目录中添加一个文本从而读取任意文件。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('360', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 3.0 \r\nTomcat 3.1', 'Apache Apache Tomcat是美国阿帕奇（Apache）软件基金会下属的Jakarta项目的一款轻量级Web应用服务器，它主要用于开发和调试JSP程序，适用于中小型系统。\r\nTomcat的snoop servlet组件存在一个安全问题，可能会泄漏服务器的敏感信息。\r\n向Tomcat请求一个不存在的以 .snp 为扩展名的文件就会从服务器返回很多对入侵者很有用的信息，包括Web绝对路径，操作系统类型等。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('361', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', 'Zabbix jsrpc.php在启用Guest权限后，可不用登录存在SQL注入漏洞, 可以获取管理员的Session登录Zabbix系统。', 'zabbix是一个基于WEB界面的提供分布式系统监视以及网络监视功能的企业级的开源解决方案。zabbix能监视各种网络参数，保证服务器系统的安全运营；并提供灵活的通知机制以让系统管理员快速定位/解决存在的各种问题。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('362', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', 'zabbix弱密码, 登录后可以在后台自定义脚本，执行命令。', 'zabbix是一个基于WEB界面的提供分布式系统监视以及网络监视功能的企业级的开源解决方案。zabbix能监视各种网络参数，保证服务器系统的安全运营；并提供灵活的通知机制以让系统管理员快速定位/解决存在的各种问题。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('363', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat 3.1', 'Apache Group Tomcat是一个免费开放源码的JSP服务器程序。\r\nApache Group Tomcat 3.1在处理不存在的JSP文件请求时存在问题，远程攻击者可能借此收集到服务器相关的配置信息。\r\n当向Apache Group Tomcat 3.1提交请求，访问一个不存在的JSP文件时，服务器返回太多敏感信息，包括WEB服务物理路径。这些信息潜在损害着系统安全性，有可能被攻击者利用。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('364', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '可登陆后台，造成敏感信息泄漏。', 'Django是一个开放源代码的Web应用框架，由Python写成。采用了MVC的框架模式，即模型M，视图V和控制器C。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('365', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Tomcat', '请求Tomcat URL /server-status将获得远程服务器正在运行实例的信息，同时允许任何人重置当前统计信息。远程攻击者可使用这些信息进行进一步攻击。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('366', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者可以在服务器中执行任意命令，获取服务器权限。', 'Oracle Fusion Middleware（Oracle融合中间件）是美国甲骨文（Oracle）公司的一套面向企业和云环境的业务创新平台。该平台提供了中间件、软件集合等功能。Oracle WebLogic Server是其中的一个适用于云环境和传统环境的应用服务器组件。\nOracle Fusion Middleware 10.3.6.0、12.1.3.0及12.2.1.0版本中的Oracle WebLogic Server组件中的WLS Core Components子组件存在安全漏洞。远程攻击者可利用该漏洞控制应用程序，影响数据的保密性、完整性及可用性。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('367', '63', 'web服务器', 'Tomcat server-status信息泄漏', ' 低危', '信息泄漏', 'Apache Tomcat 6.0.0 <= 6.0.16\r\nApache Tomcat 5.5.0 <= 5.5.26', 'Apache Tomcat是一个流行的开放源码的JSP应用服务器程序。\r\nApache Tomcat的host-manager/html/add文件没有正确地过滤对name参数的输入便返回给了用户，远程攻击者可以通过跨站脚本攻击在登录用户浏览器会话中执行任意HTML和脚本代码。', ' ', ' ', ' ', '2009-12-17');
INSERT INTO `websoc_spider` VALUES ('368', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '远程攻击者可以进行内部服务器请求MITM攻击或发起任意主机的代理连接请求。', 'PHP（PHP：Hypertext Preprocessor，PHP：超文本预处理器）是PHP Group和开放源代码社区共同维护的一种开源的通用计算机脚本语言。该语言支持多重语法、支持多数据库及操作系统和支持C、C++进行程序扩展等。\r\nPHP 7.0.8及之前的版本中存在安全漏洞，该漏洞源于程序没有解决RFC 3875模式下的命名空间冲突。程序没有正确处理来自HTTP_PROXY环境变量中不可信客户端数据应用程序。远程攻击者借助HTTP请求中特制的Proxy header消息利用该漏洞实施中间人攻击，指引服务器发送连接到任意主机。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('369', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '恶意用户可以在服务器上执行任意 OGNL语法的Java 代码', 'Apache Struts2 是一个web应用框架. 使用了Xwork和OGNL库. 在devMode 调试模式下，XWork中的ParametersInterceptor将传入的参数，参数值作为OGNL表达式执行了。从而可以注入OGNL语法的java代码，造成命令执行。', ' ', ' ', ' ', '2016-02-19');
INSERT INTO `websoc_spider` VALUES ('370', '1996', 'web服务器', 'Himail inputfile 目录穿越', ' 高危', '目录穿越', '攻击者能够利用SQL注入漏洞脱库，篡改网站，获取数据库甚至是Web服务器的控制权限。', '\'nagiosim.php\' 页面的 \'host\' 和 \'service\' 参数存在注入,可获取任意数据这可能会破坏数据库的完整性和敏感信息被泄密。', ' ', ' ', ' ', '2016-02-19');
