INSERT INTO `websoc_spider` VALUES ('420', '133', 'web服务器', 'lighttpd 1.3.7 远程CGI脚本代码泄漏漏洞', ' 中危', '代码泄漏', 'lighttpd lighttpd 1.4.1 - 1.4.15', 'lighttpd是德国软件开发者Jan Kneschke所研发的一款开源的Web服务器，它的主要特点是仅需少量的内存及CPU资源即可达到同类网页服务器的性能。\r\nlighttpd 1.4.15版本的http_auth.c中存在远程拒绝服务攻击漏洞。\r\nLighttpd没有正确地解析HTTP头，如果向mod_auth模块发送了将算法设置为MD5-sess且没有cnonce参数的认证请求的话，就可能导致拒绝服务。\r\nmod_auth模块在解析Auth-Digest头时存在错误，如果发送了多个空格字符的话，就可能导致拒绝服务。', 'CVE-2005-0453', 'CNNVD-200502-061', ' ', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('421', '883', 'web服务器', 'finecms 1.7.2 Client.Class.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'FineCMS 1.X', '在 /extensions/function.php 中 $data 在一定条件下会带入 eval 函数，精心构造代码，会造成代码执行。', ' ', ' ', ' ', '2013-01-15');
INSERT INTO `websoc_spider` VALUES ('422', '133', 'web服务器', 'lighttpd 1.3.7 远程CGI脚本代码泄漏漏洞', ' 中危', '代码泄漏', 'lighttpd lighttpd 1.3.1 - 1.3.7', 'lighttpd是一套开放源代码的网页服务器。\r\nLighttpd 1.3.7及更早版本中的buffer_urldecode函数无法正确处理控制字符，这可让远程攻击者通过在文件扩展名后带有%00(空)字符的URL来获得CGI和FastCGI脚本的源代码。', 'CVE-2005-0453', 'CNNVD-200502-061', ' ', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('423', '133', 'web服务器', 'lighttpd 1.3.7 远程CGI脚本代码泄漏漏洞', ' 中危', '代码泄漏', 'lighttpd lighttpd 1.4.1 - 1.4.13\r\nlighttpd lighttpd 1.3.10\r\nlighttpd lighttpd 1.3.8\r\nlighttpd lighttpd 1.3.7', 'lighttpd是德国软件开发者Jan Kneschke所研发的一款开源的Web服务器，它的主要特点是仅需少量的内存及CPU资源即可达到同类网页服务器的性能。\r\nLighttpd如果在解析\\r\\n\\r\\n期间连接中断的话，服务器就会陷入死循环，使用100%的CPU时间。攻击者可以反复利用这个漏洞，直至到达服务器的并行连接极限或到达文件描述符限制。\r\nLighttpd为mtime缓存所生成的字符串，缓存密钥默认值为0。这时字符串的指针仍为空，如果请求了mtime为0的文件就会试图访问空指针导致崩溃。利用这个漏洞要求恶意用户能够上传文件或操控文件的mtime。', 'CVE-2005-0453', 'CNNVD-200502-061', ' ', '2009-12-21');
INSERT INTO `websoc_spider` VALUES ('424', '883', 'web服务器', 'finecms 1.7.2 Client.Class.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Finecms v2', 'models/Search_model.php 中 catid 参数未过滤带入 SQL 语句并执行，导致 SQL 注入漏洞，可以获取管理员的帐号以及密码。', ' ', ' ', ' ', '2013-01-15');
