INSERT INTO `websoc_spider` VALUES ('426', '133', 'web服务器', 'lighttpd 1.3.7 远程CGI脚本代码泄漏漏洞', ' 中危', '代码泄漏', 'lighttpd lighttpd 1.4.10 for windows', 'lighttpd是德国软件开发者Jan Kneschke所研发的一款开源的Web服务器，它的主要特点是仅需少量的内存及CPU资源即可达到同类网页服务器的性能。\r\nLighttpd Web Server没有正确的验证用户在URL中提供的文件名扩展，允许攻击者可以通过包含有\".\"和空格字符的请求检索脚本文件(如PHP)的源码。', 'CVE-2005-0453', 'CNNVD-200502-061', ' ', '2009-12-21');
