INSERT INTO `websoc_spider` VALUES ('431', '1113', '其他', 'Elasticsearch 9200 信息泄漏', ' 低危', '信息泄漏', 'elasticsearch v1.43', '脚本查询模块，由于搜索引擎支持使用脚本代码作为表达式进行数据操作，攻击\r\n者可以通过MVEL构造执行任意java代码，后来脚本语言引擎换成了Groovy，并且\r\n加入了沙盒进行控制，由于沙盒限制的不严格，导致远程代码执行', ' ', ' ', ' ', '2013-11-20');
