INSERT INTO `websoc_spider` VALUES ('433', '993', '其他', 'o2micro minica_down.php 代码执行漏洞', ' 高危', '代码执行 ', '网御神州、天融信、美国凹凸等vpn设备', '网御神州、天融信、美国凹凸等vpn设备开发疑似源于同一套技术，其中有技术维护使用的后门文件debug.php没有删除，该后门文件可以执行任意php代码，SQL语句。', ' ', ' ', ' ', '2013-06-17');
