INSERT INTO `websoc_spider` VALUES ('455', '1064', '其他', 'android WebWiew addJavascriptInterface 代码执行漏洞', ' 高危', '代码执行 ', 'android', '在android的sdk中封装了webView控件。这个控件主要用开控制的网页浏览。在程序中装载webView控件，可以设置属性（颜色，字体等）。类似PC下directUI的功能。在webView 下有一个非常特殊的接口函数addJavascriptInterface。能实现本地java和js的交互。利用addJavascriptInterface这个接口函数可实现穿透webkit控制android 本机。', ' ', ' ', ' ', '2013-09-17');
INSERT INTO `websoc_spider` VALUES ('456', '993', '其他', 'o2micro minica_down.php 代码执行漏洞', ' 高危', '代码执行 ', '网御神州、天融信、美国凹凸等vpn设备', '网御神州、天融信、美国凹凸等vpn设备开发疑似源于同一套技术，其中的\"/minica_down.php\"文件可以被利用从而任意代码执行。', ' ', ' ', ' ', '2013-06-17');
INSERT INTO `websoc_spider` VALUES ('457', '1113', '其他', 'Elasticsearch 9200 信息泄漏', ' 低危', '信息泄漏', 'Elasticsearch 1.5.1', 'Elasticsearch是荷兰Elasticsearch公司的一套基于Lucene构建的开源分布式RESTful搜索引擎，它主要用于云计算中，并支持通过HTTP使用JSON进行数据索引。\r\nElasticsearch 1.4.5之前版本和1.5.2之前1.5.x版本中存在目录遍历漏洞。当程序启用site插件时，远程攻击者可利用该漏洞读取任意文件。', ' ', ' ', ' ', '2013-11-20');
INSERT INTO `websoc_spider` VALUES ('458', '1880', '其他', '发现ftp弱密码', ' 中危', '弱密码', 'FTP', '当网络上的主机提供匿名FTP服务时，用户则可以通过anonymous/空、FTP/FTP、USER/pass等匿名账号登陆到这些FTP服务器。攻击者可以通过该漏洞获取主机文件系统信息。', ' ', ' ', ' ', '2014-03-07');
INSERT INTO `websoc_spider` VALUES ('459', '1880', '其他', '发现ftp弱密码', ' 中危', '弱密码', 'FTP 服务弱密码, 这个会使恶意用户(黑客)使用弱密码帐户访问系统上的某些目录或文件, 从而造成公司内部敏感信息的泄露。', 'FTP 是 File Transfer Protocol（文件传输协议）的英文简称, 主要用于网络上文件的双向传输。', ' ', ' ', ' ', '2014-03-07');
INSERT INTO `websoc_spider` VALUES ('460', '1113', '其他', 'Elasticsearch 9200 信息泄漏', ' 低危', '信息泄漏', 'Elasticsearch', '默认安装情况下，Elasticsearch会开启并监听9200端口，但是对于该端口并没有访问控制，恶意用户可以在未授权的情况下通过浏览器访问ip:9200/_nodes/stats等链接获得系统信息、以及存储的敏感数据等。', ' ', ' ', ' ', '2013-11-20');
