INSERT INTO `websoc_spider` VALUES ('464', '1169', '其他', 'f5_big_ip 后门漏洞', ' 高危', '后门 ', 'VIPRION B2100, B4100, B4200\r\nBIG-IP 520, 540, 1000, 2000, 2400, 5000, 5100, 1600, 3600, 3900, 6900, 8900, 8950, 11000, 11050\r\nBIG-IP Virtual Edition\r\nEnterprise Manager 3000, 4000', 'F5 BIG-IP产品可为企业提供集成的应用交付服务，如加速、安全、访问控制与高可用性。BIG-IP 11.x 10.x 9.x平台在实现上允许未验证用户绕过身份验证并以root用户登录到设备。', ' ', ' ', ' ', '2014-01-13');
