INSERT INTO `websoc_spider` VALUES ('474', '1113', '其他', 'Elasticsearch 9200 信息泄漏', ' 低危', '信息泄漏', 'ElasticSearch', 'Elasticsearch是荷兰Elasticsearch公司的一套基于Lucene构建的开源分布式RESTful搜索引擎，它主要用于云计算中，并支持通过HTTP使用JSON进行数据索引。\r\nElasticsearch 1.2之前版本中存在安全漏洞，该漏洞源于程序在默认配置下允许使用动态脚本。远程攻击者可利用该漏洞借助‘source’参数执行任意的MVEL表达式和Java代码。', ' ', ' ', ' ', '2013-11-20');
