INSERT INTO `websoc_spider` VALUES ('566', '604', '编程语言', 'PHP <5.2.6 cgi_main.c URI 安全模式绕过漏洞', ' 中危', '安全模式绕过', 'PHP <5.2.8', 'PHP是广泛使用的通用目的脚本语言，特别适合于Web开发，可嵌入到HTML中。\r\nPHP的imageRotate函数中存在数组索引错误，允许远程攻击者通过向索引的图片提交特制的bgd_color或clrBack 参数值读取任意内存位置的内容。', 'CVE-2008-0599', 'CNNVD-200805-022', ' ', '2011-05-13');
INSERT INTO `websoc_spider` VALUES ('567', '604', '编程语言', 'PHP <5.2.6 cgi_main.c URI 安全模式绕过漏洞', ' 中危', '安全模式绕过', 'PHP 5.2.7', 'PHP是服务器端脚本语言，特别适合于Web开发，可嵌入到HTML中，可以运行于Windows，Linux和许多Unix操作系统。\r\nPHP 5.2.7版本会对FILTER_UNSAFE_RAW功能性进行不正确的更改, 并且会无心地中止magic_quotes_gpc而无视实际的magic_quotes_gpc设置，这可能更易于见机行事的攻击者借助未明的其他向量，执行SQL注入攻击。', 'CVE-2008-0599', 'CNNVD-200805-022', ' ', '2011-05-13');
