INSERT INTO `websoc_spider` VALUES ('596', '604', '编程语言', 'PHP <5.2.6 cgi_main.c URI 安全模式绕过漏洞', ' 中危', '安全模式绕过', 'PHP<5.2.6', 'PHP是广泛使用的通用目的脚本语言，特别适合于Web开发，可嵌入到HTML中。\r\nPHP的5.2.6之前版本存在多个安全漏洞，允许恶意用户绕过安全限制、导致拒绝服务或入侵有漏洞的系统。\r\nsapi/cgi/cgi_main.c中的init_request_info功能PATH_TRANSLATED长度计算不适当的优先操作,可使远程攻击者在URI执行任意代码.', 'CVE-2008-0599', 'CNNVD-200805-022', ' ', '2011-05-13');
