INSERT INTO `websoc_spider` VALUES ('598', '1709', 'web应用', 'TurboMail <=6.0.0 /mailmain 跨站脚本漏洞', ' 中危', '跨站脚本', 'TurboMail <=6.0.0', 'TurboMail邮件系统是广州拓波软件科技有限公司面向企事业单位通信需求而研发\r\n的电子邮件服务器系统,本次漏洞是由于日志查看模块在读取日志文件时没有规定日\r\n志文件类型及路径，造成攻击者可直接通过路径穿越的方式下载服务器上的任意文件。', ' ', ' ', ' ', '2015-03-16');
