INSERT INTO `websoc_spider` VALUES ('607', '1156', 'web应用', 'Apache Roller 5.0.2 代码执行漏洞', ' 高危', '代码执行 ', 'Apache Roller < 5.0.2', 'Apache Roller < 5.0.2版本的UIAction使用了不安全的ActionSupport.getText方法导致OGNL表达注入，攻击者可以通过该漏洞执行JAVA代码或者系统命令。', ' ', ' ', ' ', '2014-01-02');
