INSERT INTO `websoc_spider` VALUES ('609', '1160', 'web应用', 'Uebimiau 3.2.0 /inc/database/system_admin/admin.ucf 信息泄漏漏洞', ' 低危', '信息泄漏', 'Uebimiau 3.2.0-2.0', 'Uebimiau 3.2.0-2.0 /admin/editor.php文件在接受参数load后会把data中的数据写入配置文件，黑客可以写入恶意代码造成代码执行漏洞。', ' ', ' ', ' ', '2014-01-03');
INSERT INTO `websoc_spider` VALUES ('610', '1127', 'web应用', 'FlashComs 6.5 /flashcoms/common/server/php/file.php 文件上传漏洞', ' 高危', '文件上传 ', 'FlashComs 6.5', 'FlashComs Chat是英国FlashComs公司的一套社区聊天软件。\r\nFlashComs Chat中存在任意文件上传漏洞，该漏洞源于程序没有正确验证文件扩展名。攻击者可利用该漏洞上传任意文件到受影响计算机，导致在受影响应用程序上下文中执行任意代码。FlashComs Chat 6.5版本中存在漏洞，其他版本也可能受到影响。', ' ', 'CNNVD-201312-054', ' ', '2013-12-10');
INSERT INTO `websoc_spider` VALUES ('611', '1128', 'web应用', 'Zimbra 8.0.2 任意文件读取漏洞', ' 高危', '任意文件读取', 'Zimbra 7.2.2, 8.0.2', 'Zimbra Collaboration Suite（ZCS）是美国Zimbra公司的一款开源协同办公套件，它包括WebMail、日历、通信录等。\r\nZimbra中存在本地文件包含漏洞，该漏洞源于程序没有正确过滤用户提交的输入。攻击者可利用该漏洞获取敏感信息如LDAP根证书，并执行任意本地脚本，有助于完全控制应用程序和计算机。', ' ', 'CNNVD-201312-225', ' ', '2013-12-11');
