INSERT INTO `websoc_spider` VALUES ('625', '1125', 'web应用', 'phpmps 2.3 /Member.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'phpmps 2.3', 'phpmps 2.3 /Member.php文件中可以看到 extract($_REQUEST);的代码，由于使用了extract解析URL参数，直接导致了变量覆盖漏洞，攻击者可以在URL中定义$table变量的值，然后经过extract函数解析后便覆盖了原有应该带入数据库查询的$table变量，导致SQL注入漏洞的产生。', ' ', ' ', ' ', '2013-12-06');
INSERT INTO `websoc_spider` VALUES ('626', '1155', 'web应用', 'ESVA 2.0.6 /cgi-bin/learn-msg.cgi 命令执行漏洞', ' 高危', '命令执行', 'ESVA 2.0.6', 'ESVA (E-Mail Security Virtual Appliance)的/cgi-bin/learn-msg.cgi和/cgi-bin/release-msg.cgi文件对用户的输入没有过滤，导致远程命令执行。', ' ', ' ', ' ', '2014-01-02');
INSERT INTO `websoc_spider` VALUES ('627', '1168', 'web应用', 'Java %c0%ae 安全模式绕过漏洞', ' 中危', '安全模式绕过', 'Java', '在Java端\"%c0%ae\"解析为\"\\uC0AE\"，最后转义为ASCCII低字符-\".\"。通过这个方法可以绕过目录保护读取包配置文件信息。', ' ', ' ', ' ', '2014-01-10');
