INSERT INTO `websoc_spider` VALUES ('629', '1167', 'web应用', 'SAP ConfigServlet 命令执行漏洞', ' 高危', '命令执行', 'SAP', 'SAP ConfigServlet 在实现上存在安全问题，在未经过认证的情况下，攻击者可以通过该功能执行任意系统命令，例如/ctc/servlet/com.sap.ctc.util.ConfigServlet?param=com.sap.ctc.util.FileSystemConfig;EXECUTE_CMD;CMDLINE=whoami，其中CMDLINE是要执行的系统命令。', ' ', ' ', ' ', '2014-01-10');
INSERT INTO `websoc_spider` VALUES ('630', '1179', 'web应用', 'JIS /JIS/down.jsp 任意文件下载漏洞', ' 高危', '任意文件下载', 'Hanweb jis', '/jis/objectbox/selx_list.jsp?id=1 变量id 未进行正确过滤,导致SQL注入', ' ', ' ', ' ', '2014-02-19');
INSERT INTO `websoc_spider` VALUES ('631', '1129', 'web应用', 'CGILua 3.0 /publique/cgi/cgilua.exe/sys/start.htm SQL注入漏洞', ' 高危', 'Sql注入 ', 'CGILua 3.0', 'CGILua是一套基于lua脚本的动态网页生成工具。\r\nCGILua中存在SQL注入漏洞，该漏洞源于程序构造SQL查询语句之前没有成充分过滤用户提交的输入。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞。CGILua 3.0版本中存在漏洞，其他版本也可能受到影响。', ' ', 'CNNVD-201312-238', ' ', '2013-12-11');
INSERT INTO `websoc_spider` VALUES ('632', '1126', 'web应用', 'web_admin_tools /yb/yb.php 代码执行漏洞', ' 高危', '代码执行 ', 'web_admin_tools', 'web_admin_tools是一款基于PHP的站长工具程序，其存在代码执行漏洞，攻击者可以利用该漏洞,执行恶意PHP代码。', ' ', ' ', ' ', '2013-12-09');
