INSERT INTO `websoc_spider` VALUES ('656', '1078', 'web应用', 'appcms 1.3.708 /pic.php 任意文件下载漏洞', ' 高危', '任意文件读取', 'appcms 1.3.834', 'pic.php文件中的代码只要出现jpg 就可以绕过循环过滤的检测。从而可以再次构造所需下载文件的路径，进行 base64编码后，提交给 url 参数，下载文件。', ' ', ' ', ' ', '2013-10-11');
INSERT INTO `websoc_spider` VALUES ('657', '1072', 'web应用', 'Ecmall 2 /app/find_password.php 远程密码修改漏洞', ' 高危', '远程密码修改', 'Ecmall 2.3.0', '在编辑团购订单过程中，由于对拼接参数id字段中的关键词过滤不够严谨，导致用户可以利用extractvalue函数造成SQL语句报错注入', ' ', ' ', ' ', '2013-09-29');
INSERT INTO `websoc_spider` VALUES ('658', '1070', 'web应用', 'songcms 3.16 /global.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'songcms 3.16', 'songcms 3.16 global.php文件中对接受的字符串进行了一系列的处理，由于preg_replace(\"/[\\\\\\:\\*\\?\\\"\\\'<>\\~\\(\\)\\[\\]\\{\\}\\s\\$]/\",\'\',$_SERVER[\'QUERY_STRING\']);正则编写的不严谨，当我们外部带入url为 http://site//?m1/fproducts/l1/w1%25\' and 1=1 and \'%25\'=\'时，经过上述处理，就会生成w变量 ，经过urldecode解码，变成为1%25\' and 1=1 and \'%25\'=\'，绕过了GPC，带入数据库中查询。从而触发了漏洞。', ' ', ' ', ' ', '2013-09-26');
INSERT INTO `websoc_spider` VALUES ('659', '1072', 'web应用', 'Ecmall 2 /app/find_password.php 远程密码修改漏洞', ' 高危', '远程密码修改', 'Ecmall 2.3.0', '在进行feed设置时，对反序列化后的变量进行SQL语句拼接\r\n由于对拼接参数中关键词过滤不够严谨，导致用户可以利用extractvalue函数造成SQL语句报错注入', ' ', ' ', ' ', '2013-09-29');
INSERT INTO `websoc_spider` VALUES ('660', '1067', 'web应用', 'IceWarp 10.4.5 /rpc/gw.html 任意文件读取漏洞', ' 高危', '任意文件读取', 'IceWarp 10.4.5', 'IceWarp 10.4.5 /rpc/gw.html 使用simplexml_string_load解析xml，没有限制xml引用外部实体，导致任意文件读取漏洞', ' ', ' ', ' ', '2013-09-26');
