INSERT INTO `websoc_spider` VALUES ('662', '1063', 'web应用', 'Tipask 2.0 /control/question.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Tipask 2.0', 'Tipask 2.0 文件/control/question.php中Onajaxsearch函数对get的第二个参数urldecode后直接传入SQL语句，绕过了前面的过滤和检查，导致SQL注入的产生。', ' ', ' ', ' ', '2013-09-17');
