INSERT INTO `websoc_spider` VALUES ('678', '1084', 'web应用', 'E-tiller /reader/key_query.aspx SQL注入漏洞', ' 高危', 'Sql注入 ', 'e-tiller稿件系统8.0', '北京勤云科技发展有限公司（以下简称勤云）自2002年开始从事研发和推广远程稿件处理系统（采编系统），\r\n目前已有10年的发展历史，勤云以稳定、灵活的产品，周到的服务和创新精神赢得了广大期刊界同仁的赞赏和推荐，\r\n在行业内享有极高的口碑，目前用户数量已超过800家，覆盖了中小期刊编辑部和大型的多杂志期刊社。\r\n远程稿件处理系统8.0 /ch/reader/appraise.aspx?file_no=201309285&journal_id=zgnxtb存在表单提交过滤不严导致sql注入漏洞', ' ', ' ', ' ', '2013-10-16');
