INSERT INTO `websoc_spider` VALUES ('691', '1061', 'web应用', 'U-mail 9.8.53 /worldClient/html/fileshare.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'U-Mail 9.8.57', 'U-Mail 9.8.57 /client/pad/module/o_contact.php在导入联系人信息时，没有过滤内容，将其带入了SQL语句，导致能够通过INTO OUTFILE操作进行Getshell。（需要登录）', ' ', ' ', ' ', '2013-09-10');
