INSERT INTO `websoc_spider` VALUES ('693', '1061', 'web应用', 'U-mail 9.8.53 /worldClient/html/fileshare.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'u-mail', 'U-Mail Windows版本安装时使用IIS和PHP作为WEB中间件，PHP的默认运行方式为fcgi，但是PHP的默认配置中cgi.fix_pathinfo=1，从而导致了PHP路径解析漏洞。', ' ', ' ', ' ', '2013-09-10');
