INSERT INTO `websoc_spider` VALUES ('697', '1072', 'web应用', 'Ecmall 2 /app/find_password.php 远程密码修改漏洞', ' 高危', '远程密码修改', 'Ecmall v2.0', 'Ecmall v2.0 index.php?code=xx 的地方没有进行过滤，导致可以执行基于错误的SQL注入漏洞', ' ', ' ', ' ', '2013-09-29');
INSERT INTO `websoc_spider` VALUES ('698', '1072', 'web应用', 'Ecmall 2 /app/find_password.php 远程密码修改漏洞', ' 高危', '远程密码修改', 'Ecmall 2', 'Ecmall 2 /app/find_password.php 文件中有代码为$ms->user->edit($id, \'\', array(\'password\' => $password), true); 第三个参数为强制修改的参数，默认为flase，其edit函数定义在文件2中，看到$force就是第三个参数，默认是false，当他为true的时候，就直接跳过了身份验证，任意用户便可强制修改密码了。而文件3便是从外部接受的密码进行update了。由于无需验证便可修改，导致攻击者可以构造一个POST数据包，发送给程序，直接导致管理员账户密码可被修改。', ' ', ' ', ' ', '2013-09-29');
INSERT INTO `websoc_spider` VALUES ('699', '1078', 'web应用', 'appcms 1.3.708 /pic.php 任意文件下载漏洞', ' 高危', '任意文件读取', 'appcms 1.3.855', 'pic.php文件中的代码可以利用\\x00字符和windows 环境下’>’字符可替代任意字符进行绕过。由此，可以特殊构造文件名，进行 base64编码后实现任意文件下载。', ' ', ' ', ' ', '2013-10-11');
INSERT INTO `websoc_spider` VALUES ('700', '1078', 'web应用', 'appcms 1.3.708 /pic.php 任意文件下载漏洞', ' 高危', '任意文件读取', 'appcms 1.3.708', 'appcms 1.3.708 pic.php文件，未对下载路径做限制。只是使用了base64进行编码。攻击者只要根据所需下载的文件问，进行base64编码后，即可完成任意文件下载。', ' ', ' ', ' ', '2013-10-11');
INSERT INTO `websoc_spider` VALUES ('701', '1060', 'web应用', 'Siteserver 3.6.3 /siteserver/CMS/console_tableMetadata.aspx SQL注入漏洞', ' 高危', 'Sql注入 ', 'SiteServer 3.6.4', 'SiteServer 3.6.4 /siteserver/cms/background_contentsGroup.aspx文件使用了位于/Bin/SiteServer.CMS.dll代码，在接收GroupName参数没有合适过滤，导致SQL注入漏洞。', ' ', ' ', ' ', '2013-09-09');
INSERT INTO `websoc_spider` VALUES ('702', '1048', 'web应用', 'ThinkSaas 1.98 /app/group/action/rss.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'ThinkSAAS 2.0.1', 'ThinkSAAS 2.0.1 /thinksaas/thinksaas.php 文件在加载app配置文件时采用了include 方法，以app为变量作为include的文件，导致了本地包含的漏洞，在php<=5.3.4的情况下可使用%00进行截断，将后面的/config.php忽略，直接导致文件包含漏洞。', ' ', ' ', ' ', '2013-08-27');
