INSERT INTO `websoc_spider` VALUES ('780', '926', 'web应用', 'eYou /php/ip_status.php 命令执行漏洞', ' 高危', '命令执行', 'eYou', 'eYou是国内一款流行的邮件管理系统软件，其存/php/report/lastlogin_list_export.php文件中代码第52行，判断$_GET[\'time\']和$_GET[\'stime\']变量是否为空，执行代码第53-54行，删除由addslashes()函数添加的反斜杠，并赋值给$time和$stime变量。执行代码第66行，将$time和$stime变量拼接SQL语句，代码第68行，将拼接的SQL语句带入数据库执行。此过程中，并未将$time和$stime变量进行有效过滤，导致SQL注入漏洞产生。', ' ', ' ', ' ', '2013-04-16');
INSERT INTO `websoc_spider` VALUES ('781', '924', 'web应用', 'RubyonRails 3 XML Processor YAML Deserialization 代码执行漏洞', ' 高危', '代码执行 ', 'RubyonRails 3', 'Ruby on Rails（Rails）是Rails核心团队开发维护的一套基于Ruby语言的开源Web应用框架，它是由大卫-海纳梅尔-韩森从美国37signals公司的项目管理工具Basecamp里分离出来的。\r\nRuby on Rails 2.3.15之前版本，3.0.19之前的3.0.x版本，3.1.10之前的3.1.x版本以及3.2.11之前的3.2.x版本中的active_support/core_ext/hash/conversions.rb中存在漏洞，该漏洞源于程序没有正确限制一系列字符串的值。通过利用Action Pack支持(1)YAML类型转换或(2)Symbol类型转换，远程攻击者利用该漏洞进行对象注入攻击以及执行任意代码或导致拒绝服务（内存和CPU消耗）包含嵌套的XML实体引用。', 'CVE-2013-0156', 'CNNVD-201301-089', ' ', '2013-04-14');
