INSERT INTO `websoc_spider` VALUES ('791', '870', 'web应用', 'Invision-Power-Board 3.3.4 core.php 代码执行漏洞', ' 高危', '代码执行 ', 'Invision Power Board <= 3.3.4', 'Invision Power Services Invision Power Board（IPB或IP.Board）是美国Invision Power Services公司的一款基于PHP和MySQL的Web论坛程序。该程序可方便地创建、管理在线论坛。\r\nInvision Power Board (又名IPB或IP.Board) 3.1.x至3.3.x版本中的admin/sources/base/core.php脚本中存在未明漏洞。攻击者可借助未明向量利用该漏洞造成未知影响。', 'CVE-2012-5692', 'CNNVD-201210-816', ' ', '2012-11-06');
