INSERT INTO `websoc_spider` VALUES ('793', '740', 'web应用', 'PHPShop CMS 3.4 多个文件xss漏洞', ' 中危', '跨站脚本', 'PHPShop CMS Free 3.4', 'phpShop是一套开源的、免费的基于PHP的专业级网上商店系统。\r\nPHPShop CMS中存在多个跨站脚本漏洞和SQL注入漏洞，该漏洞源于对用户提供的输入在使用动态生成内容之前未经正确过滤。攻击者可利用该漏洞窃取基于cookie的认证证书，操控应用程序；访问或修改数据，或者在底层数据库中利用该漏洞。PHPShop CMS 3.4版本中存在该漏洞，其他版本中也可能受到影响。', ' ', 'CNNVD-201112-381', ' ', '2011-12-22');
INSERT INTO `websoc_spider` VALUES ('794', '869', 'web应用', 'ueditor 1.2.2.0 fileUp.jsp 文件上传漏洞', ' 高危', '文件上传 ', 'ueditor (jsp) 1.2.2.0\r\nueditor 1.2.0', '上述版本ueditor的jsp版本中的fileUp.jsp中使用java正则表达式验证上传文件的文件名，重新设置文件名的时候，没有使用lastIndexOf()方法来找最后一个点，导致可以上传xx.pdf.jsp等jsp文件，恶意用户可以通过该漏洞得到weshell控制服务器。', ' ', ' ', 'CNVD-2012-15610', '2012-10-29');
INSERT INTO `websoc_spider` VALUES ('795', '844', 'web应用', 'testlink 1.9.3 lib/ajax/getrequirementnodes.php SQL Injection', ' 高危', 'Sql注入 ', 'testlink 1.9.3', 'Testlink是TestLink团队开发的一套基于PHP的开源测试管理工具。该工具提供测试需求管理、测试用例管理和测试数据统计等功能。\r\nTestLink 1.9.3和1.8.5b版本中存在SQL注入漏洞，该漏洞源于lib/ajax/ URL下的getrequirementnodes.php或gettprojectnodes.php脚本没有充分过滤‘display_children’函数中的root_node参数；在程序执行edit操作时，lib/cfields/cfieldsEdit.php脚本没有充分过滤‘cfield_id’参数；程序在执行edit或create操作时，lib/plan/planMilestonesEdit.php脚本没有充分过滤‘id’或‘plan_id’参数；reqImport.php脚本没有充分过滤‘req_spec_id’参数；程序执行create操作时，lib/requirements/ URL下的reqEdit.php脚本没有充分过滤‘req_spec_id’参数。远程攻击者可利用该漏洞以特定权限执行任意SQL命令。', 'CVE-2012-0938', 'CNNVD-201202-383', 'CNVD-2012-10086', '2012-06-26');
INSERT INTO `websoc_spider` VALUES ('796', '740', 'web应用', 'PHPShop CMS 3.4 多个文件xss漏洞', ' 中危', '跨站脚本', 'PHPShop CMS Free 3.4.2.7', 'phpShop是一套开源的、免费的基于PHP的专业级网上商店系统。\r\nPHPShop CMS中存在多个跨站脚本漏洞和SQL注入漏洞，该漏洞源于对用户提供的输入在使用动态生成内容之前未经正确过滤。攻击者可利用该漏洞窃取基于cookie的认证证书，操控应用程序；访问或修改数据，或者在底层数据库中利用该漏洞。PHPShop CMS 3.4版本中存在该漏洞，其他版本中也可能受到影响。', ' ', 'CNNVD-201112-381', ' ', '2011-12-22');
INSERT INTO `websoc_spider` VALUES ('797', '835', 'web应用', 'Wordpress 插件 WP-Property 1.35.0 uploadify.php 文件上传漏洞', ' 高危', '文件上传 ', 'Wordpress插件 WP-Property <=1.35.0', 'WordPress是WordPress软件基金会的一套使用PHP语言开发的博客平台。该平台支持在PHP和MySQL的服务器上架设个人博客网站。\r\nWordPress的WP-Property插件中存在任意文件上传漏洞，该漏洞源于应用程序对用户提供的输入未经充分过滤。攻击者可利用该漏洞在web服务器进程上下文中上传并运行任意PHP代码，这可能有利于未授权访问或权限提升，也可能执行其他的攻击。WP-Property 1.35.0版本中存在该漏洞，其他版本也可能受到影响。', ' ', 'CNNVD-201206-058', 'CNVD-2012-12386', '2012-06-08');
INSERT INTO `websoc_spider` VALUES ('798', '836', 'web应用', 'phpldapadmin 1.2.1.1 lib-functions.php 代码执行漏洞', ' 高危', '代码执行 ', 'phpldapadmin >=1.2.0\r\nphpldapadmin <=1.2.1.1', 'phpLDAPadmin是基于Web的LDAP客户端，允许方便的管理LDAP服务器。\r\nphpLDAPadmin  1.2.0到1.2.1.1版本中存在远程PHP代码注入漏洞。攻击者可借助functions.php在受影响应用程序上下文中注入和执行任意PHP代码，可能进一步危害应用程序，或其他攻击。', 'CVE-2011-4075', 'CNNVD-201110-596', 'CNVD-2011-08044', '2012-06-07');
INSERT INTO `websoc_spider` VALUES ('799', '750', 'web应用', 'PowerEasy 2006 region.asp sql注入漏洞', ' 高危', 'Sql注入 ', 'PowerEasy 2006', '在动易(PowerEasy)2006版本中的region.asp文件中,因为使用了一个未做过滤的用户提交参数,导致存在一个SQL注入漏洞.', ' ', ' ', ' ', '2012-01-10');
