INSERT INTO `websoc_spider` VALUES ('801', '833', 'web应用', 'jaow 2.4.5 add_ons.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Jaow <=2.4.5', 'Jaow 2.4.5及之前版本中的add_ons.php中存在SQL注入漏洞。远程攻击者可利用该漏洞借助add_ons参数执行任意SQL命令。', 'CVE-2012-2952', 'CNNVD-201205-519', 'CNVD-2012-12098', '2012-06-01');
INSERT INTO `websoc_spider` VALUES ('802', '796', 'web应用', 'ThinkPHP 2.1/2.2/3.0 Dispatcher.class.php 代码执行漏洞', ' 高危', '代码执行 ', 'ThinkPHP 2.1\r\nThinkPHP 2.2\r\nThinkPHP 3.0', 'ThinkPHP是一款流行的PHP语言开发框架，其2.1、2.2、3.0版本中存在命令执行漏洞，攻击者可以利用该漏洞，执行恶意PHP语句。\r\n漏洞根源位于/Lib/Core/Dispatcher.class.php文件中：\r\n119  if(!isset($_GET[C(\'VAR_MODULE\')])) {// 还没有定义模块名称\r\n120      $var[C(\'VAR_MODULE\')] = array_shift($paths); // $var = Array ( [m] => index )\r\n121  }\r\n122  $var[C(\'VAR_ACTION\')] = array_shift($paths); //$var = Array ( [m] => index [a] => hack )\r\n123  // 解析剩余的URL参数\r\n124  $res = preg_replace(\'@(\\w+)\'.$depr.\'([^\'.$depr.\'\\/]+)@e\', \'$var[\\\'\\\\1\\\']=\"\\\\2\";\',implode($depr,$paths));#问题代码\r\n125  \r\n126  $_GET = array_merge($var,$_GET);\r\nThinkPHP在解析URL参数后动态生成URL时，使用了替换函数preg_replace，并且调用了e开关，同时又使用了双引号的方式来包含变量，由于PHP支持双引号的变量语法解析，因此导致任意代码执行。', ' ', ' ', ' ', '2012-04-11');
INSERT INTO `websoc_spider` VALUES ('803', '877', 'web应用', 'phpdisk 6.5 api/datacall.php sql 注入漏洞', ' 高危', 'Sql注入 ', 'PHPDisk E-Core 2.5', 'phpdisk_del_process未对file_id做过滤,导致文件删除,文件删除日志delfile_log可写入代码,并执行\r\n需要开启register_globals(在php5.3废弃5.4移除),代码执行需要关闭short_open_tag,影响范围小', ' ', ' ', ' ', '2012-12-05');
INSERT INTO `websoc_spider` VALUES ('804', '877', 'web应用', 'phpdisk 6.5 api/datacall.php sql 注入漏洞', ' 高危', 'Sql注入 ', 'phpdisk 6.8.0', 'PHPDISK网盘系统6.8 V-Core版本存在安全漏洞。PHPDisk在安装时，没有随机生成UC_KEY 而采用默认的值：.//upload/plugins/api/uc_configs.inc.php:14:define(\'UC_KEY\', \'phpdisk60_dx15_uc\');那么攻击者可以通过这个默认值调用uc.php的api接口，实现各种功能并触发SQL注射等漏洞。', ' ', ' ', ' ', '2012-12-05');
INSERT INTO `websoc_spider` VALUES ('805', '877', 'web应用', 'phpdisk 6.5 api/datacall.php sql 注入漏洞', ' 高危', 'Sql注入 ', 'phpdisk <=6.5', 'phpdisk在上述版本中的/api/datacall.php没有对用户提交的by,order参数进行验证，导致SQL注入，攻击者可以通过注入获取网站管理员的账号和密码信息，进一步的攻击可能得到webshell控制服务器。', ' ', ' ', ' ', '2012-12-05');
INSERT INTO `websoc_spider` VALUES ('806', '867', 'web应用', '青果教务网络系统 SQL注入漏洞', ' 高危', 'Sql注入 ', '', '该系统有个教师注册页面没有设置访问权限（漏洞页面：http://xxx/_data/index_JSRP.aspx）任何人都可以访问，并且随便注册教师，注册时程序对身份证号进行了验证，去查询数据库是否存在该身份证号但没有进行很好的限制导致SQL注入漏洞。恶意攻击者可以通过该漏洞得到webshell进而控制服务器。', ' ', ' ', ' ', '2012-10-18');
INSERT INTO `websoc_spider` VALUES ('807', '863', 'web应用', 'ThinkSNS 2.5 thumb.php 文件上传漏洞', ' 高危', '文件上传 ', 'ThinkSNS V3.0', 'ThinkSNS /apps/public/Lib/Action/AccountAction.class.php 处存在二次注入导致管理员账号密码泄露。', ' ', ' ', ' ', '2012-09-29');
INSERT INTO `websoc_spider` VALUES ('808', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'MyBB 1.8.1', 'MyBB（又名MyBulletinBoard）是MyBB团队开发的一套用PHP和MySQL开发的免费且基于Web的论坛软件。该软件具有简单易用、支持多国语言、可扩展等特点。\r\nMyBB 1.8.2之前1.8.x版本中存在跨站脚本漏洞，该漏洞源于report.php脚本没有充分过滤‘type’参数；usercp.php脚本没有充分过滤‘signature’参数；程序没有充分过滤style-templates模块中的‘title’参数；admin/index.php脚本没有充分过滤config-languages模块中的‘file’参数。远程攻击者可利用该漏洞注入任意Web脚本或HTML。', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('809', '809', 'web应用', 'BlueCMS 1.6 include-common.func.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'BlueCMS 1.6', 'BlueCMS是一个地方分类信息门户专用CMS系统。该程序在使用getip()函数获取客户端ip时没有严格过滤数据就直接插入数据库，导致sql注射漏洞。', ' ', ' ', ' ', '2012-05-06');
INSERT INTO `websoc_spider` VALUES ('810', '817', 'web应用', 'abcms 1.0 app/controller/admincp.php登录绕过漏洞', ' 高危', '登录绕过', 'ABCMS 1.0', '在 abcms 1.0版本中的app/controller/admincp.php文件，由于未验证请求用户身份，导致恶意用户可直接看到数据库中的管理员帐号与密码HASH。', ' ', ' ', ' ', '2012-05-17');
INSERT INTO `websoc_spider` VALUES ('811', '818', 'web应用', 'Openconf <=4.11 /author/edit.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'OpenConf <=4.11', 'OpenConf 4.12之前版本中存在SQL注入漏洞，该漏洞源于对用户提供的数据未经充分过滤。攻击者可利用该漏洞操控应用程序，访问或修改数据，或在底层数据库中利用此漏洞。', 'CVE-2012-1002', 'CNNVD-201205-410', ' ', '2012-05-18');
INSERT INTO `websoc_spider` VALUES ('812', '803', 'web应用', 'PHP-Volunteer-Management 1.0.x<=1.0.2 mods-hours-data-get-hours.php 跨站脚本漏洞', ' 中危', '跨站脚本', 'PHP Volunteer Management 1.0.0&1.0.1&1.0.2', 'PHP Volunteer Management中存在SQL注入漏洞，该漏洞源于对用户提供的数据在用于SQL查询之前未经充分过滤。攻击者可利用该漏洞操控应用程序，访问或修改数据，或者在底层数据库中利用该漏洞。PHP Volunteer Management 1.0.2版本中存在该漏洞，其他版本可能受到影响。', ' ', 'CNNVD-201301-479', ' ', '2012-04-30');
INSERT INTO `websoc_spider` VALUES ('813', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'MyBB 1.8.1', 'MyBB（又名MyBulletinBoard）是MyBB团队开发的一套用PHP和MySQL开发的免费且基于Web的论坛软件。该软件具有简单易用、支持多国语言、可扩展等特点。\r\nMyBB 1.8.2之前1.8.x版本中存在跨站脚本漏洞，该漏洞源于report.php脚本没有充分过滤‘type’参数；usercp.php脚本没有充分过滤‘signature’参数；程序没有充分过滤style-templates模块中的‘title’参数；admin/index.php脚本没有充分过滤config-languag', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('814', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'MyBB 1.8.2', 'MyBB（又名MyBulletinBoard）是MyBB团队开发的一套用PHP和MySQL开发的免费且基于Web的论坛软件。该软件具有简单易用、支持多国语言、可扩展等特点。\r\nMyBB 1.8.2之前1.8.x版本中存在跨站脚本漏洞，该漏洞源于report.php脚本没有充分过滤‘type’参数；usercp.php脚本没有充分过滤‘signature’参数；程序没有充分过滤style-templates模块中的‘title’参数；admin/index.php脚本没有充分过滤config-languages模块中的‘file’参数。远程攻击者可利用该漏洞注入任意Web脚本或HTML。', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('815', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'MyBB 1.8.1', 'MyBB（又名MyBulletinBoard）是MyBB团队开发的一套用PHP和MySQL开发的免费且基于Web的论坛软件。该软件具有简单易用、支持多国语言、可扩展等特点。\r\nMyBB 1.8.2之前1.8.x版本的member.php脚本中存在SQL注入漏洞。远程攻击者可借助do_register操作中的‘question_id’参数利用该漏洞执行任意SQL命令。', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('816', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'Mybb 1.8.1', 'Mybb <= 1.8.1 /report.php 对传递的参数 \'type\' 过滤不全，并且将其作为返回输出内容输出，造成反射型XSS（需要登录）。', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('817', '826', 'web应用', 'Alpaca 3.3.2 app/m/elem.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'Alpaca 3.3.2', '在Alpaca 3.3.2的app/m/elem.php文件中，因为过滤函数过滤不严禁，导致SQL注入漏洞。', ' ', ' ', ' ', '2012-05-28');
INSERT INTO `websoc_spider` VALUES ('818', '800', 'web应用', 'nweb 2009 system-chcklogin.asp SQL注入漏洞', ' 高危', 'Sql注入 ', '万博2009', '万博2009版本的后台管理员登录处，未对用户名做任何检查，导致攻击者可构造特殊语句绕过检查进入后台。', ' ', ' ', ' ', '2012-04-19');
INSERT INTO `websoc_spider` VALUES ('819', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.0 < 6.1.0.23\r\nWebSphere 7.0.0.0 < 7.0.0.3', 'IBM WebSphere Application Server是一个完善的、开放的Web应用服务器，它是IBM电子商务应用架构的核心。                                                       \r\nIBM WebSphere Application Server (WAS) 中的管理控制台存在授权问题漏洞。被迫注销相关的\"特定情节\"中的攻击者可以利用此漏洞，劫持用户会话。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('820', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'Mybb 1.8.2', '/inc/class_core.php中，对register_global=On时的过滤不严攻击者可以绕过过滤，覆盖任意变量，实现任意代码执行。', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('821', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.0 < 6.0.2.35\r\nWebSphere  6.1.0.0 < 6.1.0.23', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。\r\nWebsphere应用服务器的管理员控制台存在跨站脚本漏洞，远程攻击者可以通过未明向量执行跨站脚本攻击。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('822', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 7.0.0.* < 7.0.0.3', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。\r\nWebsphere应用服务器的集成解决方案控制台、PlantsByWebSphere、Dynamic Query、Application Profile等模块和/scheduler/accountreport、/scriptwssamplesei/demo等脚本没有正确地过滤用户所提交的多个参数，远程攻击者可以通过向服务器提交恶意请求执行跨站脚本攻击。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('823', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.0 < 6.1.0.25\r\nWebSphere 7.0.0.0 < 7.0.0.5', '当跟踪被激活和6.1到7.0版本的migration出现时，IBM WebSphere Application Server (WAS) 6.1.0.25之前的6.1版本和7.0.0.5之前的7.0版本中的Migration组件允许远程认证用户通过读取一个Migration跟踪文件，获得敏感信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('824', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 7.0.0.0 < 7.0.0.5', 'IBM WebSphere Application Server (WAS) 7.0.0.5之前的7.0版本中的系统管理/系统库组件中的wsadmin存在未明漏洞。远程攻击者可以借助未知向量，绕过设置的Java Management Extensions (JMX) Management Beans(又称MBeans)访问限制和引起拒绝服务攻击(后台程序停止)。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('825', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 4.0.3', 'IBM Websphere 4.0.3版本存在漏洞。远程攻击者可以借助带有超长HTTP标题的HTTP请求，例如：\"Host\"，导致服务拒绝（崩溃）并且可能执行任意代码。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('826', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 5.0 < 5.1.1.19\r\nWebSphere 6.0.0.0 < 6.0.2.33\r\nWebSphere 6.1.0.0 < 6.1.0.23', 'IBM WebSphere Application Server是一个完善的、开放的Web应用服务器，它是IBM电子商务应用架构的核心。                                  \r\nIBM WebSphere Application Server (WAS) 5.1.1.19和之前的5.x版本，6.0.2.33之前的6.0.x版本以及6.1.0.23之前的6.1.x版本中的ibm_security注销servlet存在开放重定向漏洞。远程攻击者可以借助logoutExitPage', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('827', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', '', 'websphere(只测6.0.2.25版本)默认情况下， 可用弱口令登录，从而导致攻击者可据此信息进行后续攻击。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('828', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.* < 6.0.2.37\r\nWebSphere 6.1.* < 6.1.0.27\r\nWebSphere 7.0.* < 7.0.0.7', 'IBM WebSphere Application Server(WAS)，是Java EE 和Web 服务应用程序平台，是IBM WebSphere 软件平台的基础。\r\nIBM WebSphere Application Server (WAS) 6.0.2.37之前的6.0.2中的Servlet Engine/Web Container组件没有正确的对(1)doGet和(2)doTrace方式执行安全限制，这使得远程攻击者可以借助一个特制的对web应用程序的HTTP头请求，绕过设置的访问限制和获得敏感信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('829', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.* < 6.1.0.27\r\nWebSphere 7.0.* < 7.0.0.7', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。\r\nWebSphere应用服务器中的Eclipse Help组件没有正确地验证用户所提交的输入，远程攻击者可以通过提交恶意的URL请求执行跨站脚本攻击，导致窃取基于Cookie的认证凭据。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('830', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.* < 6.1.0.27\r\nWebSphere 7.0.* < 7.0.0.7', 'IBM WebSphere Application Server (WAS) 6.1.0.27版本之前的6.1版本中存在未明漏洞。远程攻击者可以借助未知向量造成拒绝服务。该漏洞与\"fixpacks 6.1.0.23版本和6.1.0.25版本中的一个错误\"相关。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('831', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.* < 6.1.0.27\r\nWebSphere 7.0.* < 7.0.0.7', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。\r\nIBM WebSphere Application Server (WAS) 6.1.0.27之前的6.1版本和7.0.0.7之前的7.0版本在使用wsadmin脚本和JAAS-J2C认证数据的配置后不能正确处理某些异常。本地用户可通过读取WebSphere应用服务器的首次故障数据捕获(FFDC)日志文件获得敏感信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('832', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.0 < 6.0.2.37', 'IBM WebSphere Application Server(WAS)，是Java EE 和Web 服务应用程序平台，是IBM WebSphere 软件平台的基础。\r\nIBM WebSphere Application Server (WAS) 6.0.2.37之前的6.0.2中的Servlet Engine/Web Container组件没有正确的对(1)doGet和(2)doTrace方式执行安全限制，这使得远程攻击者可以借助一个特制的对web应用程序的HTTP头请求，绕过设置的访问限制和获得敏感信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('833', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', '-', '通过请求一个不必须的没有的\'snoopservlet\'版本，脚本可以尝试列举servlet 类的真实物理路径。一个获取到了文件系统的真实物理路径的攻击者，可以利用这些信息来使系统崩溃或者做更多复杂的攻击。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('834', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.* <= 6.1.0.24\r\nWebSphere 7.0.* <= 7.0.0.4', 'IBM WebSphere Application Server (WAS) 6.1版本至6.1.0.24版本以及7.0版本至7.0.0.4版本,IBM WebSphere Portal Server 5.1版本至6.0版本,和IBM Integrated Solutions Console (ISC) 6.0.1版本在WebSphere Member Manager (WMM)向Virtual Member Manager (VMM)和联合贮存器的移动中没有适当地设置securityEnabled安全标识。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('835', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.0 < 6.0.2.39\r\nWebSphere 6.1.0.0 < 6.1.0.29\r\nWebSphere 7.0.0.0 < 7.0.0.7', 'IBM WebSphere Application Server 为数百万名开发人员和 IT 架构师提供了创新的性能基础平台，用于构建、重用、运行、整合和管理面向服务架构 (SOA) 应用程序与服务，从而提高业务敏捷性。 IBM WebSphere Application Server的安全组件中的管理控制平台存在跨站请求伪造漏洞。远程攻击者可以借助未明向量，劫持管理员的身份认证。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('836', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 7.0.* < 7.0.0.5', 'z/OS上的IBM WebSphere Application Server(WAS)7.0.0.5之前的7.0版本中的System Management/Repository组件为新的应用程序使用弱文件许可，这使得远程攻击者可以借助未明向量，获得敏感信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('837', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.* < 6.1.0.25\r\nWebSphere 7.0.0.* < 7.0.0.5', 'IBM WebSphere Application Server (WAS) 6.1.0.25之前的6.1版本以及7.0.0.5之前的7.0版本中的安全组件没有正确的使用带有CSIv2Security的身份标识，这使得远程攻击者可以借助涉及Enterprise JavaBeans的向量，绕过设置的CSIv2访问限制。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('838', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere Application Server (WAS) 5.1\r\nWAS 6.0.2.* < 6.0.2.33', 'IBM WebSphere Application Server是一个完善的、开放的Web应用服务器，它是IBM电子商务应用架构的核心。\r\n在z/OS上，IBM WebSphere Application Server (WAS) 5.1版本和6.0.2.33之前的版本6.0.2中存在未明漏洞，当CSIv2 Identity Assertion被激活以及Enterprise JavaBeans (EJB)交互作用发生在一个WAS 6.1场合和一个WAS pre-6.1场合之间时，本地用户可以借助与(1)主', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('839', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.* < 6.1.0.25', 'IBM WebSphere Application Server (WAS)6.1.0.25之前的6.1版本中的网络服务部件中的IBM Stax XMLStreamWriter没能有效的运行XML编码，这使得远程攻击者可以借助通过SOAP请求发送的\"XML fuzzing attacks\"绕过设置的访问限制以及可能修改数据。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('840', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 7.0 < 7.0.0.5', 'IBM WebSphere Application Server(WAS)7.0.0.5之前的7.0版本没有正确的读取ibm-portlet-ext.xmi中的portletServingEnabled参数，这使得远程攻击者可以借助未知向量，绕过设置的访问限制。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('841', '825', 'web应用', 'supernews <=2.6.1 funcao.php SQL注入漏洞', ' 高危', 'Sql注入 ', 'supernews <=2.6.1', 'SuperNews中存在SQL注入漏洞，该漏洞源于对用户提供的数据在用于SQL查询之前未经充分过滤。攻击者可利用该漏洞操控应用程序，访问或修改数据，或者在底层数据库中利用该漏洞。SuperNews 2.6.1及之前版本中存在该漏洞。', ' ', 'CNNVD-201205-439', ' ', '2012-05-25');
INSERT INTO `websoc_spider` VALUES ('842', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.* < 6.1.0.25\r\nWebSphere 7.0.* < 7.0.0.3', 'IBM WebSphere Application Server (WAS) 7.0.0.3版本之前的7.0版本以及WAS 6.1.0.25版本之前的6.1版本的网络服务的Feature补丁,在一个WS-Security策略在操作水平上建立以后，没有适当地处理入站的请求，并缺少一个SOAPAction或WS-Addressing操作，这会允许远程攻击者可以借助一个特制的对JAX-WS应用程序的请求，绕过预设的访问限制。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('843', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.0 < 6.1.0.25\r\nWebSphere 7.0.0.0 < 7.0.0.5', 'IBM WebSphere Application Server(WAS)　Web Services功能中包含ibm-webservicesclient-bind.xmi文件和客户加密口令的某个环境存在弱口令漏洞，本地用户可以通过未明向量导致拒绝服务攻击。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('844', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.0 < 6.1.0.25\r\nWebSphere 7.0.0.0 < 7.0.0.5', '当SPNEGO Single Sign-on (SSO)和disableSecurityPreInvokeOnFilters被具体化时，IBM WebSphere Application Server(WAS)6.1.0.25之前的6.1版本和7.0.0.5之前的7.0版本中的Servlet Engine/Web Container组件允许远程攻击者借助一个需要一个\"安全的URL\"的请求，绕过身份认证。此漏洞可能与某个调用过滤器兼容性属性有关。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('845', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.x < 6.0.2.35', 'IBM WebSphere 应用程序服务器(WAS) 6.0.2.35版本之前的6.0.2版本的Administrative Console组件中的安全登陆页没有重新定向至获得一个http请求的https页，这使远程攻击者易于通过窥探网络读取WAS会话的内容。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('846', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebShpere 6.1.0.*<6.1.0.15', 'IBM WebSphere Application server 6.1.* Pack15之前的版本存在多个安全漏洞：\r\n1)', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('847', '827', 'web应用', '5uCMS 1.2.2024 admin/ajax.php SQL注入漏洞', ' 高危', 'Sql注入 ', '5uCMS 1.2.2024', '在5uCMS 3.3.2的app/m/elem.php文件中，因为过滤函数过滤不严禁，导致SQL注入漏洞。', ' ', ' ', ' ', '2012-05-28');
INSERT INTO `websoc_spider` VALUES ('848', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 7.0.0.0 < 7.0.0.5', 'z/OS上的IBM WebSphere Application Server(WAS)7.0.0.5之前的7.0版本中的System Management/Repository组件为新的应用程序使用弱文件许可，这使得远程攻击者可以借助未明向量，获得敏感信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('849', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.* < 6.1.0.23\r\nWebSphere 7.0.0.* < 7.0.0.3', 'IBM WebSphere Application Server是一个完善的、开放的Web应用服务器，它是IBM电子商务应用架构的核心。\r\n当APAR PK41002被安装时，IBM WebSphere Application Server (WAS) 6.1.0.23之前的6.1版本和7.0.0.3之前的7.0版本的Web Services安全组件中的JAX-RPC WS-Security运行时间没有正确的验证用户名标识objects。它有未知影响和攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('850', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.* < 6.0.2.35\r\nWebSphere 7.0.* < 7.0.0.3', 'IBM WebSphere Application Server是一个完善的、开放的Web应用服务器，它是IBM电子商务应用架构的核心。                                 \r\nIBM WebSphere Application Server (WAS) 6.0.2.35之前的6.0.2版本和7.0.0.3之前的7.0版本中的Web Services安全组件在XML digital-signature说明书中有一个未明的\"安全问题\"。该问题有未知影响和攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('851', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.* < 6.0.2.33\r\nWebSphere 6.1.0.* < 6.1.0.23\r\nWebSphere 6.1.0.* < 7.0.0.1', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。\r\nIBM WebSphere应用程序服务器修复补丁1 (7.0.0.1)之前的版本7.0版本,修复补丁23 (6.1.0.23)之前的版本6.1版本,以及修复补丁33 (6.0.2.33)之前的版本6.0.2版本中的网络服务安全组件没有适当地执行储存在com.ibm.wsspi.wssecurity.core客户属性的WS-Security键联中的(', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('852', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 5.1.0.0 < 5.1.1.19\r\nWebSphere 6.0.2.0 < 6.0.2.35\r\nWebSphere 6.1.0.0 < 6.1.0.23\r\nWebSphere 7.0.0.0 <7.0.0.3', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。             \r\nIBM WebSphere Application Server (WAS) 5.1.0版本,5.1.1.19颁布,6.0.2.35之前的版本6.0.2,6.1.0.23之前的版本6.1,以及7.0.0.3之前的版本7.0的Servlet Engine/Web Container和JSP组件允许远程攻击者可以借助未知向量，读取任意文件。该文件包含于(1)web-inf,(2)meta-inf,和其他未明的目录中的war文件中。该向量于(a)基于web的应用和(b)管理控制台。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('853', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'Websphere 7.0.0.0 < 7.0.0.1', 'IBM Websphere应用服务器以Java和Servlet引擎为基础，支持多种HTTP服务，可帮助用户完成从开发、发布到维护交互式的动态网站的所有工作。\r\nWebSphere应用服务器的Web Services组件没有正确地识别IDAssertion.isUsed绑定属性。如果在使用Web Services组件，且客户端使用ProviderOnly的WSPolicy，并且所使用的简单UserNameToken policyset设置的com.ibm.wsspi.wssecurity.token/IDAssertion.isUsed绑定属性设置为true，WSPolicy就可能忽略这个设置，向SOAP消息中添加敏感信息，这样客户端就可以访问本不应发送的信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('854', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.0 < 6.1.0.17', 'IBM AIX是IBM 基于unix操作系统实现，是IBM开发的用P系列主机系统符合开放标准的操作系统。                                            \r\nAIX 5.3上的IBM WebSphere Application Server (WAS) 6.1.0.17之前的6.1.x版本中的Java Message Service (JMS)组件里的IBM Asynchronous I/O(又称AIO或libibmaio)库存在未明漏洞。攻击者可以借助与aio_getioev2和getEvent方式相关的向量，引起拒绝服务攻击(后台程序崩溃)。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('855', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1 <= 6.1 25\r\nWebSphere 7.0 <= 7.05', 'Apr-util是Apache所使用的Apache可移植运行时工具库。 \r\nAPR-util库所使用的expat XML解析器(位于xml/apr_xml.c文件的apr_xml_*接口)在处理实体定义中包含有大量嵌套实体引用的XML文件时可能会耗尽所有可用的内存，导致拒绝服务的情况。所有使用APR-util库的expat wrapper接口解析不可信任XML文档的网络服务都受这个漏洞影响，如Apache httpd WebDAV模块mod_dav。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('856', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere Application Server (WAS) 6.0.x < 6.0.2.31\r\nWAS 6.1.x <6.1.0.21\r\nWAS 7.0.x < 7.0.0.1', 'IBM WebSphere Application Server(简称 WAS)，即 IBM 的 WebSphere 应用服务器，是 IBM WebSphere 软件平台的基础和面向服务的体系结构的关键构件。\r\nIBM WebSphere应用程序服务器(WAS)7.0.0.1之前的版本7中的PMI/Performance Tools组件的PerfServlet允许攻击者通过读取(1) systemout.log和(2) ffdc文件，获得敏感信息。注意:该漏洞可能是CVE-2009-0434的副本。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('857', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1 <= 6.1 25\r\nWebSphere 7.0 <= 7.05', 'Apr-util是Apache所使用的Apache可移植运行时工具库。 \r\nApr-util库的strmatch/apr_strmatch.c文件中的apr_strmatch_precompile函数存在整数下溢漏洞。如果远程攻击者通过.htaccess文件、mod_dav_svn模块中的SVNMasterURI指令、mod_apreq2模块或libapreq2库等方式传送了特制输入的话，就可能导致守护程序崩溃。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('858', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.* < 6.0.2.35\r\nWebSphere 6.1.0.* < 6.1.0.25\r\nWebSphere 7.0.0.* < 7.0.0.5', 'IBM WebSphere应用程序服务其(WAS) 6.0.2.35版本之前的6.0.2版本的System Management/Repository组中存在未明漏洞。它具有与一个\"wsadmin安全曝光\"相关的未知影响和攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('859', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.* < 6.0.2.35\r\nWebSphere 6.1.0.* < 6.1.0.25\r\nWebSphere 7.0.0.* < 7.0.0.5', 'IBM WebSphere应用程序服务器(WAS) 6.0.2.35版本之前的6.0.2版本的安全组件允许\"不规范的http方法\"，这会具有未知影响和远程攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('860', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.* < 6.0.2.31', 'IBM WebSphere Application Server (WAS) 6.0.2.31版本之前的6.0.2和6.1.0.19版本之前的6.1的Servlet Engine/Web Container中存在未明漏洞, 当FileServing feature 被激活时, 具有未知影响和攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('861', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 5.1.1.0 < 5.1.1.19\r\nWebSphere 6.0.2.0 < 6.0.2.31\r\nWebSphere 6.1.0.0 < 6.1.0.19', 'IBM WebSphere Application Server (WAS) 6.0.2.31版本之前的6.0.2和6.1.0.19版本之前的6.1的Servlet Engine/Web Container中存在未明漏洞, 当FileServing feature 被激活时, 具有未知影响和攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('862', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.* < 6.0.2.31', 'IBM WebSphere Application Server (WAS) 6.0.2.31版本之前的6.0.2和6.1.0.19版本之前的6.1的Servlet Engine/Web Container中存在未明漏洞, 当FileServing feature 被激活时, 具有未知影响和攻击向量。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('863', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.1.0.0 < 6.1.0.15', '上述的WebSphere版本可能存在信息泄露和其他未知影响的漏洞。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('864', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.*\r\nWebSphere 6.1.*', 'IBM WebSphere Application Server (WAS)的UDDI用户控制台的uddigui/navigateTree.do中存在多个跨站脚本攻击漏洞。远程攻击者可以借助(1) keyField, (2) nameField, (3) valueField, 和 (4) frameReturn参数，注入任意web脚本或HTML。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('865', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 6.0.2.x < 6.0.2.35\r\nWebSphere  7.0.0.x < 7.0.0.5', 'IBM WebSphere Application Server (WAS) 6.1.0.25之前的6.1版本以及7.0.0.5之前的7.0版本中的安全组件没有正确的使用带有CSIv2Security的身份标识，这使得远程攻击者可以借助涉及Enterprise JavaBeans的向量，绕过设置的CSIv2访问限制。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('866', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere <= 5.0.2\r\nWebSphere 5.1.* < 5.1.1.12\r\nWebSphere = 6.0.2\r\nWebSphere 6.0.2 <= 6.0.2.7', 'IBM WebSphere Application Server 5.0.2 及之前版本, 5.1.1.12之前的5.1.x , 以及6.0.2到6.0.2.7版本的 SOAP端口(8880/tcp) 上的500 Internal Server Error 页中存在跨站脚本攻击漏洞。远程攻击者可以借助该页的FAULTACTOR元素中包含的URI，注入任意Web脚本或HTML 。注意: 某些来源称该元素为\"faultfactor\" ，但这很可能不正确。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('867', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 4.*\r\nWebSphere 5.*\r\nWebSphere 6.0.*', 'IBM Websphere Application Server 5.0、5.1和6.0在Windows上运行时存在未知漏洞，远程攻击者可以通过特制的URL，使得JSP文件由服务servlet而非JSP引擎来处理，从而获取Jave Server Pages(.jsp)的源码。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('868', '806', 'web应用', 'Lizard-Cart 1.04 search.php sql注入漏洞', ' 高危', 'Sql注入 ', '5ucms v3', '在5ucms V3版本中，mobile目录下的index.asp文件对输入的id参数完全无过滤，导致SQL注入漏洞。', ' ', 'CNNVD-201203-052', ' ', '2012-05-03');
INSERT INTO `websoc_spider` VALUES ('869', '806', 'web应用', 'Lizard-Cart 1.04 search.php sql注入漏洞', ' 高危', 'Sql注入 ', 'Lizard Cart 1.04', 'Lizard Cart CMS中存在SQL注入漏洞，该漏洞源于对用户提供的数据在用于SQL查询之前未经充分过滤。攻击者可利用该漏洞操控应用程序，访问或修改数据，或在底层数据库中利用该漏洞。Lizard Cart CMS pp104版本中存在该漏洞，其它版本也可能受到影响。', ' ', 'CNNVD-201203-052', ' ', '2012-05-03');
INSERT INTO `websoc_spider` VALUES ('870', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere <=6.0', 'IBM WebSphere Application Server 6.0及更早版本，在共享web服务器的文档root时，远程攻击者可以通过带有无效主机标题的HTTP请求，导致web服务器而非JSP引擎来处理该页面，从而获取Java Server Pages (.jsp)的源代码。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('871', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere 5.5\r\nWebSphere 5.6\r\nWebSphere 5.6.0.1', '运行着IBM WebSphere Commerce版本的远程主机，可以让攻击者用重置密码方式引发暴力攻击，来获取最近密码失效的用户的私人信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('872', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere  < 5.7', '运行IBM Websphere Commerce的主机，通过默认用户账号，攻击者可以通过构造查询，获得机密信息。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('873', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere Caching Proxy Server 3.6 \r\nWebSphere Caching Proxy Server 4.* < 4.0.1.26', 'IBM WebSphere Edge Server Caching Proxy是WEB服务器缓存代理系统。\r\n缓冲代理服务器中的helpout.exe CGI程序对不完整HTTP请求处理不正确，远程攻击者可以利用这个漏洞进行拒绝服务攻击。\r\n攻击者向IBM缓冲代理服务器提交/cgi-bin/helpout.exe文件请求，并且这个HTTP请求不包含HTTP版本信息的情况下，可导致服务程序ibmproxy.exe崩溃，产生拒绝服务攻击。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('874', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', 'WebSphere  3.0.2.1\r\nWebSphere 3.0.2\r\nWebSphere 3.0\r\nWebSphere 2.0', '远程服务器可能会存在跨站脚本攻击漏洞，因为失效的输入导致显示一个错误页面。\r\n攻击者可以利用漏洞构造一段JavaScript/HTML的攻击代码，当内容从服务端呈现，用户会认为这是服务器过来的安全的页面。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('875', '803', 'web应用', 'PHP-Volunteer-Management 1.0.x<=1.0.2 mods-hours-data-get-hours.php 跨站脚本漏洞', ' 中危', '跨站脚本', 'PHP Volunteer Management 1.0.0&1.0.1&1.0.2', 'PHP Volunteer Management 1.0.2版本中的mods/hours/data/get_hours.php脚本中存在跨站脚本漏洞。通过id参数，远程攻击者利用该漏洞注入任意web脚本或HTML。', ' ', 'CNNVD-201301-479', ' ', '2012-04-30');
INSERT INTO `websoc_spider` VALUES ('876', '863', 'web应用', 'ThinkSNS 2.5 thumb.php 文件上传漏洞', ' 高危', '文件上传 ', 'ThinkSNS 2.5', '在ThinkSNS 2.5版本中，由于文件thumb.php对用户上传文件的后缀名控制不严格，导致任意文件上传漏洞。利用该漏洞，恶意攻击者可以上传一个包含恶意代码的文件，并控制文件后缀名，进而获取网站服务器的控制权。', ' ', ' ', ' ', '2012-09-29');
INSERT INTO `websoc_spider` VALUES ('877', '814', 'web应用', 'JieqiCMS 1.7 regcheck.php文件SQL注入漏洞', ' 高危', 'Sql注入 ', 'JieQiCMS 1.7', '杰奇CMS中的regcheck.php文件为对性别做出严格控制，导致恶意用户可以直接注册成admin账户', ' ', ' ', ' ', '2012-05-11');
INSERT INTO `websoc_spider` VALUES ('878', '116', 'web应用', 'WebSphere 3.0.2.1 跨站脚本漏洞', ' 中危', '跨站脚本', '一直存在。', 'IBM WebSphere MQ Listener是一个远程服务，它负责监听来自MQ channels 的链接，并且对它们的路由进行一个队列管理。', ' ', ' ', ' ', '2009-12-18');
INSERT INTO `websoc_spider` VALUES ('879', '797', 'web应用', 'MyBB 1.6.6 index.php SQL注入和跨站脚本执行漏洞', ' 高危', '跨站脚本', 'MyBB 1.6.6', 'MyBB在实现上存在SQL注入漏洞和跨站脚本执行漏洞，利用这些漏洞可允许攻击者执行任意脚本代码、窃取cookie验证凭证、控制应用、访问或修改数据。', ' ', ' ', 'CNVD-2012-10995', '2012-04-18');
INSERT INTO `websoc_spider` VALUES ('880', '845', 'web应用', 'uccass 1.8.1 classes/results.class.php SQL Injuection', ' 高危', 'Sql注入 ', 'UCCASS 1.8.1', 'UCCASS 1.8.1版本和早期版本中存在SQL注入漏洞，该漏洞个源于对用户提供的数据在用于SQL查询之前未经充分验证。攻击者可利用该漏洞操控应用程序，访问或修改数据，或在底层数据库中利用该漏洞。', ' ', 'CNNVD-201206-448', 'CNVD-2012-12888', '2012-06-27');
INSERT INTO `websoc_spider` VALUES ('881', '830', 'web应用', 'Galette <=0.63.3 picture.class.php文件任意文件写入漏洞', ' 高危', '文件上传 ', 'galette <=0.63', 'Galette 0.63版本，0.63.1版本，0.63.2版本，0.63.3版本和0.64rc1版本的includes/picture.class.php中存在SQL注入漏洞。远程攻击者可利用该漏洞借助picture.php的id_adh参数执行任意SQL命令。', ' ', ' ', ' ', '2012-05-29');
INSERT INTO `websoc_spider` VALUES ('882', '413', 'web应用', 'phpBB2-Plus 1.52 跨站脚本漏洞', ' 中危', '跨站脚本', 'phpBB2 Plus <= 1.52', 'PhpBB Plus 1.52以及较早的版本中存在多个跨站脚本(XSS)漏洞，远程攻击者可以通过(1)groupcp.php，(2)index.php，(3)portal.php，(4)viewforum.php或(5)viewtopic.php的bsid参数，(6)index.php的c参数，或者(7)portal.php的article参数来注入任意Web脚本或HTML', 'CVE-2005-1113', 'CNNVD-200505-036', ' ', '2009-12-30');
INSERT INTO `websoc_spider` VALUES ('883', '799', 'web应用', 'siteweaver 6.6 /reg/user_check.asp SQL注入漏洞', ' 高危', 'Sql注入 ', 'SiteWeaver  6.6', 'SiteWeaver 上述版本中在dyna-page.asp存在sql-inject漏洞，成功攻击可执行任意sql语句。', ' ', ' ', ' ', '2012-04-19');
INSERT INTO `websoc_spider` VALUES ('884', '332', 'web应用', 'xoops 1.02 print.php Sql注入漏洞', ' 高危', 'Sql注入 ', 'XOOPS 2.3.2', 'Xoops的xoops_lib/modules/protector/oninstall.php、xoops_lib/modules /protector/onupdate.php、xoops_lib/modules/protector/notification.php和 xoops_lib/modules/protector/onuninstall.php没有正确地过滤对mydirname参数的输入便在 eval()语句中使用，远程攻击者可以通过提交恶意请求注入并执行任意PHP代码。成功攻击要求打开了register_globals。', 'CVE-2007-3311', 'CNNVD-200706-330', ' ', '2009-12-25');
INSERT INTO `websoc_spider` VALUES ('885', '618', 'web应用', 'ucenter <1.50 跨站脚本攻击漏洞', ' 中危', '跨站脚本', 'UCenter Home 2.0', '/shop.php?ac=view&shopid=253 shopid 为经过正确过滤,导致SQL注入漏洞', ' ', ' ', ' ', '2011-06-17');
INSERT INTO `websoc_spider` VALUES ('886', '618', 'web应用', 'ucenter <1.50 跨站脚本攻击漏洞', ' 中危', '跨站脚本', 'UCenter 1.6.0', 'UCenter <= 1.6.0 /control/admin/user.php\r\n验证码算法存在缺陷，计算过程中可以控制关键输入变量，导致验证码可重放。\r\n结合修改 X-Forwarded-For 可修改ip来绕过爆破检测', ' ', ' ', ' ', '2011-06-17');
INSERT INTO `websoc_spider` VALUES ('887', '810', 'web应用', 'Discuz!NT <=3.9 tools/ajax.aspx 文件上传漏洞', ' 高危', '文件上传 ', 'Discuz!NT 3.9及以下', 'Discuz!NT 3.9及以下版本中的tools/ajax.aspx文件，可以在客户端控制上传的文件名，结果IIS6的文件名解析漏洞，可导致上传后门。', ' ', ' ', ' ', '2012-05-09');
INSERT INTO `websoc_spider` VALUES ('888', '618', 'web应用', 'ucenter <1.50 跨站脚本攻击漏洞', ' 中危', '跨站脚本', 'ucenter 1.50以下版本', 'ucenter小于1.50的版本中存在xss漏洞\r\n漏洞网页:space.php\r\n/space.php?<script>alert(/hiphop/)</script></script>', ' ', ' ', ' ', '2011-06-17');
INSERT INTO `websoc_spider` VALUES ('889', '423', 'web应用', 'phpmps 2.0 fckeditor 文件上传漏洞', ' 高危', '文件上传 ', 'FCKeditor 2.4.3', 'CKSource FCKeditor（现称CKEditor）是波兰CKSource公司的一套开源的、基于网页的文字编辑器。该编辑器具有轻量化、易于安装等特点。\r\n在Fckeditor 2.3.0 - 2.4.3版本中，在upload.php文件中，检测上传的类型时，已检查却未定义拒绝后缀，导致可以上传任意后缀文件。', ' ', ' ', ' ', '2009-12-31');
INSERT INTO `websoc_spider` VALUES ('890', '329', 'web应用', 'phpbb 2.0.5 viewtopic.php Sql注入', ' 中危', 'Sql注入 ', 'phpBB  2.0.13', 'phpBB 2.0.13版本的usercp_register.php中存在跨站脚本攻击(XSS)漏洞，允许远程攻击者通过设置(1)allowhtml，(2)allowbbcode或(3)allowsmilies参数以将HTML注入到个人信息签名中来注入任意Web脚本或HTML，此情况可能会在privmsg.php或viewtopic.php处理参数时发生。', 'CVE-2003-0486', 'CNNVD-200308-018', 'CNVD-2009-05028', '2009-12-25');
INSERT INTO `websoc_spider` VALUES ('891', '329', 'web应用', 'phpbb 2.0.5 viewtopic.php Sql注入', ' 中危', 'Sql注入 ', 'phpBB = 2.0.4\r\nphpBB = 2.0.9', 'phpBB是一款由PHP编写的WEB论坛应用程序，支持多种数据库系统，可使用在多种Unix和Linux操作系统下。\r\nphpBB的多个脚本存在HTTP应答分割攻击或者跨站脚本攻击，远程攻击者可以利用这个漏洞进行WEB缓冲毒药，劫持也7面，或者敏感信息等攻击。\r\n如下两个脚本存在HTTP应答分割攻击：\r\n- /phpBB2/privmsg.php (\'mode\' 参数)\r\n- /phpBB2/login.php (\'redirect\' 参数)\r\n这些漏洞允许攻击者执行各种页面劫持，敏感信息泄露等攻击。\r\n\'/phpBB2/search.php\'脚本对\'search_author\'参数数据缺少充分过滤，提交恶意脚本代码，当其他用户解析时可导致基于COOKIE的敏感信息泄露。', 'CVE-2003-0486', 'CNNVD-200308-018', 'CNVD-2009-05028', '2009-12-25');
