INSERT INTO `websoc_spider` VALUES ('894', '329', 'web应用', 'phpbb 2.0.5 viewtopic.php Sql注入', ' 中危', 'Sql注入 ', 'phpBB 2.0.*<2.0.14', 'File Upload Script是phpBB论坛程序的一个文件上传脚本。\r\nphpBB论坛中的文件上传脚本可能允许远程的注册用户在有漏洞的服务器上执行任意代码。这可能导致对受影响服务器的非授权访问。\r\n此外，本地或远程攻击者还可能读取config.php文件，导致泄漏数据库信息和FTP口令。', 'CVE-2003-0486', 'CNNVD-200308-018', 'CNVD-2009-05028', '2009-12-25');
INSERT INTO `websoc_spider` VALUES ('895', '329', 'web应用', 'phpbb 2.0.5 viewtopic.php Sql注入', ' 中危', 'Sql注入 ', '包含了phpBB的第三方模块远程主机会有这个问题。', 'phpBB是一种用PHP语言实现的基于Web脚本的开放源码论坛程序，使用较为广泛。\r\nphpBB的Auction模块实现上输入验证漏洞，远程攻击者可以利用此漏洞在主机上执行任意命令。\r\nAuction模块没有正确检查过滤用户在变量phpbb_root_path中提交的参数值，远程攻击者可以在数据中插入特定字串使脚本包含远程服务器上的PHP脚本代码并执行，使攻击者能以Web进程权限在服务器上执行任意命令。', 'CVE-2003-0486', 'CNNVD-200308-018', 'CNVD-2009-05028', '2009-12-25');
