INSERT INTO `websoc_spider` VALUES ('899', '329', 'web应用', 'phpbb 2.0.5 viewtopic.php Sql注入', ' 中危', 'Sql注入 ', 'Impact:\r\nphpBB <2.0.18', 'phpBB 2是一个论坛软件，使用PHP语言开发的并开放其原始码。\r\nphpBB 2.0.17及更早版本，在启用了register_globals而未调用session_start函数来处理会话时，可让远程攻击者通过将$_SESSION和$HTTP_SESSION_VARS变量设置为字符串而非数组来绕过安全性检查，这会导致array_merge函数调用失败。', 'CVE-2003-0486', 'CNNVD-200308-018', 'CNVD-2009-05028', '2009-12-25');
