INSERT INTO `websoc_spider` VALUES ('983', '1074', 'web应用', 'destoon 后门漏洞', ' 高危', '后门 ', 'destoon', 'destoon 官网在一次被入侵后，黑客通过修改官网js，导致任何使用destoon的用户登录后台后都会被自动写入后门文件，写如的文件有：/file/swfupload/editor.php,/file/editor.php,/announce/announce.php。', ' ', ' ', ' ', '2013-10-08');
