INSERT INTO `websoc_spider` VALUES ('989', '767', 'web应用', 'Jxcms 1.3 Include-tags.func.php文件PHP代码注入漏洞', ' 高危', '代码执行 ', 'Jxcms 1.3', 'JXCMS生成缓存文件时变量未处理导致直接写 WebShell.。生成缓存文件时调用了tag_list函数，函数中有处变量因为没有受到任何处理，导致返回的值完整的写入到缓存文件中。article/article.class.php中的function check()虽然过滤掉了几个比较重要的字段，不过还不是很完整，漏掉了[defaultpicurl]。', ' ', ' ', ' ', '2012-03-07');
INSERT INTO `websoc_spider` VALUES ('990', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla Gallery WD', 'Joomla!是美国Open Source Matters团队开发的一套开源的内容管理系统(CMS)，该系统提供RSS馈送、网站搜索等功能。Gallery WD是其中的一个相册设计和布局组件。\r\nJoomla! Gallery WD组件中存在SQL注入漏洞，该漏洞源于程序在构造SQL查询语句之前没有充分过滤用户提交的输入。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞。', ' ', ' ', ' ', '2012-03-21');
