INSERT INTO `websoc_spider` VALUES ('1017', '617', 'web应用', 'aspcms 2.0-2.15 productbuy.asp SQL注入漏洞', ' 高危', 'Sql注入 ', 'ASPCMS 2.2.9', '/admin_aspcms/_content/_Tag/AspCms_TagFun.asp文件中行 49 中将 cookie 中的 languageID 取出直接带入 sql 语句,最终被带入行 56 中的sql 语句,更为重要的是,该文件虽然在 admin 目录底下,但是并没有 checkLogin 函数的保护,所以攻击者可以利用此漏洞对任意敏感信息进行查询。', ' ', ' ', 'CNVD-2011-06903', '2011-08-18');
INSERT INTO `websoc_spider` VALUES ('1018', '819', 'web应用', 'espcms <=4.3 public-class-connector.php sql注入漏洞', ' 高危', 'Sql注入 ', 'ESPCMS 5.7.13.06.08', '在/interface/search.php文件第139行的代码处，外界传入的数据未经安全处理被拼接到SQL查询语句中，导致了SQL注入漏洞的产生。', ' ', ' ', ' ', '2012-05-18');
INSERT INTO `websoc_spider` VALUES ('1019', '819', 'web应用', 'espcms <=4.3 public-class-connector.php sql注入漏洞', ' 高危', 'Sql注入 ', 'ESPCMS 4~5', 'ESPCMS 5 /interface/order.php文件中，第22行中的$cartid变量是由accept()方法获取Cookie中的ecisp_order_list值，$cartid是一个序列化数组，代码第24行将序列化数组反解赋值给$uncartid变量。第25行代码判断$uncartid是否为数组，代码第26-27行将数组的值提取出来赋值给$didlist变量，代码第28行判断$didlist变量是否为空，如果不为空继续执行下面代码，代码第29-32行拼接SQL语句。第33行将SQL语句带入数据库执行，这个过程中并没有将$uncartid变量过滤，所以导致SQL注入漏洞产生。', ' ', ' ', ' ', '2012-05-18');
INSERT INTO `websoc_spider` VALUES ('1020', '819', 'web应用', 'espcms <=4.3 public-class-connector.php sql注入漏洞', ' 高危', 'Sql注入 ', 'ESPCMS 5', 'ESPCMS 5 /interface/enquiry.php文件中，第86行中的$cartid变量是由accept()方法获取Cookie中的ecisp_enquiry_list值，$cartid是一个序列化数组，代码第88行将序列化数组反解赋值给$uncartid变量。第89行代码判断$uncartid是否为数组，代码第91-92行将数组的值提取出来赋值给$didlist变量，代码第93行判断$didlist变量是否为空，如果不为空继续执行下面代码，代码第94-96行拼接SQL语句。第97行将SQL语句带入数据库执行，这个过程中并没有将$uncartid变量过滤，所以导致漏洞产生。', ' ', ' ', ' ', '2012-05-18');
INSERT INTO `websoc_spider` VALUES ('1021', '819', 'web应用', 'espcms <=4.3 public-class-connector.php sql注入漏洞', ' 高危', 'Sql注入 ', 'ESPCMS 5.6.13.06.18', 'ESPCMS是国内一款比较流行的企业网站管理系统，其v5.7.S13.06.08以下版本存在SQL注入漏洞，攻击者可以利用该漏洞，执行恶意SQL语句。漏洞根源在/interface/3gwap_search.php文件中，在/interface/3gwap_search.php文件第122行的代码处，外界传入的数据未经安全处理被拼接到SQL查询语句中，导致了SQL注入漏洞的产生。', ' ', ' ', ' ', '2012-05-18');
INSERT INTO `websoc_spider` VALUES ('1022', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla MacGallery 1.5', '插件Mac Gallery /index.php?option=com_macgallery&view=download&albumid=[filename]文件下载功能中albumid变量正确过滤处理,导致任意文件下载', ' ', ' ', ' ', '2012-03-21');
