INSERT INTO `websoc_spider` VALUES ('1024', '811', 'web应用', 'cmseasy <=4.8_lib-tool-front_class.php 跨站脚本漏洞', ' 中危', '跨站脚本', 'CMSeasy 5.5', 'CMSeasy是国内一款非常流行的CMS 建站系统，其5.5版本存在SQL注入漏洞，攻击者可以利用该漏洞，执行恶意SQL语句。漏洞根源位于/lib/default/archive_act.php文件中，在该文件的行 240 中将 get 中的 keyword 取出直接带入 sql 语句,最终被带入行 282 中的执行sql 语句的函数,攻击者可以利用此漏洞对任意敏感信息进行查询。', ' ', ' ', ' ', '2012-05-10');
INSERT INTO `websoc_spider` VALUES ('1025', '819', 'web应用', 'espcms <=4.3 public-class-connector.php sql注入漏洞', ' 高危', 'Sql注入 ', 'espcms <=4.3', 'espcms是一套企业网站管理系统。由于对tagkey参数的过滤不够严格，导致了SQL注入漏洞的产生。攻击者可以通过构造恶意的语句，得到数据库里的任意值。', ' ', ' ', ' ', '2012-05-18');
INSERT INTO `websoc_spider` VALUES ('1026', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla FaceGallery 1.0', '插件facegallery /index.php?option=com_facegallery&view=images&aid=[SQLi]&lang=en其中aid变量未正确过滤,导致SQL注入存在', ' ', ' ', ' ', '2012-03-21');
INSERT INTO `websoc_spider` VALUES ('1027', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla Spider Contacts', 'Joomla!是美国Open Source Matters团队开发的一套开源的内容管理系统(CMS)，该系统提供RSS馈送、网站搜索等功能。Spider Contacts是其中的一个联系人组件。\r\nJoomla! Spider Contacts 1.3.6及之前版本中存在SQL注入漏洞，该漏洞源于程序构造SQL查询语句前没有充分过滤用户提交的输入。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库的潜在漏洞。', ' ', ' ', ' ', '2012-03-21');
INSERT INTO `websoc_spider` VALUES ('1028', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'joomla com_redshop插件', 'Joomla!是美国Open Source Matters团队开发的一套开源的内容管理系统(CMS)，它提供RSS馈送、网站搜索等功能。redSHOP是其中的一个网络商店工具包组件。\r\nJoomla!中的redSHOP组件中存在SQL注入漏洞，该漏洞源于程序构造SQL查询语句之前没有充分过滤用户提交的数据。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞。redSHOP 1.2版本中存在漏洞，其他版本也可能受到影响。', ' ', ' ', ' ', '2012-03-21');
INSERT INTO `websoc_spider` VALUES ('1029', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla com_enmasse 插件 1.2.0.4', 'Joomla com_enmasse 插件 1.2.0.4存在SQL注入漏洞，可带入恶意构造SQL语句，获取敏感信息。', ' ', ' ', ' ', '2012-03-21');
INSERT INTO `websoc_spider` VALUES ('1030', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla com_sectionex插件 2.5.96', 'Joomla!是美国OSM开发团队开发的一套开源的内容管理系统(CMS)，它提供RSS馈送、网站搜索等功能。SectionEx是其中的一个文章标题展示组件，该组件可在网页中某个单元内集中显示文章标题。\r\nJoomla!中的SectionEx组件中存在多个SQL注入漏洞，这些漏洞源于程序构造SQL查询语句之前没有充分过滤用户提交的输入。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞。SectionEx 2.5.96版本中存在漏洞，其他版本也可能受到影响。', ' ', ' ', ' ', '2012-03-21');
