INSERT INTO `websoc_spider` VALUES ('1032', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla 3.2.1', 'Joomla!是美国Open Source Matters团队开发的一套开源的内容管理系统(CMS)。该系统提供RSS馈送、网站搜索等功能。\r\nJoomla!中存在SQL注入漏洞，该漏洞源于程序使用SQL查询语句之前没有充分过滤用户提交的输入。攻击者可利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在的漏洞。Joomla! 3.2.1版本中存在漏洞，其他版本也可能受到影响。', ' ', ' ', ' ', '2012-03-21');
INSERT INTO `websoc_spider` VALUES ('1033', '777', 'web应用', 'Joomla <2.5.3 远程特权提升漏洞', ' 高危', '权限提升 ', 'Joomla com_rsfiles插件', 'Joomla!是美国Open Source Matters团队开发的一套开源的内容管理系统(CMS)。该系统提供RSS馈送、网站搜索等功能。\r\nJoomla!中的RSFiles!组件中的cid参数中存在SQL注入漏洞，该漏洞源于使用SQL查询之前，程序没有充分验证用户提供的输入。攻击者利用该漏洞控制应用程序，访问或修改数据，或利用底层数据库中潜在漏洞。', ' ', ' ', ' ', '2012-03-21');
