INSERT INTO `websoc_spider` VALUES ('1054', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1', 'MetInfo 5.1 /message/access.php文件中$listinfo变量和$id变量都是属于外部获取的，在文件中的有如下语句：$message_list=$db->get_one(\"SELECT * FROM $met_message where id=$id and lang=\'$lang\'\");直接带入数据库进行查询，而且$id是没有单引号保护的，这样就导致了SQL注入的产生。', ' ', ' ', ' ', '2012-06-21');
