INSERT INTO `websoc_spider` VALUES ('1059', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'Metinfo v5.2', '/job/job.php $metinfo会有类似全局变量注册的机制，导致这里可以通过get/post等覆盖$mobilesql导致sql注入', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1060', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.2.4', '核心的文件admin/include/common.inc.php伪全局变量，而在admin/content/feedback/export.php未检查登录，并且传入变量可导致变量覆盖，所以造成盲注。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1061', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1', 'MetInfo 5.1 官方下载源被黑客入侵，黑客在安装包/feedback/uploadfile_save.php文件中植入了后门代码，造成后门漏洞。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1062', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'Metinfo 5.1', 'MetInfo是一款采用PHP Mysql架构的企业网站管理系统。其admin/getpassword.php中多处变量传入未合理过滤，密码修改设计缺陷导致可以重设密码。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1063', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1.3', 'MetInfo是国内一款比较流行的企业网站管理系统，其5.1.3版本/include/common.inc.php文件中的第29-30行代码处28-32行代码实现了全局变量机制，同时也引入了变量覆盖的问题，导致所有包含该文件的代码都存在变量覆盖问题；其中/admin/include/uploadify.php文件因为该变量覆盖，攻击者可以控制文件上传时的关键变量，从而上传恶意脚本文件。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1064', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1', 'MetInfo是国内一款比较流行的企业网站管理系统，其5.1版本/message/access.php文件中的第12行代码处外界传入的变量$id直接拼接到SQL查询语句中，导致了SQL注入漏洞的产生。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1065', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1', 'MetInfo是国内一款比较流行的企业网站管理系统，其5.1版本/admin/admin/getpassword.php文件中的第8行代码处$array[0]变量最终来在外界传入的变量$p，而且$array[0]直接拼接到SQL查询语句中，导致了SQL注入漏洞的产生。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1066', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1', '\\include\\common.inc.php代码第30行产生了变量覆盖漏洞，代码33行的{$tablepre}可以覆盖掉，所以导致SQL注入漏洞产生。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1067', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'MetInfo 5.1', 'MetInfo是国内一款流行的内容管理系统软件，其v5.1版本/about/show.php文件第4行代码引入了/include/common.inc.php文件，执行/include/common.inc.php文件第28-32行代码，遍历数组，并且用daddslashes()方法过滤，daddslashes()方法在/include/global.func.php 文件中定义，/include/global.func.php 文件代码第22-65行代码将特定的字符串替换为空，/about/show.php文件代码第6行，将$id变量拼接到SQL语句，由于daddslashes()方法过滤不严和$id拼接到SQL语句的时候没有单引号的保护，导致SQL注入漏洞产生。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1068', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'Meinfo 2.0.*<5.0.=2', '在上述版本中程序在修改用户代码时程序没有进行任何检查就将对应用户的密码修改，恶意用户可以构造表单修改任意用户的密码切管理员信息与普通用户的信息存储在一个表中所以造成恶意用户同样可以修改管理员密码达到控制网站的目的。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1069', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'Metinfo 2.0.*<=2.0.2', '在上述版本中对用户提交的数据没有进行很好的过滤恶意用户可以通过控制$serch_sql变量进行SQL注入，漏洞会导致管理员账户密码的泄露导致网站被操控。', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1070', '842', 'web应用', 'MetInfo 4.0 member/save.php 任意密码修改漏洞', ' 高危', '远程密码修改', 'metinfo<=4.0', 'metinfo 4.0在member/save.php文件在修改密码时通过外部提交的userid作为更新数据库的条件，导致可以修改userid达到修改任意用户密码的攻击', ' ', ' ', ' ', '2012-06-21');
INSERT INTO `websoc_spider` VALUES ('1071', '1066', 'web应用', 'magento 1.7.0.1 /lib/Zend/XmlRpc/Request.php 任意文件读取漏洞', ' 高危', 'Magento', 'magento 1.7.0.1', 'Zend框架的loadXml函数（Zend XMLRPC Request.php和Response.php）在解析XML数据的时候未禁用加载外部entities。在初始化SimpleXMLElement类之前未调用libxml_disable_entity_loader函数，这一情况导致当WEB应用程序使用Zend_XmlRpc_Server类(ZendXmlRpcServer.php)处理XMLRPC请求时，可能泄露远程服务器的任意文件。', ' ', ' ', ' ', '2013-09-22');
INSERT INTO `websoc_spider` VALUES ('1072', '379', 'web应用', 'php168 5.0 job.php 信息泄漏漏洞', ' 低危', '信息泄漏', 'php168 <= 6.1', 'login.php参数处理不当，入侵者可以在用户登陆页面构造特殊语句，将PHP一句话写入cache目录，从而获得使用PHP168整站程序网站的WEBSHELL权限。', ' ', ' ', ' ', '2009-12-28');
INSERT INTO `websoc_spider` VALUES ('1073', '379', 'web应用', 'php168 5.0 job.php 信息泄漏漏洞', ' 低危', '信息泄漏', 'php168 = 5.0', 'php168 上述版本中存在信息泄露漏洞，在拥有后台管理员账号的情况下，成功攻击可浏览任意文件内容。', ' ', ' ', ' ', '2009-12-28');
